import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Link, Navigate, useLocation } from 'react-router-dom';
import { supabase } from './supabaseClient'; 

// Pages
import HomePage from './pages/HomePage';
import CategoryPage from './pages/CategoryPage'; 
import AdminDashboard from './pages/AdminDashboard';
import SuccessPage from './pages/SuccessPage'; 
import AuthPage from './pages/Authpage'; 
import ProfilePage from './pages/ProfilePage';
import ProductDetails from './pages/ProductDetail';
import CheckoutPage from './pages/CheckoutPage'; 
import TrackOrder from './pages/TrackOrder';
import AboutUs from './pages/AboutUs';
import ContactUs from './pages/ContactUs'; 
import PrivacyPolicy from './pages/PrivacyPolicy';
import TermsOfService from './pages/TermsOfService';

// Components
import Navbar from './components/Navbar';
import Sidebar from './components/Sidebar'; 
import WhatsAppButton from './components/WhatsAppButton';

const ScrollToTop = () => {
  const { pathname } = useLocation();
  useEffect(() => { window.scrollTo(0, 0); }, [pathname]);
  return null;
};

function App() {
  const [cart, setCart] = useState(() => {
    const saved = localStorage.getItem('megaMartCart');
    return saved ? JSON.parse(saved) : [];
  });
  const [isSidebarOpen, setIsSidebarOpen] = useState(false); 
  const [isAdmin, setIsAdmin] = useState(false);
  const [user, setUser] = useState(null);
  const [categories, setCategories] = useState([]);

  useEffect(() => { localStorage.setItem('megaMartCart', JSON.stringify(cart)); }, [cart]);

  useEffect(() => {
    const fetchCategories = async () => {
      const { data } = await supabase.from('products').select('category');
      if (data) setCategories([...new Set(data.map(item => item.category))].filter(Boolean));
    };
    fetchCategories();
  }, []);

  useEffect(() => {
    supabase.auth.getSession().then(({ data: { session } }) => setUser(session?.user ?? null));
    const { data: { subscription } } = supabase.auth.onAuthStateChange((_event, session) => setUser(session?.user ?? null));
    return () => subscription.unsubscribe();
  }, []);

  const addToCart = (product) => {
    setCart((prev) => [...prev, product]);
    alert(`${product.name} added to cart!`);
  };

  return (
    <Router future={{ v7_startTransition: true, v7_relativeSplatPath: true }}>
      <ScrollToTop />
      <div className="App" style={{ minHeight: '100vh', display: 'flex', flexDirection: 'column' }}>
        <Sidebar isOpen={isSidebarOpen} onClose={() => setIsSidebarOpen(false)} user={user} categories={categories} />
        
        <Navbar cartCount={cart.length} openSidebar={() => setIsSidebarOpen(true)} user={user} categories={categories} />
        
        <main style={{ flex: 1, backgroundColor: '#f4f4f4' }}>
          <Routes>
            <Route path="/" element={<HomePage />} /> 
            <Route path="/about" element={<AboutUs />} />
            <Route path="/contact-us" element={<ContactUs />} />
            <Route path="/info/privacy-policy" element={<PrivacyPolicy />} />
            <Route path="/info/terms" element={<TermsOfService />} />
            <Route path="/category/:categoryName" element={<CategoryPage addToCart={addToCart} />} />
            <Route path="/product/:id" element={<ProductDetails addToCart={addToCart} />} />
            <Route path="/checkout" element={<CheckoutPage cart={cart} setCart={setCart} user={user} />} />
            <Route path="/login" element={<AuthPage />} />
            <Route path="/track-order" element={<TrackOrder />} />
            <Route path="/success" element={<SuccessPage />} />
          </Routes>
        </main>

        <WhatsAppButton />
        <Footer />
      </div>
    </Router>
  );
}

const Footer = () => (
  <footer style={{ backgroundColor: '#131921', color: 'white', padding: '40px 10%' }}>
    <div style={{ display: 'flex', flexWrap: 'wrap', gap: '30px', justifyContent: 'space-between' }}>
      <div>
        <h3 style={{ color: '#008ECC' }}>MegaMart Rwanda</h3>
        <p style={{ fontSize: '13px', opacity: 0.7, maxWidth: '250px' }}>Your trusted gadget store in Kigali.</p>
      </div>
      <div>
        <h4 style={{ color: '#febd69' }}>Legal</h4>
        <Link to="/info/privacy-policy" style={{ display: 'block', color: 'white', textDecoration: 'none', fontSize: '13px', marginBottom: '8px' }}>Privacy Policy</Link>
        <Link to="/info/terms" style={{ display: 'block', color: 'white', textDecoration: 'none', fontSize: '13px' }}>Terms of Service</Link>
      </div>
      <div>
        <h4 style={{ color: '#febd69' }}>Support</h4>
        <Link to="/track-order" style={{ display: 'block', color: 'white', textDecoration: 'none', fontSize: '13px' }}>Track Order</Link>
      </div>
    </div>
  </footer>
);

export default App;
