import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { supabase } from '../supabaseClient';
import { 
  ShieldCheck, Smartphone, ChevronRight, Lock, Mail, MapPin 
} from 'lucide-react';

const CheckoutPage = ({ cart, setCart, user }) => {
  const navigate = useNavigate();
  const [loading, setLoading] = useState(false);
  const [showMomoOverlay, setShowMomoOverlay] = useState(false);
  
  const [formData, setFormData] = useState({
    email: user?.email || '',
    phone: '',
    firstName: '',
    lastName: '',
    address: '',
    city: 'Kigali'
  });

  const subtotal = cart.reduce((acc, item) => acc + (item.price || 0), 0);
  const delivery = 2000; 
  const total = subtotal + delivery;

  const processOrder = async () => {
    setLoading(true);
    try {
      // This object must match your Supabase columns EXACTLY
      const { error } = await supabase.from('orders').insert([{
        user_email: formData.email,
        total_amount: total,
        shipping_address: `${formData.firstName} ${formData.lastName}, ${formData.address}, ${formData.city}. Tel: ${formData.phone}`,
        payment_method: 'Mobile Money',
        status: 'Paid', // This was missing in your DB
        items: cart    // This was missing in your DB
      }]);

      if (error) throw error;

      setCart([]);
      navigate('/success');
    } catch (err) {
      alert("Database Error: " + err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={boltWrapper}>
      {/* Decorative Blobs from the Bolt screenshot */}
      <div style={blueBlob}></div>
      <div style={purpleBlob}></div>

      {showMomoOverlay && (
        <div style={modalOverlay}>
          <div style={momoCard}>
            <h3 style={{marginTop: 0}}>Confirm Payment</h3>
            <p style={{fontSize: '14px', color: '#666'}}>Dial this code to pay <b>RWF {total.toLocaleString()}</b>:</p>
            <div style={ussdBox}>*182*8*1*000000#</div>
            <button style={paidBtn} onClick={processOrder} disabled={loading}>
              {loading ? "Verifying..." : "I Have Paid"}
            </button>
            <button style={cancelBtn} onClick={() => setShowMomoOverlay(false)}>Cancel</button>
          </div>
        </div>
      )}

      <div style={mainCard}>
        <div style={leftForm}>
          <h2 style={logo}>MegaMart</h2>
          <div style={stepper}>
            <span style={activeStep}>1. Shipping</span>
            <ChevronRight size={14} color="#ccc" />
            <span style={inactiveStep}>2. Payment</span>
          </div>

          <div style={inputStack}>
             <label style={fieldLabel}><Mail size={14}/> Contact</label>
             <input style={input} placeholder="Email" value={formData.email} onChange={e => setFormData({...formData, email: e.target.value})}/>
             <input style={input} placeholder="MTN/Airtel Phone Number" onChange={e => setFormData({...formData, phone: e.target.value})}/>
             
             <label style={fieldLabel}><MapPin size={14}/> Address</label>
             <div style={{display: 'flex', gap: '10px'}}>
                <input style={{...input, flex: 1}} placeholder="First name" onChange={e => setFormData({...formData, firstName: e.target.value})}/>
                <input style={{...input, flex: 1}} placeholder="Last name" onChange={e => setFormData({...formData, lastName: e.target.value})}/>
             </div>
             <input style={input} placeholder="Street Address" onChange={e => setFormData({...formData, address: e.target.value})}/>
          </div>

          <button style={payBtn} onClick={() => setShowMomoOverlay(true)}>Pay RWF {total.toLocaleString()}</button>
          <p style={footerSecure}><Lock size={12}/> Secure Payment Powered by MegaMart</p>
        </div>

        <div style={rightSummary}>
           <h4 style={{marginTop: 0}}>Order Summary</h4>
           <div style={summaryScroll}>
              {cart.map((item, i) => (
                <div key={i} style={itemRow}>
                   <div style={itemIcon}>{item.name[0]}</div>
                   <div style={{flex: 1, fontSize: '13px'}}>
                      <b>{item.name}</b>
                      <div style={{color: '#888'}}>Qty: 1</div>
                   </div>
                   <div style={{fontSize: '13px', fontWeight: 'bold'}}>RWF {item.price.toLocaleString()}</div>
                </div>
              ))}
           </div>
           <div style={divider}></div>
