import React, { useState } from 'react';
import { Mail, Phone, MapPin, Send, Clock } from 'lucide-react';
import { supabase } from '../supabaseClient';

const ContactUs = () => {
  const [formData, setFormData] = useState({ name: '', email: '', subject: '', message: '' });
  const [status, setStatus] = useState({ type: '', msg: '' });

  const handleSubmit = async (e) => {
    e.preventDefault();
    setStatus({ type: 'info', msg: 'Sending your message...' });

    const { error } = await supabase.from('messages').insert([formData]);

    if (error) {
      setStatus({ type: 'error', msg: 'Failed to send. Please try again later.' });
    } else {
      setStatus({ type: 'success', msg: 'Message sent! We will get back to you soon.' });
      setFormData({ name: '', email: '', subject: '', message: '' });
    }
  };

  return (
    <div style={pageContainer}>
      <div style={headerSection}>
        <h1 style={title}>Contact MegaMart Rwanda</h1>
        <p style={subtitle}>We're here to help with your gadget needs and wholesale inquiries.</p>
      </div>

      <div style={grid}>
        {/* Info Column */}
        <div style={infoCol}>
          <div style={infoCard}>
            <h3 style={cardTitle}>Store Information</h3>
            
            <div style={item}>
              <MapPin size={20} color="#008ECC" />
              <div>
                <p style={label}>Location</p>
                <p style={text}>Kigali Heights, 3rd Floor, Kigali, Rwanda</p>
              </div>
            </div>

            <div style={item}>
              <Phone size={20} color="#008ECC" />
              <div>
                <p style={label}>Phone & WhatsApp</p>
                <p style={text}>+250 788 000 000</p>
              </div>
            </div>

            <div style={item}>
              <Mail size={20} color="#008ECC" />
              <div>
                <p style={label}>Email Support</p>
                <p style={text}>support@megamart.rw</p>
              </div>
            </div>

            <div style={item}>
              <Clock size={20} color="#008ECC" />
              <div>
                <p style={label}>Working Hours</p>
                <p style={text}>Mon - Sat: 8:00 AM - 7:00 PM</p>
              </div>
            </div>
          </div>
        </div>

        {/* Form Column */}
        <div style={formCol}>
          <form onSubmit={handleSubmit} style={formCard}>
            <div style={row}>
              <div style={inputGroup}>
                <label style={formLabel}>Full Name</label>
                <input 
                  type="text" 
                  required 
                  style={input} 
                  value={formData.name}
                  onChange={(e) => setFormData({...formData, name: e.target.value})}
                />
              </div>
              <div style={inputGroup}>
                <label style={formLabel}>Email Address</label>
                <input 
                  type="email" 
                  required 
                  style={input} 
                  value={formData.email}
                  onChange={(e) => setFormData({...formData, email: e.target.value})}
                />
              </div>
            </div>

            <div style={inputGroup}>
              <label style={formLabel}>Subject</label>
              <input 
                type="text" 
                required 
                style={input} 
                value={formData.subject}
                onChange={(e) => setFormData({...formData, subject: e.target.value})}
              />
            </div>

            <div style={inputGroup}>
              <label style={formLabel}>Message</label>
              <textarea 
                rows="5" 
                required 
                style={{...input, resize: 'none'}} 
                value={formData.message}
                onChange={(e) => setFormData({...formData, message: e.target.value})}
              />
            </div>

            <button type="submit" style={submitBtn}>
              <Send size={18} /> Send Message
            </button>

            {status.msg && (
              <p style={{
                marginTop: '15px', 
                color: status.type === 'success' ? '#2e7d32' : '#d32f2f',
                fontWeight: 'bold'
              }}>{status.msg}</p>
            )}
          </form>
        </div>
      </div>
    </div>
  );
};

// --- STYLES ---
const pageContainer = { padding: '50px 10%', backgroundColor: '#f8f9fa' };
const headerSection = { textAlign: 'center', marginBottom: '40px' };
const title = { fontSize: '28px', color: '#131921', marginBottom: '10px' };
const subtitle = { color: '#666' };
const grid = { display: 'grid', gridTemplateColumns: '1fr 1.5fr', gap: '30px' };
const infoCard = { backgroundColor: 'white', padding: '30px', borderRadius: '8px', boxShadow: '0 2px 10px rgba(0,0,0,0.05)' };
const cardTitle = { marginBottom: '25px', color: '#131921' };
const item = { display: 'flex', gap: '15px', marginBottom: '20px' };
const label = { fontSize: '12px', color: '#888', margin: 0 };
const text = { fontSize: '14px', color: '#333', fontWeight: '500', margin: 0 };
const formCard = { backgroundColor: 'white', padding: '30px', borderRadius: '8px', boxShadow: '0 2px 10px rgba(0,0,0,0.05)' };
const row = { display: 'flex', gap: '20px', marginBottom: '15px' };
const inputGroup = { flex: 1, display: 'flex', flexDirection: 'column', marginBottom: '15px' };
const formLabel = { fontSize: '13px', fontWeight: '600', marginBottom: '5px' };
const input = { padding: '10px', borderRadius: '4px', border: '1px solid #ddd', outline: 'none' };
const submitBtn = { backgroundColor: '#008ECC', color: 'white', border: 'none', padding: '12px 25px', borderRadius: '4px', cursor: 'pointer', display: 'flex', alignItems: 'center', gap: '8px', fontWeight: '600' };

export default ContactUs;
