import React, { useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { supabase } from '../supabaseClient';

const SuccessPage = ({ user, totalAmount }) => {
  const navigate = useNavigate();

  useEffect(() => {
    const saveOrder = async () => {
      // Only save if a user is actually logged in
      if (user) {
        const { error } = await supabase
          .from('orders')
          .insert([
            { 
              user_id: user.id, 
              amount: totalAmount || 0, 
              status: 'paid' 
            }
          ]);
        
        if (error) console.error("Error saving order:", error.message);
      }
    };

    saveOrder();
  }, [user, totalAmount]);

  return (
    <div style={{ height: '80vh', display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center', fontFamily: 'sans-serif' }}>
      <div style={checkCircleStyle}>âœ“</div>
      <h1 style={{ color: '#333' }}>Payment Successful!</h1>
      <p style={{ color: '#666', textAlign: 'center' }}>
        Your order has been recorded in your account.
      </p>
      <button onClick={() => navigate('/')} style={btnStyle}>Back to Shopping</button>
    </div>
  );
};

const checkCircleStyle = { width: '80px', height: '80px', backgroundColor: '#4BB543', borderRadius: '50%', display: 'flex', alignItems: 'center', justifyContent: 'center', color: 'white', fontSize: '40px', marginBottom: '20px' };
const btnStyle = { marginTop: '30px', backgroundColor: '#008ECC', color: 'white', border: 'none', padding: '12px 30px', borderRadius: '8px', fontWeight: 'bold', cursor: 'pointer' };

export default SuccessPage;
