import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { supabase } from '../supabaseClient';
import { 
  CreditCard, ShieldCheck, MapPin, Smartphone, 
  CheckCircle, Star, ArrowRight 
} from 'lucide-react';

const CheckoutPage = ({ cart, setCart, user }) => {
  const navigate = useNavigate();
  const [checkoutStep, setCheckoutStep] = useState(2); // Start at Step 2: Information
  const [loading, setLoading] = useState(false);
  const [rating, setRating] = useState(0);
  const [paymentMethod, setPaymentMethod] = useState('momo');
  const [shippingDetails, setShippingDetails] = useState({ phone: '', address: '', district: 'Gasabo' });

  const total = cart.reduce((sum, item) => sum + (item.price || 0), 0) + 2000;

  const handleFinishPayment = async () => {
    setLoading(true);
    // Simulate payment and save to Supabase
    const { error } = await supabase.from('orders').insert([{
      user_id: user?.id,
      items: cart,
      total_amount: total,
      phone_number: shippingDetails.phone,
      status: 'paid'
    }]);

    if (!error) {
      setCart([]);
      setCheckoutStep(4); // Move to Step 4: Success & Notification
    }
    setLoading(false);
  };

  // --- STEP 2: INFORMATION ---
  if (checkoutStep === 2) return (
    <div style={pageLayout}>
      <div style={leftColumn}>
        <h2 style={sectionHeader}>Step 2: Delivery Details</h2>
        <section style={cardStyle}>
          <h3 style={cardTitle}><MapPin size={20} color="#008ECC" /> Where should we deliver?</h3>
          <div style={formGrid}>
            <div style={inputGroup}>
              <label style={labelStyle}>Phone (Momo)</label>
              <input style={inputStyle} placeholder="078..." onChange={e => setShippingDetails({...shippingDetails, phone: e.target.value})} />
            </div>
            <div style={inputGroup}>
              <label style={labelStyle}>District</label>
              <select style={inputStyle} onChange={e => setShippingDetails({...shippingDetails, district: e.target.value})}>
                <option>Gasabo</option><option>Kicukiro</option><option>Nyarugenge</option>
              </select>
            </div>
            <div style={{...inputGroup, gridColumn: 'span 2'}}>
              <input style={inputStyle} placeholder="Street / Landmark (e.g. Near Amahoro Stadium)" onChange={e => setShippingDetails({...shippingDetails, address: e.target.value})} />
            </div>
          </div>
          <button onClick={() => setCheckoutStep(3)} style={orderBtn}>Continue to Payment <ArrowRight size={18} /></button>
        </section>
      </div>
    </div>
  );

  // --- STEP 3: PAYMENT ---
  if (checkoutStep === 3) return (
    <div style={pageLayout}>
      <div style={leftColumn}>
        <h2 style={sectionHeader}>Step 3: Payment Method</h2>
        <section style={cardStyle}>
          <div style={paymentOptions}>
            <div style={activeMethod} onClick={() => setPaymentMethod('momo')}>
              <Smartphone /> <div><p style={methodName}>Mobile Money</p><p style={methodDesc}>Pay via MTN/Airtel</p></div>
            </div>
          </div>
          <button onClick={handleFinishPayment} style={orderBtn} disabled={loading}>
            {loading ? "Processing..." : `Pay RWF ${total.toLocaleString()}`}
          </button>
        </section>
      </div>
    </div>
  );

  // --- STEP 4 & 5: SUCCESS & RATING ---
  if (checkoutStep === 4) return (
    <div style={successContainer}>
      <CheckCircle size={80} color="#28a745" />
      <h1>Thanks for buying on MegaMart!</h1>
      <p>Step 4: We've received your order for the {shippingDetails.district} area.</p>
      
      <div style={ratingBox}>
        <h3>Step 5: Rate our website</h3>
        <div style={{ display: 'flex', justifyContent: 'center', gap: '10px' }}>
          {[1, 2, 3, 4, 5].map(star => (
            <Star 
              key={star} 
              fill={rating >= star ? "#ffc107" : "none"} 
              color="#ffc107" 
              style={{ cursor: 'pointer' }} 
              onClick={() => setRating(star)} 
            />
          ))}
        </div>
        {rating > 0 && <p style={{ color: '#28a745', marginTop: '10px', fontWeight: 'bold' }}>Thank you for your feedback!</p>}
        <button onClick={() => navigate('/')} style={continueBtn}>Return to Shop</button>
      </div>
    </div>
  );

  return null;
};

// New Success Styles
const successContainer = { textAlign: 'center', padding: '80px 20px', backgroundColor: 'white', minHeight: '100vh' };
const ratingBox = { marginTop: '40px', padding: '30px', border: '1px solid #eee', borderRadius: '15px', maxWidth: '400px', margin: '40px auto' };
const continueBtn = { marginTop: '20px', padding: '12px 25px', backgroundColor: '#1a1a1a', color: 'white', border: 'none', borderRadius: '10px', cursor: 'pointer' };

// (Include your existing layout/card styles here as defined in your previous code)
const pageLayout = { display: 'flex', gap: '30px', padding: '40px 8%', backgroundColor: '#f8f9fa', minHeight: '100vh' };
const leftColumn = { flex: 2 };
const sectionHeader = { fontSize: '28px' };
const cardStyle = { backgroundColor: 'white', padding: '25px', borderRadius: '16px' };
const cardTitle = { display: 'flex', alignItems: 'center', gap: '10px' };
const formGrid = { display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '15px', margin: '20px 0' };
const inputGroup = { display: 'flex', flexDirection: 'column' };
const labelStyle = { fontSize: '13px', fontWeight: 'bold' };
const inputStyle = { padding: '12px', borderRadius: '10px', border: '1px solid #ddd' };
const paymentOptions = { margin: '20px 0' };
const activeMethod = { display: 'flex', alignItems: 'center', gap: '15px', padding: '15px', border: '2px solid #008ECC', borderRadius: '12px', cursor: 'pointer' };
const methodName = { margin: 0, fontWeight: 'bold' };
const methodDesc = { margin: 0, fontSize: '12px' };
const orderBtn = { width: '100%', padding: '16px', backgroundColor: '#008ECC', color: 'white', border: 'none', borderRadius: '12px', fontWeight: 'bold', cursor: 'pointer' };

export default CheckoutPage;
