import React, { useState, useEffect } from 'react';
import { supabase } from '../supabaseClient';

const AdminDashboard = () => {
  const [products, setProducts] = useState([]);
  const [formData, setFormData] = useState({ name: '', price: '', category: 'Smartphones', image_url: '', stock: 0 });

  // 1. Fetch products to show current inventory
  const fetchInventory = async () => {
    const { data } = await supabase.from('products').select('*');
    if (data) setProducts(data);
  };

  useEffect(() => { fetchInventory(); }, []);

  // 2. Promotion Logic: Update all items in a category
  const applyDiscount = async (percentage) => {
    const confirmAction = window.confirm(`Apply a ${percentage}% discount to all Smartphones?`);
    
    if (confirmAction) {
      // Fetch current smartphones
      const { data: smartphones } = await supabase
        .from('products')
        .select('*')
        .eq('category', 'Smartphones');

      if (!smartphones) return;

      // Update each product's price in the database
      const updates = smartphones.map(product => {
        const newPrice = product.price * ((100 - percentage) / 100);
        return supabase
          .from('products')
          .update({ price: newPrice })
          .eq('id', product.id);
      });

      await Promise.all(updates);
      alert(`Success! All smartphone prices reduced by ${percentage}%`);
      fetchInventory(); // Refresh the table
    }
  };

  // 3. Handle adding a new product
  const handleAddProduct = async (e) => {
    e.preventDefault();
    const { data, error } = await supabase
      .from('products')
      .insert([{ 
        name: formData.name, 
        price: parseFloat(formData.price), 
        category: formData.category, 
        image_url: formData.image_url 
      }])
      .select();

    if (!error) {
      alert("Product Added Successfully!");
      setFormData({ name: '', price: '', category: 'Smartphones', image_url: '', stock: 0 });
      fetchInventory();
    } else {
      alert("Error: " + error.message);
    }
  };

  return (
    <div style={{ padding: '40px', backgroundColor: '#f4f7f6', minHeight: '100vh', fontFamily: 'sans-serif' }}>
      <h1 style={{ color: '#008ECC', marginBottom: '30px' }}>MegaMart Admin Panel</h1>
      
      <div style={{ display: 'grid', gridTemplateColumns: '1fr 2fr', gap: '30px' }}>
        
        {/* LEFT: Add Product Form & Promotions */}
        <div style={{ backgroundColor: 'white', padding: '25px', borderRadius: '12px', boxShadow: '0 4px 6px rgba(0,0,0,0.1)' }}>
          <h3 style={{ marginBottom: '20px' }}>Add New Product</h3>
          <form onSubmit={handleAddProduct} style={{ display: 'flex', flexDirection: 'column', gap: '15px' }}>
            <input type="text" placeholder="Product Name" required style={inputStyle} onChange={(e) => setFormData({...formData, name: e.target.value})} value={formData.name} />
            <input type="number" placeholder="Price (RWF)" required style={inputStyle} onChange={(e) => setFormData({...formData, price: e.target.value})} value={formData.price} />
            <select style={inputStyle} value={formData.category} onChange={(e) => setFormData({...formData, category: e.target.value})}>
              <option>Smartphones</option>
              <option>Laptops</option>
              <option>Accessories</option>
            </select>
            <input type="text" placeholder="Image URL" style={inputStyle} onChange={(e) => setFormData({...formData, image_url: e.target.value})} value={formData.image_url} />
            <button type="submit" style={{ backgroundColor: '#008ECC', color: 'white', border: 'none', padding: '12px', borderRadius: '6px', cursor: 'pointer', fontWeight: 'bold' }}>
              Save Product
            </button>
          </form>

          {/* NEW: QUICK PROMOTIONS SECTION */}
          <div style={{ marginTop: '30px', borderTop: '1px solid #eee', paddingTop: '20px' }}>
            <h4 style={{ fontSize: '14px', color: '#666', marginBottom: '15px' }}>Quick Promotions</h4>
            <div style={{ display: 'flex', gap: '10px' }}>
              <button 
                onClick={() => applyDiscount(10)}
                style={{ flex: 1, backgroundColor: '#FF8A00', color: 'white', border: 'none', padding: '10px', borderRadius: '6px', cursor: 'pointer', fontSize: '11px', fontWeight: 'bold' }}
              >
                Apply 10% Off
              </button>
              <button 
                onClick={() => applyDiscount(20)}
                style={{ flex: 1, backgroundColor: '#E03C3C', color: 'white', border: 'none', padding: '10px', borderRadius: '6px', cursor: 'pointer', fontSize: '11px', fontWeight: 'bold' }}
              >
                Mega 20% Sale
              </button>
            </div>
          </div>
        </div>

        {/* RIGHT: Inventory List */}
        <div style={{ backgroundColor: 'white', padding: '25px', borderRadius: '12px', boxShadow: '0 4px 6px rgba(0,0,0,0.1)' }}>
          <h3 style={{ marginBottom: '20px' }}>Current Inventory</h3>
          <table style={{ width: '100%', borderCollapse: 'collapse' }}>
            <thead>
              <tr style={{ textAlign: 'left', borderBottom: '2px solid #eee' }}>
                <th style={{ padding: '10px' }}>Product</th>
                <th>Category</th>
                <th>Price</th>
                <th>Status</th>
              </tr>
            </thead>
            <tbody>
              {products.map(product => (
                <tr key={product.id} style={{ borderBottom: '1px solid #f9f9f9' }}>
                  <td style={{ padding: '10px' }}>{product.name}</td>
                  <td>{product.category}</td>
                  <td>RWF {product.price?.toLocaleString()}</td>
                  <td><span style={{ color: 'green', fontSize: '12px' }}>‚óè In Stock</span></td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>

      </div>
    </div>
  );
};

const inputStyle = { padding: '10px', borderRadius: '6px', border: '1px solid #ddd', fontSize: '14px' };

export default AdminDashboard;
