import React from 'react';
import { ShieldCheck, CreditCard, Truck, Store, CheckCircle, Users } from 'lucide-react';

const AboutUs = () => {
  const BRAND_BLUE = '#008ECC';

  return (
    <div style={container}>
      {/* 1. HERO HEADER */}
      <section style={heroHeader}>
        <h1 style={heroTitle}>About MegaMart Rwanda</h1>
        <p style={heroSubtitle}>Your trusted partner for global tech and electronics.</p>
      </section>

      <div style={contentWrapper}>
        {/* 2. HOW WE SELL (Business Model) */}
        <section style={infoSection}>
          <div style={iconBox}><Store size={32} color={BRAND_BLUE} /></div>
          <div style={textContent}>
            <h2 style={sectionTitle}>1. How We Sell</h2>
            <p style={paragraph}>
              MegaMart operates as a premium wholesale and retail marketplace. 
              We partner directly with verified global manufacturers to bring the latest gadgets 
              including Smartphones, Laptops, and Gaming gear directly to the Rwandan market 
              at competitive prices.
            </p>
            <ul style={list}>
              <li style={listItem}><CheckCircle size={16} color={BRAND_BLUE} /> Genuine products with official warranty</li>
              <li style={listItem}><CheckCircle size={16} color={BRAND_BLUE} /> Direct-from-factory wholesale pricing</li>
              <li style={listItem}><CheckCircle size={16} color={BRAND_BLUE} /> Quality testing on every device</li>
            </ul>
          </div>
        </section>

        {/* 3. HOW WE PAY (Payment System) */}
        <section style={infoSection}>
          <div style={iconBox}><CreditCard size={32} color={BRAND_BLUE} /></div>
          <div style={textContent}>
            <h2 style={sectionTitle}>2. How We Pay</h2>
            <p style={paragraph}>
              We provide a secure and flexible payment environment for all our customers. 
              Your financial security is our top priority.
            </p>
            <div style={paymentGrid}>
              <div style={paymentMethod}>Mobile Money (MoMo)</div>
              <div style={paymentMethod}>Credit/Debit Cards</div>
              <div style={paymentMethod}>Bank Transfer</div>
              <div style={paymentMethod}>Pay on Delivery (Selected items)</div>
            </div>
          </div>
        </section>

        {/* 4. DELIVERY SYSTEM */}
        <section style={infoSection}>
          <div style={iconBox}><Truck size={32} color={BRAND_BLUE} /></div>
          <div style={textContent}>
            <h2 style={sectionTitle}>3. Delivery System</h2>
            <p style={paragraph}>
              We offer efficient logistics tailored to your needs, ensuring your gadgets reach 
              you safely and quickly.
            </p>
            <div style={deliveryStats}>
              <div style={statCard}>
                <h4 style={statLabel}>Same Day</h4>
                <p style={statText}>Delivery within Kigali</p>
              </div>
              <div style={statCard}>
                <h4 style={statLabel}>2-3 Days</h4>
                <p style={statText}>Upcountry provinces</p>
              </div>
              <div style={statCard}>
                <h4 style={statLabel}>Worldwide</h4>
                <p style={statText}>Global shipping available</p>
              </div>
            </div>
          </div>
        </section>

        {/* 5. WHY CHOOSE US */}
        <div style={trustBanner}>
          <div style={trustItem}><ShieldCheck size={20} /> 100% Secure Checkout</div>
          <div style={trustItem}><Users size={20} /> 24/7 Dedicated Support</div>
        </div>
      </div>
    </div>
  );
};

// --- STYLES ---
const container = { backgroundColor: '#fff', minHeight: '100vh', paddingBottom: '80px' };

const heroHeader = {
  backgroundColor: '#008ECC',
  color: '#fff',
  padding: '60px 8%',
  textAlign: 'center'
};

const heroTitle = { fontSize: '36px', fontWeight: '800', margin: '0 0 10px 0' };
const heroSubtitle = { fontSize: '18px', opacity: 0.9 };

const contentWrapper = { maxWidth: '1000px', margin: '0 auto', padding: '40px 20px' };

const infoSection = {
  display: 'flex',
  gap: '30px',
  marginBottom: '60px',
  alignItems: 'flex-start'
};

const iconBox = {
  backgroundColor: '#F0F8FF',
  padding: '20px',
  borderRadius: '15px'
};

const textContent = { flex: 1 };
const sectionTitle = { fontSize: '24px', fontWeight: '700', color: '#131921', marginBottom: '15px' };
const paragraph = { color: '#555', lineHeight: '1.6', fontSize: '16px', marginBottom: '20px' };

const list = { listStyle: 'none', padding: 0 };
const listItem = { display: 'flex', alignItems: 'center', gap: '10px', marginBottom: '10px', color: '#444', fontWeight: '500' };

const paymentGrid = { display: 'grid', gridTemplateColumns: 'repeat(2, 1fr)', gap: '10px' };
const paymentMethod = { backgroundColor: '#f5f5f5', padding: '12px', borderRadius: '8px', fontSize: '14px', fontWeight: '600', textAlign: 'center' };

const deliveryStats = { display: 'flex', gap: '15px', marginTop: '20px' };
const statCard = { flex: 1, backgroundColor: '#f9f9f9', padding: '15px', borderRadius: '10px', border: '1px solid #eee' };
const statLabel = { margin: '0 0 5px 0', color: '#008ECC', fontSize: '16px' };
const statText = { margin: 0, fontSize: '13px', color: '#666' };

const trustBanner = {
  marginTop: '40px',
  display: 'flex',
  justifyContent: 'center',
  gap: '40px',
  padding: '30px',
  backgroundColor: '#131921',
  borderRadius: '15px',
  color: '#fff'
};

const trustItem = { display: 'flex', alignItems: 'center', gap: '10px', fontWeight: '600' };

export default AboutUs;
