import React, { useState } from 'react';
import { supabase } from '../supabaseClient';
import { Search, Package, Truck, CheckCircle, Clock } from 'lucide-react';

const TrackOrder = () => {
  const [orderId, setOrderId] = useState('');
  const [order, setOrder] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleTrack = async (e) => {
    e.preventDefault();
    if (!orderId) return;

    setLoading(true);
    const { data, error } = await supabase
      .from('orders')
      .select('*')
      .eq('id', orderId)
      .single();

    if (error) {
      alert("Order not found. Please check the ID and try again.");
      setOrder(null);
    } else {
      setOrder(data);
    }
    setLoading(false);
  };

  const getStatusStep = (status) => {
    const steps = ['pending', 'paid', 'shipped', 'delivered'];
    return steps.indexOf(status?.toLowerCase());
  };

  return (
    <div style={container}>
      <div style={searchCard}>
        <h2>Track Your Order</h2>
        <p style={{ color: '#666', fontSize: '14px' }}>Enter the Order ID from your confirmation screen or email.</p>
        <form onSubmit={handleTrack} style={searchForm}>
          <input 
            style={inputStyle} 
            placeholder="e.g. 550e8400-e29b-41d4-a716..." 
            value={orderId}
            onChange={(e) => setOrderId(e.target.value)}
          />
          <button type="submit" style={btnStyle} disabled={loading}>
            {loading ? "Searching..." : <Search size={20} />}
          </button>
        </form>
      </div>

      {order && (
        <div style={resultCard}>
          <div style={statusHeader}>
            <Clock size={20} /> <span>Status: <strong>{order.status.toUpperCase()}</strong></span>
          </div>
          
          <div style={timeline}>
            <div style={step(getStatusStep(order.status) >= 1)}><CheckCircle size={18}/> Paid</div>
            <div style={connector(getStatusStep(order.status) >= 2)} />
            <div style={step(getStatusStep(order.status) >= 2)}><Truck size={18}/> Shipped</div>
            <div style={connector(getStatusStep(order.status) >= 3)} />
            <div style={step(getStatusStep(order.status) >= 3)}><Package size={18}/> Delivered</div>
          </div>

          <div style={detailsBox}>
            <p><strong>Shipping to:</strong> {order.shipping_address}</p>
            <p><strong>Total Paid:</strong> {order.currency} {order.total_amount}</p>
          </div>
        </div>
      )}
    </div>
  );
};

// --- STYLES ---
const container = { padding: '60px 10%', display: 'flex', flexDirection: 'column', alignItems: 'center', minHeight: '70vh' };
const searchCard = { backgroundColor: 'white', padding: '30px', borderRadius: '12px', width: '100%', maxWidth: '600px', textAlign: 'center', boxShadow: '0 2px 10px rgba(0,0,0,0.05)' };
const searchForm = { display: 'flex', gap: '10px', marginTop: '20px' };
const inputStyle = { flex: 1, padding: '12px', borderRadius: '8px', border: '1px solid #ddd', outline: 'none' };
const btnStyle = { backgroundColor: '#005efd', color: 'white', border: 'none', padding: '0 20px', borderRadius: '8px', cursor: 'pointer' };

const resultCard = { marginTop: '30px', backgroundColor: 'white', padding: '30px', borderRadius: '12px', width: '100%', maxWidth: '600px' };
const statusHeader = { display: 'flex', alignItems: 'center', gap: '10px', marginBottom: '30px', color: '#005efd' };
const timeline = { display: 'flex', alignItems: 'center', justifyContent: 'space-between', marginBottom: '30px' };
const step = (active) => ({ display: 'flex', flexDirection: 'column', alignItems: 'center', gap: '5px', fontSize: '12px', color: active ? '#28a745' : '#ccc', fontWeight: active ? 'bold' : 'normal' });
const connector = (active) => ({ flex: 1, height: '2px', backgroundColor: active ? '#28a745' : '#eee', margin: '0 10px' });
const detailsBox = { padding: '15px', backgroundColor: '#f9f9f9', borderRadius: '8px', fontSize: '14px', lineHeight: '1.6' };

export default TrackOrder;
