import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { supabase } from '../supabaseClient';
import { 
  ShieldCheck, CheckCircle, 
  Globe, CreditCard, Wallet, Smartphone, Ticket, Tag
} from 'lucide-react';

const CheckoutPage = ({ cart, setCart, user }) => {
  const navigate = useNavigate();
  const [step, setStep] = useState(1);
  const [loading, setLoading] = useState(false);
  const [selectedCountry, setSelectedCountry] = useState('Rwanda');
  const [currency, setCurrency] = useState({ code: 'RWF', symbol: 'RWF', rate: 1 });
  const [paymentMethod, setPaymentMethod] = useState('card');

  // --- COUPON STATES ---
  const [couponCode, setCouponCode] = useState('');
  const [appliedCoupon, setAppliedCoupon] = useState(null);
  const [couponError, setCouponError] = useState('');
  const [isApplying, setIsApplying] = useState(false);

  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    address: ''
  });

  const currencies = [
    { code: 'RWF', symbol: 'RWF', rate: 1 },
    { code: 'USD', symbol: '$', rate: 0.00078 },
    { code: 'EUR', symbol: '€', rate: 0.00072 }
  ];

  // Calculations
  const subtotal = cart.reduce((acc, item) => acc + (item.price || 0), 0);
  
  // Apply Discount Logic
  const discountAmount = appliedCoupon ? (subtotal * appliedCoupon.discount_percent) / 100 : 0;
  const delivery = selectedCountry === 'Rwanda' ? 2000 : 15000;
  const totalInRWF = (subtotal - discountAmount) + delivery;

  const formatPrice = (amount) => {
    const converted = (amount * currency.rate).toLocaleString(undefined, {
      minimumFractionDigits: currency.code === 'RWF' ? 0 : 2,
      maximumFractionDigits: currency.code === 'RWF' ? 0 : 2
    });
    return `${currency.symbol} ${converted}`;
  };

  // --- COUPON VALIDATION ---
  const handleApplyCoupon = async () => {
    if (!couponCode) return;
    setIsApplying(true);
    setCouponError('');

    try {
      const { data, error } = await supabase
        .from('promotions')
        .select('*')
        .eq('code', couponCode.toUpperCase())
        .eq('is_active', true)
        .single();

      if (error || !data) {
        setCouponError('Invalid or expired code');
        setAppliedCoupon(null);
      } else {
        setAppliedCoupon(data);
        setCouponError('');
      }
    } catch (err) {
      setCouponError('Error verifying coupon');
    } finally {
      setIsApplying(false);
    }
  };

  const handlePayment = async () => {
    if (!formData.firstName || !formData.address) {
      alert("Please fill in your name and address.");
      return;
    }

    setLoading(true);
    try {
      const { error } = await supabase
        .from('orders')
        .insert([
          {
            user_email: user?.email || 'Guest',
            items: cart,
            total_amount: totalInRWF, // Saving the discounted total in base RWF
            currency: currency.code,
            shipping_address: `${formData.firstName} ${formData.lastName}, ${formData.address}, ${selectedCountry}`,
            status: 'paid',
            payment_method: paymentMethod,
            coupon_used: appliedCoupon ? appliedCoupon.code : null // SAVES FOR ADMIN TRACKING
          }
        ]);

      if (error) throw error;
      setCart([]);
      setStep(3);
    } catch (error) {
      alert("Error saving order: " + error.message);
    } finally {
      setLoading(false);
    }
  };

  if (step === 3) {
    return (
      <div style={successContainer}>
        <CheckCircle size={80} color="#28a745" />
        <h1>Order Confirmed!</h1>
        <p>Thank you, {formData.firstName}! Your order is shipping to {selectedCountry}.</p>
        {appliedCoupon && (
          <p style={{color: '#28a745', fontWeight: 'bold'}}>
            You saved {appliedCoupon.discount_percent}% with code {appliedCoupon.code}!
          </p>
        )}
        <button onClick={() => navigate('/')} style={continueBtn}>Continue Shopping</button>
      </div>
    );
  }

  return (
    <div style={pageWrapper}>
      <div style={mainContent}>
        <div style={sectionCard}>
          <h2 style={sectionHeader}><Globe size={20} /> 1. Shipping Details</h2>
          <label style={labelStyle}>Country</label>
          <select style={selectInput} value={selectedCountry} onChange={(e) => setSelectedCountry(e.target.value)}>
            <option value="Rwanda">Rwanda</option>
            <option value="United States">United States</option>
            <option value="United Kingdom">United Kingdom</option>
          </select>
          
          <div style={formRow}>
            <div style={{flex: 1}}>
              <label style={labelStyle}>First Name</label>
              <input style={inputFull} placeholder="John" value={formData.firstName} onChange={(e) => setFormData({...formData, firstName: e.target.value})} />
            </div>
            <div style={{flex: 1}}>
              <label style={labelStyle}>Last Name</label>
              <input style={inputFull} placeholder="Doe" value={formData.lastName} onChange={(e) => setFormData({...formData, lastName: e.target.value})} />
            </div>
          </div>
          
          <label style={labelStyle}>Street Address</label>
          <input style={inputFull} placeholder="House number and street name" value={formData.address} onChange={(e) => setFormData({...formData, address: e.target.value})} />
        </div>

        <div style={sectionCard}>
          <h2 style={sectionHeader}><CreditCard size={20} /> 2. Payment & Currency</h2>
          <div style={currencyGrid}>
            {currencies.map(c => (
              <button key={c.code} onClick={() => setCurrency(c)} style={currency.code === c.code ? activeCurrencyBtn : currencyBtn}>{c.code}</button>
            ))}
          </div>

          <div style={paymentGrid}>
            <div style={paymentMethod === 'card' ? activePayment : paymentOption} onClick={() => setPaymentMethod('card')}><CreditCard size={18} /> Card</div>
            {selectedCountry === 'Rwanda' && (
              <div style={paymentMethod === 'momo' ? activePayment : paymentOption} onClick={() => setPaymentMethod('momo')}><Smartphone size={18} /> Mobile Money</div>
            )}
          </div>
        </div>
      </div>

      <div style={sidebar}>
        <div style={summaryCard}>
          <h3 style={{marginTop: 0}}>Order Summary</h3>
          
          {/* --- COUPON INPUT SECTION --- */}
          <div style={couponSection}>
            <div style={{display: 'flex', gap: '8px'}}>
              <input 
                style={couponInput} 
                placeholder="Promo Code" 
                value={couponCode} 
                onChange={(e) => setCouponCode(e.target.value.toUpperCase())}
              />
              <button 
                style={applyBtn} 
                onClick={handleApplyCoupon} 
                disabled={isApplying || !couponCode}
              >
                {isApplying ? '...' : 'Apply'}
              </button>
            </div>
            {couponError && <p style={{color: '#d91e18', fontSize: '11px', marginTop: '5px'}}>{couponError}</p>}
            {appliedCoupon && (
              <div style={appliedBadge}>
                <Tag size={12} /> {appliedCoupon.code} Applied (-{appliedCoupon.discount_percent}%)
                <button onClick={() => setAppliedCoupon(null)} style={removeCoupon}>×</button>
              </div>
            )}
          </div>

          <div style={summaryRow}><span>Items ({cart.length})</span><span>{formatPrice(subtotal)}</span></div>
          {appliedCoupon && (
            <div style={{...summaryRow, color: '#28a745'}}>
              <span>Discount</span><span>-{formatPrice(discountAmount)}</span>
            </div>
          )}
          <div style={summaryRow}><span>Shipping</span><span>{formatPrice(delivery)}</span></div>
          <hr style={divider} />
          <div style={totalRow}><span>Total</span><span>{formatPrice(totalInRWF)}</span></div>
          
          <button style={confirmBtn} onClick={handlePayment} disabled={loading || cart.length === 0}>
            {loading ? "Processing..." : `Pay ${formatPrice(totalInRWF)}`}
          </button>
          
          <div style={protectionBox}>
            <ShieldCheck size={16} color="#0053a0" />
            <span style={{fontSize: '11px'}}>Secure MegaMart Checkout</span>
          </div>
        </div>
      </div>
    </div>
  );
};

// --- ADDED STYLES FOR COUPONS ---
const couponSection = { marginBottom: '20px', backgroundColor: '#f9f9f9', padding: '10px', borderRadius: '8px' };
const couponInput = { flex: 1, padding: '8px', borderRadius: '5px', border: '1px solid #ddd', width: '60%', fontSize: '13px' };
const applyBtn = { padding: '8px 12px', backgroundColor: '#1a1a1a', color: 'white', border: 'none', borderRadius: '5px', cursor: 'pointer', fontSize: '13px' };
const appliedBadge = { display: 'flex', alignItems: 'center', gap: '5px', marginTop: '10px', color: '#28a745', fontSize: '12px', fontWeight: 'bold' };
const removeCoupon = { background: 'none', border: 'none', color: '#d91e18', cursor: 'pointer', fontSize: '16px', fontWeight: 'bold' };

// --- EXISTING STYLES ---
const pageWrapper = { display: 'flex', flexWrap: 'wrap', gap: '20px', padding: '20px 5%', backgroundColor: '#f8f9fa', minHeight: '100vh' };
const mainContent = { flex: '1 1 500px', minWidth: '300px' };
const sidebar = { flex: '1 1 300px', minWidth: '300px' };
const sectionCard = { backgroundColor: 'white', padding: '20px', borderRadius: '12px', border: '1px solid #eee', marginBottom: '20px' };
const sectionHeader = { fontSize: '18px', display: 'flex', alignItems: 'center', gap: '10px', marginBottom: '15px' };
const labelStyle = { fontSize: '12px', fontWeight: 'bold', color: '#666', marginBottom: '5px', display: 'block' };
const formRow = { display: 'flex', gap: '15px', marginBottom: '15px' };
const selectInput = { width: '100%', padding: '12px', borderRadius: '8px', border: '1px solid #ddd', marginBottom: '15px', fontSize: '14px' };
const inputFull = { width: '100%', padding: '12px', borderRadius: '8px', border: '1px solid #ddd', fontSize: '14px', boxSizing: 'border-box' };
const currencyGrid = { display: 'flex', gap: '8px', marginBottom: '20px' };
const currencyBtn = { flex: 1, padding: '10px', borderRadius: '8px', border: '1px solid #ddd', backgroundColor: 'white', cursor: 'pointer', fontWeight: 'bold' };
const activeCurrencyBtn = { ...currencyBtn, borderColor: '#005efd', backgroundColor: '#f0faff', color: '#005efd' };
const paymentGrid = { display: 'flex', flexDirection: 'column', gap: '10px' };
const paymentOption = { display: 'flex', alignItems: 'center', gap: '12px', padding: '15px', borderRadius: '10px', cursor: 'pointer', borderWidth: '1px', borderStyle: 'solid', borderColor: '#ddd', backgroundColor: 'white' };
const activePayment = { ...paymentOption, borderColor: '#005efd', backgroundColor: '#f0faff', borderWidth: '2px' };
const summaryCard = { backgroundColor: 'white', padding: '20px', borderRadius: '12px', border: '1px solid #eee', position: 'sticky', top: '80px' };
const summaryRow = { display: 'flex', justifyContent: 'space-between', marginBottom: '10px', fontSize: '14px' };
const divider = { border: 'none', borderTop: '1px solid #eee', margin: '15px 0' };
const totalRow = { display: 'flex', justifyContent: 'space-between', fontWeight: 'bold', fontSize: '18px', color: '#d91e18' };
const confirmBtn = { width: '100%', padding: '16px', backgroundColor: '#005efd', color: 'white', border: 'none', borderRadius: '30px', fontWeight: 'bold', marginTop: '20px', cursor: 'pointer', fontSize: '16px' };
const protectionBox = { display: 'flex', gap: '8px', marginTop: '15px', color: '#666', alignItems: 'center', justifyContent: 'center' };
const successContainer = { textAlign: 'center', padding: '80px 20px' };
const continueBtn = { marginTop: '20px', padding: '12px 24px', backgroundColor: '#1a1a1a', color: 'white', border: 'none', borderRadius: '30px', cursor: 'pointer' };

export default CheckoutPage;
