import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Link, Navigate } from 'react-router-dom';
import { supabase } from './supabaseClient'; 

// Pages
import HomePage from './pages/HomePage';
import CategoryPage from './pages/CategoryPage'; 
import AdminDashboard from './pages/AdminDashboard';
import SuccessPage from './pages/SuccessPage'; 
import AuthPage from './pages/Authpage'; 
import ProfilePage from './pages/ProfilePage';
import LoginPage from './pages/LoginPage';
import ProductDetailPage from './pages/ProductDetailPage'; // NEW: Import your detail page

// Components
import Navbar from './components/Navbar';
import Sidebar from './components/Sidebar'; 
import CheckoutModal from './components/CheckoutModal';
import ProductSection from './components/ProductionSection';

function App() {
  const [cartCount, setCartCount] = useState(0);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [isSidebarOpen, setIsSidebarOpen] = useState(false); 
  const [isAdmin, setIsAdmin] = useState(false);
  const [user, setUser] = useState(null);

  useEffect(() => {
    supabase.auth.getSession().then(({ data: { session } }) => {
      setUser(session?.user ?? null);
    });

    const { data: { subscription } } = supabase.auth.onAuthStateChange((_event, session) => {
      setUser(session?.user ?? null);
    });

    return () => subscription.unsubscribe();
  }, []);

  const handleUserLogout = async () => {
    await supabase.auth.signOut();
    alert("Logged out successfully");
  };

  const addToCart = () => setCartCount(prev => prev + 1);

  return (
    <Router future={{ v7_startTransition: true, v7_relativeSplatPath: true }}>
      <div className="App" style={{ minHeight: '100vh', display: 'flex', flexDirection: 'column', fontFamily: 'Arial, sans-serif' }}>
        
        <Sidebar 
          isOpen={isSidebarOpen} 
          onClose={() => setIsSidebarOpen(false)} 
          user={user} 
        />

        <div style={topInfoBar}>
          <Link to="/" style={{ color: 'white', textDecoration: 'none', opacity: 0.8 }}>
            MEGAMART RWANDA - OFFICIAL GADGET STORE
          </Link>
          
          {user && (
            <div style={{ display: 'flex', gap: '15px', alignItems: 'center' }}>
              <span style={{ opacity: 0.8 }}><strong>{user.email}</strong></span>
              <button onClick={handleUserLogout} style={logoutBtn}>LOGOUT</button>
            </div>
          )}
        </div>

        <Navbar 
          cartCount={cartCount} 
          openModal={() => setIsModalOpen(true)} 
          openSidebar={() => setIsSidebarOpen(true)} 
          user={user} 
        />
        
        <main style={{ flex: 1 }}>
          <Routes>
            <Route path="/" element={<HomePage addToCart={addToCart} />} />
            <Route path="/category/:categoryName" element={<CategoryPage addToCart={addToCart} />} />
            <Route path="/deals/:dealType" element={<CategoryPage addToCart={addToCart} isDealPage={true} />} />
            
            {/* NEW: Product Detail Route */}
            {/* The :productId part captures the ID from the URL so the page knows which product to fetch */}
            <Route path="/product/:productId" element={<ProductDetailPage addToCart={addToCart} />} />
            
            <Route path="/login" element={<AuthPage />} />
            <Route path="/profile" element={<ProfilePage user={user} />} />
            <Route path="/success" element={<SuccessPage user={user} />} />

            <Route path="/admin-login" element={<LoginPage setIsAdmin={setIsAdmin} />} />
            <Route 
              path="/admin" 
              element={isAdmin ? <AdminDashboard setIsAdmin={setIsAdmin} /> : <Navigate to="/admin-login" />} 
            />
          </Routes>
        </main>

        {isModalOpen && (
          <CheckoutModal 
            cartCount={cartCount} 
            closeModal={() => setIsModalOpen(false)} 
            user={user} 
          />
        )}

        <footer style={footerStyle}>
          <p style={{ fontSize: '12px', color: '#888', margin: 0 }}>Â© 2025 MegaMart Rwanda. Built for the future of Rwandan E-commerce.</p>
        </footer>
      </div>
    </Router>
  );
}

const topInfoBar = { backgroundColor: '#131921', color: 'white', padding: '8px 50px', fontSize: '11px', display: 'flex', justifyContent: 'space-between', alignItems: 'center' };
const logoutBtn = { background: 'none', border: 'none', color: '#FF4D4D', cursor: 'pointer', fontSize: '10px', fontWeight: 'bold' };
const footerStyle = { backgroundColor: '#fff', padding: '30px', textAlign: 'center', borderTop: '1px solid #eee' };

export default App;
