import React, { useState, useEffect } from 'react';
import { supabase } from '../supabaseClient';
import { useNavigate } from 'react-router-dom';
import { GoogleGenerativeAI } from "@google/generative-ai";
import { 
  Package, Truck, CheckCircle, Tag, LogOut, 
  RefreshCcw, Trash2, Search, DollarSign, 
  ShoppingCart, Copy, ExternalLink, Sparkles, Image as ImageIcon
} from 'lucide-react';

const AdminDashboard = ({ setIsAdmin }) => {
  const [activeTab, setActiveTab] = useState('orders'); 
  const [products, setProducts] = useState([]);
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [isGenerating, setIsGenerating] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');
  
  const [formData, setFormData] = useState({ 
    name: '', 
    price: '', 
    category: 'Smartphones', 
    description: '',
    image_url: '' // Added for AI Image
  });
  
  const [totalSales, setTotalSales] = useState(0);

  useEffect(() => {
    fetchInventory();
    fetchOrders();
  }, []);

  // --- MAGIC AI FILL: Text (Gemini) + Image (Unsplash) ---
  const generateProductDetails = async () => {
    if (!formData.name) {
      alert("Please enter a product name first!");
      return;
    }

    setIsGenerating(true);
    try {
      const geminiKey = process.env.REACT_APP_GEMINI_API_KEY;
      const unsplashKey = process.env.REACT_APP_UNSPLASH_KEY;

      // 1. Get AI Text Description
      const genAI = new GoogleGenerativeAI(geminiKey);
      const model = genAI.getGenerativeModel({ model: "gemini-1.5-flash" });
      const prompt = `You are a tech expert. Write a professional description for: ${formData.name}. 
      Include 2 marketing sentences and a bulleted list of 3 key technical specs.`;

      const result = await model.generateContent(prompt);
      const aiDescription = result.response.text();

      // 2. Get AI Product Image from Unsplash
      const imageRes = await fetch(
        `https://api.unsplash.com/search/photos?query=${formData.name}&client_id=${unsplashKey}&per_page=1`
      );
      const imageData = await imageRes.json();
      const aiImageUrl = imageData.results[0]?.urls?.regular || '';

      // 3. Update Form with both Text and Image
      setFormData({ 
        ...formData, 
        description: aiDescription,
        image_url: aiImageUrl
      });

    } catch (error) {
      console.error("Magic Fill Error:", error);
      alert("AI failed to fetch details. Check your .env file keys.");
    } finally {
      setIsGenerating(false);
    }
  };

  const fetchInventory = async () => {
    const { data, error } = await supabase.from('products').select('*');
    if (error) console.error("Inventory Fetch Error:", error);
    if (data) setProducts(data);
  };

  const fetchOrders = async () => {
    setLoading(true);
    const { data, error } = await supabase.from('orders').select('*').order('created_at', { ascending: false });
    if (error) console.error("Orders Fetch Error:", error);
    if (data) {
      setOrders(data);
      const total = data.reduce((sum, order) => sum + (order.total_amount || 0), 0);
      setTotalSales(total);
    }
    setLoading(false);
  };

  const handleAddProduct = async (e) => {
    e.preventDefault();
    const productToInsert = { 
      name: formData.name,
      price: parseFloat(formData.price),
      category: formData.category,
      description: formData.description || 'No description provided',
      image_url: formData.image_url // Save the image URL to Supabase
    };

    const { error } = await supabase.from('products').insert([productToInsert]);

    if (error) {
      alert(`Failed: ${error.message}`);
    } else {
      alert("Product Registered Successfully!");
      setFormData({ name: '', price: '', category: 'Smartphones', description: '', image_url: '' });
      fetchInventory();
    }
  };

  const deleteProduct = async (id, name) => {
    if (window.confirm(`Are you sure you want to delete "${name}"?`)) {
      const { error } = await supabase.from('products').delete().eq('id', id);
      if (!error) fetchInventory();
    }
  };

  const getStatusBadge = (s) => ({
    padding: '3px 7px', borderRadius: '10px', fontSize: '9px', fontWeight: 'bold', textTransform: 'uppercase',
    backgroundColor: s === 'pending' ? '#fff3cd' : s === 'shipped' ? '#d1ecf1' : '#d4edda',
    color: s === 'pending' ? '#856404' : s === 'shipped' ? '#0c5460' : '#155724'
  });

  const filteredProducts = products.filter(p => 
    p.name?.toLowerCase().includes(searchTerm.toLowerCase()) ||
    p.category?.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div style={containerStyle}>
      <div style={headerStyle}>
        <div>
          <h1 style={{ margin: 0, fontSize: '22px' }}>MegaMart Command Center</h1>
          <p style={{ color: '#666', fontSize: '13px' }}>Rwanda Operations</p>
        </div>
        <button onClick={() => setIsAdmin(false)} style={logoutBtn}><LogOut size={16} /> Logout</button>
      </div>

      <div style={statsContainer}>
        <div style={statCard}>
          <div style={statIconBox}><DollarSign size={20} color="#28a745" /></div>
          <div>
            <p style={statLabel}>REVENUE</p>
            <h2 style={statValue}>RWF {totalSales.toLocaleString()}</h2>
          </div>
        </div>
      </div>

      <div style={tabBar}>
        <button onClick={() => setActiveTab('orders')} style={activeTab === 'orders' ? activeTabBtn : tabBtn}>
          <Package size={18} /> Orders
        </button>
        <button onClick={() => setActiveTab('inventory')} style={activeTab === 'inventory' ? activeTabBtn : tabBtn}>
          <Tag size={18} /> Inventory
        </button>
      </div>

      {activeTab === 'orders' ? (
        <div style={panelStyle}>
          <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '15px' }}>
            <h3 style={{margin:0}}>Recent Sales</h3>
            <button onClick={fetchOrders} style={refreshBtn}><RefreshCcw size={18} /></button>
          </div>
          <div style={{ overflowX: 'auto' }}>
            <table style={tableStyle}>
              <thead>
                <tr style={thRow}>
                  <th>ID / DATE</th>
                  <th>CUSTOMER</th>
                  <th>TOTAL</th>
                  <th>STATUS</th>
                  <th>ACTION</th>
                </tr>
              </thead>
              <tbody>
                {orders.map(o => (
                  <tr key={o.id} style={trStyle}>
                    <td style={{ padding: '12px 0' }}>
                      <div style={{fontSize: '10px', color: '#888'}}>{o.id.slice(0,8)}</div>
                      <div style={{fontSize: '11px'}}>{new Date(o.created_at).toLocaleDateString()}</div>
                    </td>
                    <td style={{fontSize: '12px'}}>{o.user_email || 'Guest'}</td>
                    <td style={{fontWeight: 'bold'}}>RWF {o.total_amount?.toLocaleString()}</td>
                    <td><span style={getStatusBadge(o.status)}>{o.status}</span></td>
                    <td><button onClick={() => alert("Copied ID")} style={actionBtn}><Copy size={14} /></button></td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      ) : (
        <div style={inventoryGridStyle}>
          <div style={panelStyle}>
            <h3>Add Product</h3>
            <form onSubmit={handleAddProduct} style={{ display: 'flex', flexDirection: 'column', gap: '10px' }}>
              
              {/* Image Preview Area */}
              {formData.image_url && (
                <div style={imagePreviewContainer}>
                  <img src={formData.image_url} alt="AI Preview" style={imagePreviewImg} />
                  <p style={{fontSize: '10px', color: '#888', marginTop: '5px'}}>AI Selected Image</p>
                </div>
              )}

              <div style={{ display: 'flex', gap: '8px' }}>
                <input 
                  type="text" 
                  placeholder="Product Name (e.g. MacBook Air M2)" 
                  style={{ ...inputStyle, flex: 1 }} 
                  onChange={e => setFormData({...formData, name: e.target.value})} 
                  value={formData.name} 
                  required 
                />
                <button 
                  type="button" 
                  onClick={generateProductDetails} 
                  disabled={isGenerating} 
                  style={magicBtn}
                  title="Magic Fill: Text & Image"
                >
                  {isGenerating ? <RefreshCcw className="animate-spin" size={16} /> : <Sparkles size={16} />}
                </button>
              </div>
              
              <input type="number" placeholder="Price (RWF)" style={inputStyle} onChange={e => setFormData({...formData, price: e.target.value})} value={formData.price} required />
              
              <textarea 
                placeholder="Description..." 
                style={{ ...inputStyle, minHeight: '120px', resize: 'vertical' }} 
                onChange={e => setFormData({...formData, description: e.target.value})} 
                value={formData.description} 
              />
              
              <input 
                type="text" 
                placeholder="Image URL (AI fills this automatically)" 
                style={{ ...inputStyle, fontSize: '11px', color: '#666' }} 
                value={formData.image_url} 
                onChange={e => setFormData({...formData, image_url: e.target.value})}
              />

              <select style={inputStyle} value={formData.category} onChange={e => setFormData({...formData, category: e.target.value})}>
                <option>Smartphones</option><option>Laptops</option><option>Accessories</option>
              </select>
              <button type="submit" style={mainBtn} disabled={loading}>
                {loading ? 'Adding...' : 'Save Product'}
              </button>
            </form>
          </div>

          <div style={panelStyle}>
            <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '15px' }}>
              <h3 style={{margin:0}}>Stock</h3>
              <div style={searchContainer}>
                <Search size={14} color="#888" />
                <input type="text" placeholder="Search..." style={searchInput} value={searchTerm} onChange={(e) => setSearchTerm(e.target.value)} />
              </div>
            </div>
            <div style={{ overflowX: 'auto' }}>
              <table style={tableStyle}>
                <tbody>
                  {filteredProducts.map(p => (
                    <tr key={p.id} style={trStyle}>
                      <td style={{ padding: '10px 0', display: 'flex', alignItems: 'center', gap: '10px' }}>
                        {p.image_url && <img src={p.image_url} style={{width: '30px', height: '30px', borderRadius: '4px', objectFit: 'cover'}} alt=""/>}
                        <div>
                          <div style={{fontWeight:'bold'}}>{p.name}</div>
                          <div style={{fontSize:'10px', color: '#888'}}>{p.category}</div>
                        </div>
                      </td>
                      <td style={{textAlign: 'right'}}>
                        <div style={{fontSize: '12px'}}>RWF {p.price?.toLocaleString()}</div>
                        <button onClick={() => deleteProduct(p.id, p.name)} style={deleteBtn}><Trash2 size={12} /></button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

// --- STYLES ---
const containerStyle = { padding: '15px', backgroundColor: '#f4f7f6', minHeight: '100vh', fontFamily: 'sans-serif' };
