const deleteBtn = { backgroundColor: '#ff4d4d', color: 'white', border: 'none', padding: '5px', borderRadius: '4px', cursor: 'pointer' };
const imagePreviewContainer = { textAlign: 'center', border: '2px dashed #ddd', padding: '10px', borderRadius: '8px', marginBottom: '10px' };
const imagePreviewImg = { width: '100%', maxHeight: '150px', objectFit: 'contain', borderRadius: '5px' };

export default AdminDashboard;
