import React, { useEffect, useState } from 'react';
import { supabase } from '../supabaseClient';
import { ShoppingBag, ArrowRight, Zap, Star, ShieldCheck, Timer } from 'lucide-react';
import { Link, useNavigate } from 'react-router-dom';

const HomePage = ({ addToCart }) => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  // --- REAL PRODUCT PHOTOS (Transparent Backgrounds) ---
  const categories = [
    { 
      name: 'Smartphones', 
      img: 'https://images.ctfassets.net/16nm6vz47z5u/7p9vP7U0S8f7Z6yW0Q6v5/6a7a7a7a7a7a7a7a7a7a7a7a7a7a7a7a/iPhone_15_Pro_Natural_Titanium_PDP_Image_Position-1__en-US.png?w=300&q=80' 
    },
    { 
      name: 'Laptops', 
      img: 'https://pngimg.com/d/macbook_PNG65.png' 
    },
    { 
      name: 'Watch', 
      img: 'https://pngimg.com/d/watches_PNG9854.png' 
    },
    { 
      name: 'Audio', 
      img: 'https://pngimg.com/d/headphones_PNG101925.png' 
    },
    { 
      name: 'Gaming', 
      img: 'https://assets.nintendo.com/image/upload/f_auto/q_auto/dpr_2.0/c_scale,w_400/ncom/en_US/switch/system/nintendo-switch-oled-model-white/gallery/image01' 
    }
  ];

  // --- COUNTDOWN LOGIC ---
  const [timeLeft, setTimeLeft] = useState({ hours: 24, minutes: 0, seconds: 0 });

  useEffect(() => {
    const timer = setInterval(() => {
      const now = new Date();
      const endOfDay = new Date();
      endOfDay.setHours(23, 59, 59);
      const diff = endOfDay - now;
      if (diff > 0) {
        setTimeLeft({
          hours: Math.floor((diff / (1000 * 60 * 60)) % 24),
          minutes: Math.floor((diff / 1000 / 60) % 60),
          seconds: Math.floor((diff / 1000) % 60),
        });
      }
    }, 1000);
    return () => clearInterval(timer);
  }, []);

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    const { data, error } = await supabase.from('products').select('*');
    if (!error) setProducts(data);
    setLoading(false);
  };

  return (
    <div style={homeContainer}>
      {/* 1. TICKER BAR */}
      <div style={tickerWrapper}>
        <div style={slidingTrack}>
          {["iPhone 16 Pro", "MacBook M3", "S24 Ultra", "PS5 Pro", "AirPods Max"].map((item, i) => (
            <span key={i} style={tickerItem}>{item} <span style={{color: '#008ECC'}}>•</span></span>
          ))}
          {/* Duplicate for smooth scroll */}
          {["iPhone 16 Pro", "MacBook M3", "S24 Ultra", "PS5 Pro", "AirPods Max"].map((item, i) => (
            <span key={i+'d'} style={tickerItem}>{item} <span style={{color: '#008ECC'}}>•</span></span>
          ))}
        </div>
      </div>

      {/* 2. HERO SECTION */}
      <section style={heroSection}>
        <div style={heroOverlay}>
          <div style={timerBadge}>
            <Timer size={14} /> 
            <span>ENDS IN: {timeLeft.hours}h {timeLeft.minutes}m {timeLeft.seconds}s</span>
          </div>
          <h1 style={heroTitle}>The Future of <br/> <span style={highlightText}>Gadgets</span></h1>
          <p style={heroSubtitle}>Verified global tech delivered to your doorstep in Rwanda.</p>
          <div style={heroButtons}>
            <button style={primaryBtn} onClick={() => document.getElementById('featured').scrollIntoView({ behavior: 'smooth' })}>
              Shop Now <ArrowRight size={18} />
            </button>
            <button style={secondaryBtn} onClick={() => navigate('/deals/hot-deals')}>View Deals</button>
          </div>
        </div>
      </section>

      {/* 3. CATEGORY BUBBLES WITH REAL PRODUCT PHOTOS */}
      <div style={categoryWrapper}>
        {categories.map(cat => (
          <Link key={cat.name} to={`/category/${cat.name}`} style={catCircleLink}>
            <div style={catCircle} className="photo-bubble">
              <img src={cat.img} alt={cat.name} style={catPhoto} />
            </div>
            <span style={catName}>{cat.name}</span>
          </Link>
        ))}
      </div>

      {/* 4. PRODUCT GRID */}
      <div style={sectionWrapper} id="featured">
        <h2 style={{marginBottom: '30px', fontWeight: '800'}}>Featured Products</h2>
        {loading ? <p>Loading...</p> : (
          <div style={productGrid}>
            {products.map((product) => (
              <div key={product.id} style={productCard}>
                <div style={imagePlaceholder}>
                  <img src={product.image_url || 'https://via.placeholder.com/200'} alt={product.name} style={productImg} />
                </div>
                <div style={cardContent}>
                  <span style={cardCategory}>{product.category}</span>
                  <h3 style={cardTitle}>{product.name}</h3>
                  <div style={priceRow}>
                    <span style={cardPrice}>RWF {product.price?.toLocaleString()}</span>
                    <button onClick={() => addToCart(product)} style={addBtn}>+</button>
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>

      <style>
        {`
          @keyframes scrollTicker { 0% { transform: translateX(0); } 100% { transform: translateX(-50%); } }
          .photo-bubble { transition: all 0.4s cubic-bezier(0.175, 0.885, 0.32, 1.275); cursor: pointer; }
          .photo-bubble:hover { 
            transform: scale(1.2) rotate(5deg); 
            background-color: #fff !important;
            box-shadow: 0 15px 30px rgba(0,0,0,0.1);
          }
        `}
      </style>
    </div>
  );
};

// --- STYLES ---
const catPhoto = { width: '85%', height: '85%', objectFit: 'contain' };
const productImg = { width: '100%', height: '100%', objectFit: 'contain' };

const homeContainer = { backgroundColor: '#fcfcfc', minHeight: '100vh' };
const tickerWrapper = { overflow: 'hidden', backgroundColor: '#232f3e', color: 'white', height: '40px', display: 'flex', alignItems: 'center' };
const slidingTrack = { display: 'flex', whiteSpace: 'nowrap', animation: 'scrollTicker 30s linear infinite' };
const tickerItem = { padding: '0 30px', fontSize: '13px', fontWeight: '600' };

const heroSection = { height: '70vh', background: 'linear-gradient(rgba(0,0,0,0.4), rgba(0,0,0,0.6)), url("https://images.unsplash.com/photo-1550745165-9bc0b252726f?q=80&w=2070&auto=format&fit=crop")', backgroundSize: 'cover', backgroundPosition: 'center', display: 'flex', alignItems: 'center', padding: '0 8%', color: 'white' };
