import React, { useEffect, useState } from 'react';
import { supabase } from '../supabaseClient';
import { ShoppingBag, ArrowRight, Zap, Star, ShieldCheck, Timer } from 'lucide-react';
import { Link, useNavigate } from 'react-router-dom';

const HomePage = ({ addToCart }) => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [timeLeft, setTimeLeft] = useState({ hours: 0, minutes: 0, seconds: 0 });
  const navigate = useNavigate();

  /* ================= REAL CDN CATEGORY IMAGES ================= */
  const categories = [
    {
      name: 'Smartphones',
      img: 'https://images.unsplash.com/photo-1511707171634-5f897ff02aa9?auto=format&fit=crop&w=500&q=80'
    },
    {
      name: 'Laptops',
      img: 'https://images.unsplash.com/photo-1517336714731-489689fd1ca8?auto=format&fit=crop&w=500&q=80'
    },
    {
      name: 'Watch',
      img: 'https://images.unsplash.com/photo-1523275335684-37898b6baf30?auto=format&fit=crop&w=500&q=80'
    },
    {
      name: 'Audio',
      img: 'https://images.unsplash.com/photo-1505740420928-5e560c06d30e?auto=format&fit=crop&w=500&q=80'
    },
    {
      name: 'Gaming',
      img: 'https://www.alibaba.com/product-detail/GameSir-Kaleid-Flux-Xbox-Gaming-Controller_1601524275561.html'
    }
  ];

  const techHighlights = [
    'iPhone 16 Pro',
    'MacBook M3 Max',
    'Samsung S24 Ultra',
    'PlayStation 5 Pro',
    'Apple Watch Ultra 2',
    'RTX 4090',
    'DJI Mini 4 Pro'
  ];

  /* ================= FLASH SALE TIMER ================= */
  useEffect(() => {
    const timer = setInterval(() => {
      const now = new Date();
      const end = new Date();
      end.setHours(23, 59, 59);
      const diff = end - now;

      if (diff > 0) {
        setTimeLeft({
          hours: Math.floor(diff / (1000 * 60 * 60)),
          minutes: Math.floor((diff / (1000 * 60)) % 60),
          seconds: Math.floor((diff / 1000) % 60)
        });
      }
    }, 1000);

    return () => clearInterval(timer);
  }, []);

  /* ================= FETCH PRODUCTS ================= */
  useEffect(() => {
    const fetchProducts = async () => {
      const { data, error } = await supabase.from('products').select('*');
      if (!error) setProducts(data);
      setLoading(false);
    };
    fetchProducts();
  }, []);

  return (
    <div style={homeContainer}>

      {/* ================= TICKER ================= */}
      <div style={tickerWrapper}>
        <div style={slidingTrack}>
          {[...techHighlights, ...techHighlights].map((item, i) => (
            <span key={i} style={tickerItem}>
              {item} <span style={dot}>•</span>
            </span>
          ))}
        </div>
      </div>

      {/* ================= HERO ================= */}
      <section style={heroSection}>
        <div>
          <div style={timerBadge}>
            <Timer size={14} />
            Flash Sale Ends In:
            <strong>{` ${timeLeft.hours}h ${timeLeft.minutes}m ${timeLeft.seconds}s`}</strong>
          </div>

          <h1 style={heroTitle}>
            The Future of <span style={highlight}>Gadgets</span> Is Here
          </h1>

          <p style={heroSubtitle}>
            Official Megamart Rwanda — Verified global tech, real warranty.
          </p>

          <div style={heroButtons}>
            <button
              style={primaryBtn}
              onClick={() =>
                document
                  .getElementById('featured-products')
                  .scrollIntoView({ behavior: 'smooth' })
              }
            >
              Shop Now <ArrowRight size={18} />
            </button>

            <button
              style={secondaryBtn}
              onClick={() => navigate('/deals/hot-deals')}
            >
              View Deals
            </button>
          </div>
        </div>
      </section>

      {/* ================= TRUST BAR ================= */}
      <div style={trustBar}>
        <div style={trustItem}><Zap size={20} /> Same Day Delivery</div>
        <div style={trustItem}><ShieldCheck size={20} /> Official Warranty</div>
        <div style={trustItem}><Star size={20} /> Top Rated Service</div>
      </div>

      {/* ================= CATEGORIES ================= */}
      <div style={categoryWrapper}>
        {categories.map(cat => (
          <Link key={cat.name} to={`/category/${cat.name}`} style={catLink}>
            <div style={catCircle}>
              <img src={cat.img} alt={cat.name} style={catImage} />
            </div>
            <span style={catName}>{cat.name}</span>
          </Link>
        ))}
      </div>

      {/* ================= PRODUCTS ================= */}
      <div style={sectionWrapper} id="featured-products">
        <div style={sectionHeader}>
          <h2>Featured Products</h2>
          <Link to="/category/all" style={viewAll}>View All</Link>
        </div>

        {loading ? (
          <div style={loader}>Loading products…</div>
        ) : (
          <div style={productGrid}>
            {products.map(product => (
              <div key={product.id} style={productCard}>
                <div style={imageBox}>
                  {product.image_url ? (
                    <img src={product.image_url} alt={product.name} style={productImg} />
                  ) : (
                    <ShoppingBag size={40} />
                  )}
                </div>

                <div style={cardBody}>
                  <span style={categoryLabel}>{product.category}</span>
                  <h3 style={productName}>{product.name}</h3>

                  <div style={priceRow}>
                    <strong style={price}>
                      RWF {product.price?.toLocaleString()}
                    </strong>
                    <button
                      style={addBtn}
                      onClick={() => addToCart(product)}
                    >
                      +
                    </button>
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

/* ================= STYLES ================= */

const homeContainer = { background: '#fcfcfc', minHeight: '100vh' };

const tickerWrapper = {
  background: '#232f3e',
  overflow: 'hidden',
  height: '40px',
  display: 'flex',
  alignItems: 'center'
};

const slidingTrack = {
  display: 'flex',
  whiteSpace: 'nowrap',
  animation: 'scroll 35s linear infinite'
};

