import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { Search, ShoppingCart, Menu, User, X } from 'lucide-react';

const Navbar = ({ cartCount, openSidebar, categories }) => {
  const [searchInput, setSearchInput] = useState('');
  const navigate = useNavigate();

  const handleSearch = (e) => {
    e.preventDefault();
    if (searchInput.trim()) {
      // Navigate to a search results page or category page with a query
      navigate(`/category/search?q=${searchInput}`);
    }
  };

  return (
    <nav style={navContainer}>
      {/* Top Row: Logo, Search, Account */}
      <div style={topRow}>
        <div style={logoSection}>
          <Menu size={25} onClick={openSidebar} style={{ cursor: 'pointer' }} />
          <Link to="/" style={{ textDecoration: 'none', color: 'white' }}>
            <h1 style={logoText}>MegaMart</h1>
          </Link>
        </div>

        <form onSubmit={handleSearch} style={searchBarContainer}>
          <input 
            type="text" 
            placeholder="Search for electronics, laptops, watches..." 
            style={searchInputStyle}
            value={searchInput}
            onChange={(e) => setSearchInput(e.target.value)}
          />
          <button type="submit" style={searchBtn}>
            <Search size={20} color="#333" />
          </button>
        </form>

        <div style={navLinks}>
          <Link to="/profile" style={navItem}>
            <User size={20} />
            <div style={hideMobile}>
              <span style={navTinyText}>Hello, Sign in</span>
              <span style={navBoldText}>Account</span>
            </div>
          </Link>
          
          <div style={navItem} onClick={() => navigate('/checkout')}>
            <div style={cartIconContainer}>
              <ShoppingCart size={26} />
              <span style={cartBadge}>{cartCount}</span>
            </div>
            <span style={{ ...navBoldText, marginTop: '10px' }}>Cart</span>
          </div>
        </div>
      </div>

      {/* Bottom Row: Category Quick Links */}
      <div style={bottomRow}>
        <div onClick={openSidebar} style={allBtn}>
          <Menu size={18} /> <b>All</b>
        </div>
        <div style={categoryLinks}>
          {categories.slice(0, 6).map((cat) => (
            <Link key={cat} to={`/category/${cat}`} style={catLink}>
              {cat}
            </Link>
          ))}
        </div>
      </div>
    </nav>
  );
};

// --- Professional Styling ---
const navContainer = { display: 'flex', flexDirection: 'column', backgroundColor: '#131921', color: 'white', position: 'sticky', top: 0, zIndex: 100 };
const topRow = { display: 'flex', alignItems: 'center', padding: '10px 20px', gap: '20px', justifyContent: 'space-between' };
const logoSection = { display: 'flex', alignItems: 'center', gap: '15px' };
const logoText = { fontSize: '22px', fontWeight: 'bold', margin: 0, letterSpacing: '1px' };

const searchBarContainer = { flex: 1, display: 'flex', maxWidth: '700px', height: '40px' };
const searchInputStyle = { flex: 1, padding: '0 15px', borderRadius: '4px 0 0 4px', border: 'none', outline: 'none', fontSize: '15px' };
const searchBtn = { backgroundColor: '#febd69', border: 'none', width: '45px', borderRadius: '0 4px 4px 0', cursor: 'pointer', display: 'flex', justifyContent: 'center', alignItems: 'center' };

const navLinks = { display: 'flex', gap: '25px', alignItems: 'center' };
const navItem = { display: 'flex', alignItems: 'center', gap: '8px', cursor: 'pointer', textDecoration: 'none', color: 'white' };
const navTinyText = { fontSize: '11px', display: 'block', opacity: 0.8 };
const navBoldText = { fontSize: '14px', fontWeight: 'bold', display: 'block' };
const hideMobile = { '@media (max-width: 768px)': { display: 'none' } };

const cartIconContainer = { position: 'relative' };
const cartBadge = { position: 'absolute', top: '-8px', right: '-8px', backgroundColor: '#f08804', color: '#131921', borderRadius: '50%', padding: '2px 6px', fontSize: '12px', fontWeight: 'bold' };

const bottomRow = { display: 'flex', alignItems: 'center', gap: '20px', padding: '8px 20px', backgroundColor: '#232f3e', fontSize: '14px' };
const allBtn = { display: 'flex', alignItems: 'center', gap: '5px', cursor: 'pointer' };
const categoryLinks = { display: 'flex', gap: '15px', overflowX: 'auto' };
const catLink = { color: 'white', textDecoration: 'none', textTransform: 'capitalize', whiteSpace: 'nowrap' };

export default Navbar;
