import React, { useEffect, useState } from 'react';
import { supabase } from '../supabaseClient';

const HomePage = ({ addToCart }) => {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    const fetchProducts = async () => {
      const { data } = await supabase.from('products').select('*');
      setProducts(data || []);
    };
    fetchProducts();
  }, []);

  return (
    <div style={{ backgroundColor: '#fcfcfc', minHeight: '100vh', paddingBottom: '50px' }}>
      {/* Hero Section */}
      <div style={heroSection}>
        <h2 style={{ fontSize: '42px', margin: 0 }}>Elevate Your Lifestyle</h2>
        <p style={{ fontSize: '18px', opacity: 0.8 }}>Discover Rwanda's finest selection of premium tech & electronics.</p>
      </div>

      <div style={{ padding: '0 60px', marginTop: '-40px' }}>
        <div style={productGrid}>
          {products.map((item) => (
            <div key={item.id} style={productCard}>
              <div style={imageContainer}>
                <img src={item.image_url} alt={item.name} style={productImg} />
                <span style={categoryBadge}>{item.category}</span>
              </div>
              <div style={{ padding: '20px' }}>
                <h4 style={productTitle}>{item.name}</h4>
                <div style={priceRow}>
                  <span style={priceText}>RWF {item.price.toLocaleString()}</span>
                  <button onClick={() => addToCart(item)} style={addBtn}>+</button>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

// --- Styles ---
const heroSection = { backgroundColor: '#1a1a1a', height: '300px', display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center', color: 'white', textAlign: 'center' };
const productGrid = { display: 'grid', gridTemplateColumns: 'repeat(auto-fill, minmax(280px, 1fr))', gap: '30px' };
const productCard = { backgroundColor: 'white', borderRadius: '24px', overflow: 'hidden', border: '1px solid #f0f0f0', boxShadow: '0 4px 15px rgba(0,0,0,0.03)', transition: 'transform 0.2s' };
const imageContainer = { position: 'relative', height: '220px', backgroundColor: '#f9f9f9', display: 'flex', alignItems: 'center', justifyContent: 'center' };
const productImg = { maxWidth: '75%', maxHeight: '75%', objectFit: 'contain' };
const categoryBadge = { position: 'absolute', top: '15px', left: '15px', backgroundColor: 'white', padding: '5px 12px', borderRadius: '10px', fontSize: '11px', fontWeight: '800', color: '#008ECC', boxShadow: '0 2px 5px rgba(0,0,0,0.05)' };
const productTitle = { fontSize: '17px', margin: '0 0 15px 0', color: '#333', fontWeight: '700' };
const priceRow = { display: 'flex', justifyContent: 'space-between', alignItems: 'center' };
const priceText = { fontSize: '20px', fontWeight: '800', color: '#008ECC' };
const addBtn = { backgroundColor: '#008ECC', color: 'white', border: 'none', width: '40px', height: '40px', borderRadius: '12px', cursor: 'pointer', fontSize: '22px' };

export default HomePage;
