import React, { useState } from 'react';
import { Mail, Phone, MapPin, Send, MessageSquare } from 'lucide-react';

const BRAND_BLUE = '#008ECC';

const ContactUs = () => {
  const [formData, setFormData] = useState({ name: '', email: '', subject: '', message: '' });
  const [status, setStatus] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    setStatus('Sending...');
    
    // Placeholder for Supabase logic:
    // const { error } = await supabase.from('messages').insert([formData]);
    
    console.log('Form Submitted:', formData);
    setTimeout(() => {
      setStatus('Message sent successfully! We will contact you soon.');
      setFormData({ name: '', email: '', subject: '', message: '' });
    }, 1500);
  };

  return (
    <div style={container}>
      <div style={headerSection}>
        <h1 style={mainTitle}>Contact Our Team</h1>
        <p style={subtitle}>Have questions about a product or an order? We are here to help.</p>
      </div>

      <div style={contentGrid}>
        {/* Left: Contact Information */}
        <div style={infoCard}>
          <h2 style={cardTitle}>Get in Touch</h2>
          <div style={infoItem}>
            <div style={iconCircle}><MapPin size={20} color={BRAND_BLUE} /></div>
            <div>
              <p style={infoLabel}>Our Location</p>
              <p style={infoValue}>Kigali Heights, 3rd Floor<br />Kigali, Rwanda</p>
            </div>
          </div>

          <div style={infoItem}>
            <div style={iconCircle}><Phone size={20} color={BRAND_BLUE} /></div>
            <div>
              <p style={infoLabel}>Phone Number</p>
              <p style={infoValue}>+250 788 000 000</p>
            </div>
          </div>

          <div style={infoItem}>
            <div style={iconCircle}><Mail size={20} color={BRAND_BLUE} /></div>
            <div>
              <p style={infoLabel}>Email Address</p>
              <p style={infoValue}>support@megamart.rw</p>
            </div>
          </div>

          <div style={chatBox}>
            <MessageSquare size={24} color="#fff" />
            <div>
              <p style={{ margin: 0, fontWeight: '700' }}>Live Chat</p>
              <p style={{ margin: 0, fontSize: '12px', opacity: 0.9 }}>Available Mon-Sat, 8am - 6pm</p>
            </div>
          </div>
        </div>

        {/* Right: Contact Form */}
        <div style={formCard}>
          <form onSubmit={handleSubmit}>
            <div style={inputRow}>
              <div style={inputGroup}>
                <label style={label}>Your Name</label>
                <input 
                  type="text" 
                  required 
                  style={input} 
                  value={formData.name}
                  onChange={(e) => setFormData({...formData, name: e.target.value})}
                />
              </div>
              <div style={inputGroup}>
                <label style={label}>Email Address</label>
                <input 
                  type="email" 
                  required 
                  style={input} 
                  value={formData.email}
                  onChange={(e) => setFormData({...formData, email: e.target.value})}
                />
              </div>
            </div>

            <div style={inputGroup}>
              <label style={label}>Subject</label>
              <input 
                type="text" 
                required 
                style={input} 
                value={formData.subject}
                onChange={(e) => setFormData({...formData, subject: e.target.value})}
              />
            </div>

            <div style={inputGroup}>
              <label style={label}>Message</label>
              <textarea 
                rows="5" 
                required 
                style={{...input, resize: 'none'}} 
                value={formData.message}
                onChange={(e) => setFormData({...formData, message: e.target.value})}
              ></textarea>
            </div>

            <button type="submit" style={submitBtn}>
              <Send size={18} /> Send Message
            </button>
            {status && <p style={statusText}>{status}</p>}
          </form>
        </div>
      </div>
    </div>
  );
};

// --- STYLES ---
const container = { padding: '40px 8%', backgroundColor: '#f4f7f9', minHeight: '90vh' };
const headerSection = { textAlign: 'center', marginBottom: '50px' };
const mainTitle = { fontSize: '32px', fontWeight: '800', color: '#1a1a1a', margin: '0 0 10px 0' };
const subtitle = { color: '#666', fontSize: '16px' };

const contentGrid = { display: 'grid', gridTemplateColumns: '1fr 1.5fr', gap: '40px' };
const infoCard = { backgroundColor: '#fff', padding: '40px', borderRadius: '12px', boxShadow: '0 4px 20px rgba(0,0,0,0.05)' };
const cardTitle = { fontSize: '22px', fontWeight: '700', marginBottom: '30px' };
const infoItem = { display: 'flex', gap: '20px', marginBottom: '25px' };
const iconCircle = { width: '45px', height: '45px', borderRadius: '50%', backgroundColor: '#f0faff', display: 'flex', alignItems: 'center', justifyContent: 'center', flexShrink: 0 };
const infoLabel = { fontSize: '13px', color: '#888', margin: '0 0 4px 0', fontWeight: '600' };
const infoValue = { fontSize: '15px', color: '#333', margin: 0, fontWeight: '500', lineHeight: '1.5' };

const chatBox = { marginTop: '20px', backgroundColor: BRAND_BLUE, color: '#fff', padding: '20px', borderRadius: '8px', display: 'flex', alignItems: 'center', gap: '15px' };

const formCard = { backgroundColor: '#fff', padding: '40px', borderRadius: '12px', boxShadow: '0 4px 20px rgba(0,0,0,0.05)' };
const inputRow = { display: 'flex', gap: '20px', marginBottom: '20px' };
const inputGroup = { flex: 1, display: 'flex', flexDirection: 'column', marginBottom: '20px' };
const label = { fontSize: '14px', fontWeight: '600', color: '#444', marginBottom: '8px' };
const input = { padding: '12px 15px', borderRadius: '6px', border: '1px solid #ddd', fontSize: '14px', outline: 'none', transition: 'border 0.2s' };
const submitBtn = { backgroundColor: BRAND_BLUE, color: '#fff', border: 'none', padding: '15px 30px', borderRadius: '6px', fontWeight: '700', cursor: 'pointer', display: 'flex', alignItems: 'center', gap: '10px', width: 'fit-content' };
const statusText = { marginTop: '15px', fontSize: '14px', color: '#2e7d32', fontWeight: '500' };

export default ContactUs;
