import React, { useEffect, useState } from 'react';
import { supabase } from '../supabaseClient';

const ProfilePage = ({ user }) => {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchOrders = async () => {
      if (user) {
        const { data, error } = await supabase
          .from('orders')
          .select('*')
          .eq('user_id', user.id)
          .order('created_at', { ascending: false });

        if (!error) setOrders(data);
      }
      setLoading(false);
    };

    fetchOrders();
  }, [user]);

  if (!user) return <div style={{ padding: '50px', textAlign: 'center' }}>Please log in to view your orders.</div>;

  return (
    <div style={{ maxWidth: '800px', margin: '40px auto', padding: '20px', fontFamily: 'sans-serif' }}>
      <h2 style={{ borderBottom: '2px solid #008ECC', paddingBottom: '10px' }}>My Order History</h2>
      
      {loading ? <p>Loading your transactions...</p> : (
        <div style={{ marginTop: '20px' }}>
          {orders.length === 0 ? (
            <p style={{ color: '#888' }}>You haven't made any purchases yet.</p>
          ) : (
            orders.map(order => (
              <div key={order.id} style={orderCardStyle}>
                <div>
                  <span style={{ fontSize: '12px', color: '#888' }}>ORDER ID: {order.id.slice(0, 8)}...</span>
                  <p style={{ margin: '5px 0', fontWeight: 'bold' }}>Payment via Mobile Money</p>
                  <span style={{ fontSize: '13px', color: '#666' }}>{new Date(order.created_at).toLocaleDateString()}</span>
                </div>
                <div style={{ textAlign: 'right' }}>
                  <h3 style={{ color: '#4BB543', margin: 0 }}>RWF {order.amount?.toLocaleString()}</h3>
                  <span style={{ fontSize: '12px', color: '#4BB543', fontWeight: 'bold' }}>‚óè {order.status.toUpperCase()}</span>
                </div>
              </div>
            ))
          )}
        </div>
      )}
    </div>
  );
};

const orderCardStyle = {
  display: 'flex',
  justifyContent: 'space-between',
  alignItems: 'center',
  backgroundColor: 'white',
  padding: '20px',
  borderRadius: '12px',
  marginBottom: '15px',
  boxShadow: '0 2px 8px rgba(0,0,0,0.05)',
  border: '1px solid #eee'
};

export default ProfilePage;
