import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { supabase } from '../supabaseClient'; // Ensure this path is correct
import { 
  ShieldCheck, CheckCircle, Globe, 
  CreditCard, Wallet, Smartphone 
} from 'lucide-react';

const CheckoutPage = ({ cart, setCart, user }) => {
  const navigate = useNavigate();
  const [loading, setLoading] = useState(false);
  const [step, setStep] = useState(1);
  const [selectedCountry, setSelectedCountry] = useState('Rwanda');
  const [currency, setCurrency] = useState({ code: 'RWF', symbol: 'RWF', rate: 1 });
  const [paymentMethod, setPaymentMethod] = useState('card');
  
  // Form State
  const [address, setAddress] = useState('');
  const [firstName, setFirstName] = useState('');
  const [lastName, setLastName] = useState('');

  const subtotal = cart.reduce((acc, item) => acc + (item.price || 0), 0);
  const delivery = selectedCountry === 'Rwanda' ? 2000 : 15000;
  const total = subtotal + delivery;

  const handlePayment = async () => {
    if (!address || !firstName) {
      alert("Please fill in your shipping details.");
      return;
    }

    setLoading(true);

    try {
      const { data, error } = await supabase
        .from('orders')
        .insert([
          {
            user_email: user?.email || 'Guest',
            items: cart,
            total_amount: total * currency.rate,
            currency: currency.code,
            shipping_address: `${firstName} ${lastName}, ${address}, ${selectedCountry}`,
            status: 'paid'
          }
        ]);

      if (error) throw error;

      // Clear cart and show success
      setCart([]);
      setStep(3);
    } catch (error) {
      alert("Error saving order: " + error.message);
    } finally {
      setLoading(false);
    }
  };

  // ... (Keep the formatPrice function and success screen logic from previous code)

  return (
    <div style={pageWrapper} className="checkout-container">
      <div style={mainContent}>
        <div style={sectionCard}>
          <h2 style={sectionHeader}><Globe size={20} /> 1. Shipping Details</h2>
          <div style={formRow} className="mobile-stack">
            <input 
              style={inputHalf} 
              placeholder="First name" 
              onChange={(e) => setFirstName(e.target.value)}
              className="full-width-mobile" 
            />
            <input 
              style={inputHalf} 
              placeholder="Last name" 
              onChange={(e) => setLastName(e.target.value)}
              className="full-width-mobile" 
            />
          </div>
          <input 
            style={inputFull} 
            placeholder="Street address" 
            onChange={(e) => setAddress(e.target.value)}
          />
        </div>

        {/* ... Payment Selection Logic ... */}
      </div>

      <div style={sidebar} className="checkout-sidebar">
        <div style={summaryCard}>
          <button 
            style={confirmBtn} 
            onClick={handlePayment}
            disabled={loading || cart.length === 0}
          >
            {loading ? "Processing..." : `Pay ${currency.symbol} ${(total * currency.rate).toFixed(0)}`}
          </button>
        </div>
      </div>
    </div>
  );
};

// ... (Keep styles from previous code)
export default CheckoutPage;
