import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { ShieldCheck, ChevronDown, CheckCircle, Star, Globe, CreditCard, Wallet } from 'lucide-react';

const CheckoutPage = ({ cart, setCart, user }) => {
  const navigate = useNavigate();
  const [step, setStep] = useState(1);
  const [rating, setRating] = useState(0);
  const [selectedCountry, setSelectedCountry] = useState('Rwanda');
  
  // NEW: Currency and Payment State
  const [currency, setCurrency] = useState({ code: 'RWF', symbol: 'RWF', rate: 1 });
  const [paymentMethod, setPaymentMethod] = useState('card');

  const currencies = [
    { code: 'RWF', symbol: 'RWF', rate: 1 },
    { code: 'USD', symbol: '$', rate: 0.00078 }, // Example rates
    { code: 'EUR', symbol: '€', rate: 0.00072 },
    { code: 'GBP', symbol: '£', rate: 0.00062 }
  ];

  const subtotal = cart.reduce((acc, item) => acc + (item.price || 0), 0);
  const deliveryBase = selectedCountry === 'Rwanda' ? 2000 : 15000;

  // Function to format price based on selected currency
  const formatPrice = (amount) => {
    const converted = (amount * currency.rate).toLocaleString(undefined, {
      minimumFractionDigits: currency.code === 'RWF' ? 0 : 2,
      maximumFractionDigits: currency.code === 'RWF' ? 0 : 2
    });
    return `${currency.symbol} ${converted}`;
  };

  if (step === 3) {
    return (
      <div style={successContainer}>
        <CheckCircle size={80} color="#28a745" />
        <h1>Payment Successful!</h1>
        <p>Your order is being prepared for shipping to {selectedCountry}.</p>
        <button onClick={() => navigate('/')} style={continueBtn}>Return to Shop</button>
      </div>
    );
  }

  return (
    <div style={pageWrapper}>
      <div style={mainContent}>
        {/* SHIPPING SECTION */}
        <div style={sectionCard}>
          <h2 style={sectionHeader}><Globe size={24} style={{marginRight: '10px'}}/> 1. Shipping Information</h2>
          <div style={formRow}>
            <div style={inputGroupFull}>
              <label style={labelStyle}>Ship to Country</label>
              <select style={selectInput} value={selectedCountry} onChange={(e) => setSelectedCountry(e.target.value)}>
                <option>Rwanda</option><option>United States</option><option>United Kingdom</option><option>Canada</option>
              </select>
            </div>
          </div>
          <div style={formRow}>
            <input style={inputHalf} placeholder="First name" />
            <input style={inputHalf} placeholder="Last name" />
          </div>
          <input style={inputFull} placeholder="Street address" />
        </div>

        {/* PAYMENT & CURRENCY SECTION */}
        <div style={sectionCard}>
          <h2 style={sectionHeader}><CreditCard size={24} style={{marginRight: '10px'}}/> 2. Payment & Currency</h2>
          
          {/* Currency Selector */}
          <div style={{marginBottom: '20px'}}>
            <label style={labelStyle}>Select Currency</label>
            <div style={currencyGrid}>
              {currencies.map(c => (
                <button 
                  key={c.code} 
                  onClick={() => setCurrency(c)}
                  style={currency.code === c.code ? activeCurrencyBtn : currencyBtn}
                >
                  {c.code} ({c.symbol})
                </button>
              ))}
            </div>
          </div>

          {/* Payment Methods */}
          <label style={labelStyle}>Pay with</label>
          <div style={paymentGrid}>
            <div style={paymentMethod === 'card' ? activePayment : paymentOption} onClick={() => setPaymentMethod('card')}>
              <CreditCard size={20} />
              <span>Visa / MasterCard</span>
            </div>
            <div style={paymentMethod === 'paypal' ? activePayment : paymentOption} onClick={() => setPaymentMethod('paypal')}>
              <Wallet size={20} />
              <span>PayPal</span>
            </div>
            {selectedCountry === 'Rwanda' && (
              <div style={paymentMethod === 'momo' ? activePayment : paymentOption} onClick={() => setPaymentMethod('momo')}>
                <Smartphone size={20} />
                <span>Mobile Money</span>
              </div>
            )}
          </div>
        </div>
      </div>

      {/* ORDER SUMMARY SIDEBAR */}
      <div style={sidebar}>
        <div style={summaryCard}>
          <h2 style={summaryTitle}>Order Summary</h2>
          <div style={summaryRow}>
            <span>Items ({cart.length})</span>
            <span>{formatPrice(subtotal)}</span>
          </div>
          <div style={summaryRow}>
            <span>Shipping</span>
            <span>{formatPrice(deliveryBase)}</span>
          </div>
          <hr style={divider} />
          <div style={totalRow}>
            <span>Order total</span>
            <span>{formatPrice(subtotal + deliveryBase)}</span>
          </div>
          
          <button style={confirmBtn} onClick={() => setStep(3)}>
            Confirm and pay
          </button>
          
          <div style={protectionBox}>
            <ShieldCheck size={20} color="#0053a0" />
            <span>Purchase protected by <b>MegaMart Money Back Guarantee</b></span>
          </div>
        </div>
      </div>
    </div>
  );
};

// --- ADDITIONAL STYLES ---
const currencyGrid = { display: 'flex', gap: '10px', marginTop: '10px' };
