import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { supabase } from '../supabaseClient';
import { 
  ShieldCheck, CheckCircle, Globe, CreditCard, 
  Smartphone, Tag, ChevronRight, Lock 
} from 'lucide-react';

const CheckoutPage = ({ cart, setCart, user }) => {
  const navigate = useNavigate();
  const [loading, setLoading] = useState(false);
  const [selectedCountry, setSelectedCountry] = useState('Rwanda');
  const [currency, setCurrency] = useState({ code: 'RWF', symbol: 'RWF', rate: 1 });
  
  // Coupon States
  const [couponCode, setCouponCode] = useState('');
  const [appliedCoupon, setAppliedCoupon] = useState(null);
  
  const [formData, setFormData] = useState({
    email: user?.email || '',
    firstName: '',
    lastName: '',
    address: '',
    city: 'Kigali'
  });

  const subtotal = cart.reduce((acc, item) => acc + (item.price || 0), 0);
  const discount = appliedCoupon ? (subtotal * appliedCoupon.discount_percent) / 100 : 0;
  const delivery = selectedCountry === 'Rwanda' ? 2000 : 15000;
  const total = (subtotal - discount) + delivery;

  const handlePayment = async () => {
    setLoading(true);
    // ... (Your existing Supabase insert logic remains the same)
    setLoading(false);
    navigate('/success');
  };

  return (
    <div style={pageBackground}>
      <div style={checkoutCard}>
        {/* LEFT SIDE: FORM */}
        <div style={formSection}>
          <div style={header}>
            <h2 style={brandText}>MegaMart</h2>
            <div style={stepIndicator}>
              <span style={activeStep}>1. Shipping</span>
              <ChevronRight size={14} />
              <span>2. Delivery</span>
              <ChevronRight size={14} />
              <span>3. Payment</span>
            </div>
          </div>

          <div style={inputGroup}>
            <input style={fullInput} placeholder="Email address" value={formData.email} disabled />
            <div style={row}>
              <input style={halfInput} placeholder="First name" onChange={e => setFormData({...formData, firstName: e.target.value})}/>
              <input style={halfInput} placeholder="Last name" onChange={e => setFormData({...formData, lastName: e.target.value})}/>
            </div>
            <input style={fullInput} placeholder="Street address" onChange={e => setFormData({...formData, address: e.target.value})}/>
            <div style={row}>
              <input style={halfInput} placeholder="City" value={formData.city}/>
              <select style={halfInput} value={selectedCountry} onChange={e => setSelectedCountry(e.target.value)}>
                <option value="Rwanda">Rwanda ðŸ‡·ðŸ‡¼</option>
                <option value="USA">USA ðŸ‡ºðŸ‡¸</option>
              </select>
            </div>
          </div>

          <button style={continueBtn} onClick={handlePayment}>
            {loading ? "Processing..." : "Continue to Payment"}
          </button>
          
          <p style={secureText}><Lock size={12} /> Powered by MegaMart Secure</p>
        </div>

        {/* RIGHT SIDE: SUMMARY */}
        <div style={summarySection}>
          <h3 style={summaryTitle}>Order Summary</h3>
          <div style={itemList}>
            {cart.map((item, i) => (
              <div key={i} style={itemRow}>
                <div style={itemThumb}>{item.name[0]}</div>
                <div style={itemInfo}>
                  <p style={itemName}>{item.name}</p>
                  <p style={itemQty}>Qty: 1</p>
                </div>
                <span style={itemPrice}>RWF {item.price?.toLocaleString()}</span>
              </div>
            ))}
          </div>

          <div style={divider} />
          
          <div style={promoLink} onClick={() => { /* Toggle coupon input */ }}>
            <Tag size={14} /> + Discount Code
          </div>

          <div style={calcRows}>
            <div style={calcRow}><span>Subtotal</span><span>RWF {subtotal.toLocaleString()}</span></div>
            <div style={calcRow}><span>Delivery</span><span>RWF {delivery.toLocaleString()}</span></div>
            {appliedCoupon && <div style={calcRow}><span>Discount</span><span style={{color:'#28a745'}}>-{appliedCoupon.discount_percent}%</span></div>}
            <div style={totalRow}><span>Total</span><span>RWF {total.toLocaleString()}</span></div>
          </div>
        </div>
      </div>
    </div>
  );
};

// --- MODERN STYLES ---
const pageBackground = {
  backgroundColor: '#0a0a0a', // Dark background like the reference
  minHeight: '100vh',
  display: 'flex',
  alignItems: 'center',
  justifyContent: 'center',
  padding: '20px',
  fontFamily: 'Inter, sans-serif'
};

const checkoutCard = {
  backgroundColor: 'white',
  width: '100%',
  maxWidth: '900px',
  borderRadius: '16px',
  display: 'flex',
  overflow: 'hidden',
  boxShadow: '0 20px 40px rgba(0,0,0,0.4)',
  flexWrap: 'wrap'
};

const formSection = { flex: 1.2, padding: '40px', minWidth: '350px' };
const summarySection = { flex: 0.8, backgroundColor: '#f8f9fa', padding: '40px', borderLeft: '1px solid #eee', minWidth: '300px' };

const brandText = { fontSize: '24px', fontWeight: '900', color: '#008ECC', margin: 0, fontStyle: 'italic' };
const stepIndicator = { display: 'flex', alignItems: 'center', gap: '8px', fontSize: '12px', color: '#888', marginTop: '10px' };
const activeStep = { color: '#000', fontWeight: 'bold', borderBottom: '2px solid #000' };

const inputGroup = { marginTop: '30px', display: 'flex', flexDirection: 'column', gap: '12px' };
const fullInput = { padding: '14px', borderRadius: '8px', border: '1px solid #ddd', fontSize: '14px' };
const row = { display: 'flex', gap: '12px' };
const halfInput = { ...fullInput, flex: 1 };

const continueBtn = {
  width: '100%', padding: '18px', backgroundColor: '#1a1a1a', color: 'white', 
  border: 'none', borderRadius: '12px', fontWeight: 'bold', fontSize: '16px', 
  marginTop: '30px', cursor: 'pointer', transition: '0.2s'
};

const itemRow = { display: 'flex', gap: '12px', marginBottom: '15px', alignItems: 'center' };
const itemThumb = { width: '50px', height: '50px', backgroundColor: '#eee', borderRadius: '8px', display: 'flex', alignItems: 'center', justifyContent: 'center', fontWeight: 'bold' };
const itemInfo = { flex: 1 };
const itemName = { margin: 0, fontSize: '13px', fontWeight: '600' };
const itemQty = { margin: 0, fontSize: '11px', color: '#888' };
const itemPrice = { fontSize: '13px', fontWeight: '600' };

const divider = { height: '1px', backgroundColor: '#eee', margin: '20px 0' };
const calcRows = { display: 'flex', flexDirection: 'column', gap: '8px' };
const calcRow = { display: 'flex', justifyContent: 'space-between', fontSize: '13px', color: '#666' };
const totalRow = { ...calcRow, fontWeight: 'bold', fontSize: '18px', color: '#000', marginTop: '10px' };
const secureText = { textAlign: 'center', fontSize: '11px', color: '#aaa', marginTop: '15px', display: 'flex', alignItems: 'center', justifyContent: 'center', gap: '4px' };

export default CheckoutPage;
