import React from 'react';
import { FileText, AlertCircle, ShoppingBag, RotateCcw } from 'lucide-react';

const TermsOfService = () => {
  const BRAND_BLUE = '#008ECC';

  return (
    <div style={container}>
      <section style={header}>
        <h1 style={title}>Terms of Service</h1>
        <p style={subtitle}>The agreement between you and MegaMart Rwanda.</p>
      </section>

      <div style={content}>
        <section style={section}>
          <div style={iconHeader}><ShoppingBag color={BRAND_BLUE} /> <h2>1. Orders & Pricing</h2></div>
          <p style={text}>
            By placing an order, you agree to provide accurate information. MegaMart reserves the right 
            to cancel orders if products are listed at an incorrect price due to technical errors.
          </p>
        </section>

        <section style={section}>
          <div style={iconHeader}><RotateCcw color={BRAND_BLUE} /> <h2>2. Returns & Refunds</h2></div>
          <p style={text}>
            We offer a 7-day return policy for defective electronics. Items must be in their original 
            packaging. Refunds for MoMo payments are processed back to the original phone number used.
          </p>
        </section>

        <section style={section}>
          <div style={iconHeader}><AlertCircle color={BRAND_BLUE} /> <h2>3. Delivery Liability</h2></div>
          <p style={text}>
            We aim for same-day delivery in Kigali. However, we are not liable for delays caused by 
            incorrect addresses provided by the customer or extreme weather conditions.
          </p>
        </section>
      </div>
    </div>
  );
};

// Reuse styles from PrivacyPolicy or import a shared CSS file
const container = { backgroundColor: '#fff', minHeight: '100vh', paddingBottom: '60px' };
const header = { backgroundColor: '#008ECC', color: '#fff', padding: '60px 20px', textAlign: 'center' };
const title = { fontSize: '32px', margin: '0 0 10px 0' };
const subtitle = { opacity: 0.9 };
const content = { maxWidth: '800px', margin: '0 auto', padding: '40px 20px' };
const section = { marginBottom: '40px' };
const iconHeader = { display: 'flex', alignItems: 'center', gap: '10px', marginBottom: '15px' };
const text = { color: '#555', lineHeight: '1.7' };

export default TermsOfService;
