import React from 'react';
import { Link } from 'react-router-dom';
import { X, User, ChevronRight, LogIn, ShoppingBag, Settings, HelpCircle } from 'lucide-react';

const Sidebar = ({ isOpen, onClose, user, categories }) => {
  return (
    <>
      {/* 1. BLUR OVERLAY - Standard for pro websites like Amazon/Kikuu */}
      {isOpen && (
        <div 
          onClick={onClose} 
          style={overlayStyle} 
        />
      )}

      {/* 2. SIDEBAR PANEL */}
      <div style={{ 
        ...sidebarPanel, 
        left: isOpen ? '0' : '-350px' // Slides in from left
      }}>
        
        {/* HEADER: User Profile section */}
        <div style={sidebarHeader}>
          <div style={userProfileInfo}>
            <div style={avatarCircle}>
              <User size={24} color="#232f3e" />
            </div>
            <span style={greetingText}>
              {user ? `Hello, ${user.email.split('@')[0]}` : 'Hello, Sign In'}
            </span>
          </div>
          <X 
            size={24} 
            onClick={onClose} 
            style={{ cursor: 'pointer', opacity: 0.8 }} 
          />
        </div>

        {/* CONTENT AREA */}
        <div style={sidebarScrollArea}>
          
          {/* DYNAMIC CATEGORIES SECTION */}
          <div style={sectionWrapper}>
            <h3 style={sectionTitle}>Shop By Category</h3>
            <ul style={listContainer}>
              {categories && categories.length > 0 ? (
                categories.map((cat) => (
                  <li key={cat} style={listItem}>
                    <Link to={`/category/${cat}`} onClick={onClose} style={navLink}>
                      <span style={{ textTransform: 'capitalize' }}>{cat}</span>
                      <ChevronRight size={18} color="#ccc" />
                    </Link>
                  </li>
                ))
              ) : (
                <li style={loadingText}>Updating categories...</li>
              )}
            </ul>
          </div>

          <div style={divider} />

          {/* ACCOUNT & SETTINGS SECTION */}
          <div style={sectionWrapper}>
            <h3 style={sectionTitle}>Account & Settings</h3>
            <ul style={listContainer}>
              <li style={listItem}>
                <Link to="/profile" onClick={onClose} style={navLink}>
                  <div style={iconLabelGroup}><Settings size={18} /> <span>Your Account</span></div>
                </Link>
              </li>
              <li style={listItem}>
                <Link to="/orders" onClick={onClose} style={navLink}>
                  <div style={iconLabelGroup}><ShoppingBag size={18} /> <span>Orders</span></div>
                </Link>
              </li>
              {!user ? (
                <li style={listItem}>
                  <Link to="/login" onClick={onClose} style={navLink}>
                    <div style={iconLabelGroup}><LogIn size={18} /> <span>Sign In</span></div>
                  </Link>
                </li>
              ) : (
                <li style={listItem}>
                  <button onClick={onClose} style={logoutActionBtn}>Sign Out</button>
                </li>
              )}
            </ul>
          </div>

          <div style={divider} />

          <div style={sectionWrapper}>
            <ul style={listContainer}>
              <li style={listItem}>
                <Link to="/help" onClick={onClose} style={navLink}>
                  <div style={iconLabelGroup}><HelpCircle size={18} /> <span>Help Center</span></div>
                </Link>
              </li>
            </ul>
          </div>
        </div>
      </div>
    </>
  );
};

// --- STYLES (Professional UI Design) ---

const overlayStyle = {
  position: 'fixed',
  top: 0,
  left: 0,
  width: '100%',
  height: '100%',
  backgroundColor: 'rgba(0, 0, 0, 0.7)',
  backdropFilter: 'blur(2px)', // Adds that modern blur effect
  zIndex: 1000,
  transition: 'opacity 0.3s ease'
};

const sidebarPanel = {
  position: 'fixed',
  top: 0,
  width: '320px',
  height: '100%',
  backgroundColor: '#fff',
  zIndex: 1001,
  transition: '0.4s cubic-bezier(0.4, 0, 0.2, 1)', // Smooth slide transition
  display: 'flex',
  flexDirection: 'column',
  boxShadow: '10px 0 25px rgba(0,0,0,0.1)'
};

const sidebarHeader = {
  backgroundColor: '#232f3e',
  color: 'white',
  padding: '20px',
  display: 'flex',
  alignItems: 'center',
  justifyContent: 'space-between',
};

const userProfileInfo = {
  display: 'flex',
  alignItems: 'center',
  gap: '12px'
};

const avatarCircle = {
  width: '35px',
  height: '35px',
  borderRadius: '50%',
  backgroundColor: '#fff',
  display: 'flex',
  justifyContent: 'center',
  alignItems: 'center'
};

const greetingText = {
  fontSize: '18px',
  fontWeight: 'bold',
  letterSpacing: '0.5px'
};

const sidebarScrollArea = {
  flex: 1,
  overflowY: 'auto',
  paddingBottom: '40px'
};

const sectionWrapper = {
  padding: '10px 0'
};

const sectionTitle = {
  fontSize: '17px',
  fontWeight: 'bold',
  padding: '15px 25px 5px',
  color: '#111',
  margin: 0
};

const listContainer = {
  listStyle: 'none',
  padding: 0,
  margin: 0
};

const listItem = {
  borderBottom: '1px solid #f3f3f3'
};

const navLink = {
  display: 'flex',
  justifyContent: 'space-between',
  alignItems: 'center',
  padding: '14px 25px',
  textDecoration: 'none',
  color: '#444',
  fontSize: '15px',
  transition: 'background 0.2s'
};

const iconLabelGroup = {
  display: 'flex',
  alignItems: 'center',
  gap: '12px'
};

const divider = {
  height: '5px',
  backgroundColor: '#f6f6f6',
  borderTop: '1px solid #eee'
};

const loadingText = {
  padding: '20px 25px',
  color: '#888',
  fontSize: '14px'
};

const logoutActionBtn = {
  width: '100%',
  textAlign: 'left',
  padding: '14px 25px',
  background: 'none',
  border: 'none',
  color: '#d9534f',
  fontSize: '15px',
  cursor: 'pointer',
  fontWeight: '600'
};

export default Sidebar;
