import React from 'react';
import ProductSection from '../components/ProductionSection';
import { Smartphone, Laptop, Watch, Headphones, Gamepad, LayoutGrid } from 'lucide-react';
import { Link } from 'react-router-dom';

const HomePage = ({ addToCart }) => {
  // Map icons to your category names for a pro look
  const categoryIcons = [
    { name: 'smartphones', icon: <Smartphone size={32} color="#008ecc" /> },
    { name: 'laptops', icon: <Laptop size={32} color="#008ecc" /> },
    { name: 'watches', icon: <Watch size={32} color="#008ecc" /> },
    { name: 'audio', icon: <Headphones size={32} color="#008ecc" /> },
    { name: 'gaming', icon: <Gamepad size={32} color="#008ecc" /> },
    { name: 'accessories', icon: <LayoutGrid size={32} color="#008ecc" /> },
  ];

  return (
    <div style={{ backgroundColor: '#f8f9fa', paddingBottom: '40px' }}>
      
      {/* 1. HERO BANNER - Professional Kikuu Style */}
      <div style={heroContainer}>
        <div style={heroContent}>
          <span style={badge}>Limited Time Offer</span>
          <h1 style={heroTitle}>Latest Galaxy S24 Ultra</h1>
          <p style={heroSub}>Get up to 15% discount on all pre-orders this week at MegaMart Rwanda.</p>
          <button style={heroBtn}>Shop Now</button>
        </div>
        <div style={heroImageContainer}>
          <img 
            src="https://images.unsplash.com/photo-1610945265064-0e34e5519bbf?auto=format&fit=crop&w=800&q=80" 
            alt="Galaxy S24" 
            style={heroImage} 
          />
        </div>
      </div>

      {/* 2. ICON CATEGORY GRID - Matching your screenshot */}
      <div style={iconGrid}>
        {categoryIcons.map((item) => (
          <Link to={`/category/${item.name}`} key={item.name} style={iconCard}>
            <div style={iconCircle}>{item.icon}</div>
            <span style={iconLabel}>{item.name}</span>
          </Link>
        ))}
      </div>

      {/* 3. DYNAMIC PRODUCT SECTIONS */}
      <div style={{ maxWidth: '1200px', margin: '0 auto', padding: '0 15px' }}>
        
        {/* Today's Deals Section */}
        <ProductSection 
          title="Today's Deals" 
          filterType="deal_type" 
          filterValue="todays_deal" 
          addToCart={addToCart} 
        />

        {/* Dynamic Category Section (e.g., Electronics) */}
        <ProductSection 
          title="Best in Electronics" 
          filterType="category" 
          filterValue="electronics" 
          addToCart={addToCart} 
        />

        {/* Flash Sale Section */}
        <ProductSection 
          title="Flash Sales" 
          filterType="deal_type" 
          filterValue="flash_sale" 
          addToCart={addToCart} 
        />
      </div>
    </div>
  );
};

// --- PRO STYLING ---

const heroContainer = {
  backgroundColor: '#1c2939',
  color: 'white',
  margin: '20px auto',
  maxWidth: '1200px',
  borderRadius: '15px',
  display: 'flex',
  alignItems: 'center',
  padding: '40px',
  overflow: 'hidden',
  height: '350px'
};

const heroContent = { flex: 1, paddingRight: '20px' };
const badge = { backgroundColor: '#ff4d4f', padding: '5px 12px', borderRadius: '20px', fontSize: '12px' };
const heroTitle = { fontSize: '42px', margin: '20px 0 10px' };
const heroSub = { fontSize: '18px', opacity: 0.8, marginBottom: '30px' };
const heroBtn = { padding: '12px 30px', borderRadius: '8px', border: 'none', fontWeight: 'bold', cursor: 'pointer' };

const heroImageContainer = { flex: 1, display: 'flex', justifyContent: 'center' };
const heroImage = { width: '100%', borderRadius: '10px', objectFit: 'cover' };

const iconGrid = {
  display: 'flex',
  justifyContent: 'center',
  gap: '30px',
  padding: '40px 20px',
  flexWrap: 'wrap'
};

const iconCard = {
  display: 'flex',
  flexDirection: 'column',
  alignItems: 'center',
  textDecoration: 'none',
  color: '#333'
};

const iconCircle = {
  width: '70px',
  height: '70px',
  borderRadius: '20px',
  backgroundColor: '#fff',
  display: 'flex',
  justifyContent: 'center',
  alignItems: 'center',
  boxShadow: '0 4px 12px rgba(0,0,0,0.05)',
  marginBottom: '10px',
  transition: '0.3s'
};

const iconLabel = { fontSize: '14px', fontWeight: '600', textTransform: 'capitalize' };

export default HomePage;
