import React, { useEffect, useState } from 'react';
import { supabase } from '../supabaseClient';

const HomePage = ({ addToCart }) => {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    const fetchProducts = async () => {
      const { data } = await supabase.from('products').select('*');
      setProducts(data || []);
    };
    fetchProducts();
  }, []);

  return (
    <div style={{ backgroundColor: '#eaeded', minHeight: '100vh' }}>
      {/* Hero Banner Section */}
      <div style={heroWrapper}>
        <img 
          src="https://m.media-amazon.com/images/I/61lwJy4B8PL._SX3000_.jpg" 
          alt="Banner" 
          style={heroImg} 
        />
        
        {/* Floating Cards Grid */}
        <div style={cardGrid}>
          {products.map((item) => (
            <div key={item.id} style={amazonCard}>
              <h3 style={{ fontSize: '21px', margin: '0 0 10px 0' }}>{item.name}</h3>
              <div style={imgBox}>
                <img src={item.image_url} alt={item.name} style={{ maxHeight: '100%', maxWidth: '100%' }} />
              </div>
              <p style={{ fontWeight: 'bold', fontSize: '18px', margin: '10px 0' }}>RWF {item.price.toLocaleString()}</p>
              <button 
                onClick={() => addToCart(item)} 
                style={amazonBtn}
              >
                Add to Cart
              </button>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

const heroWrapper = { position: 'relative', width: '100%' };
const heroImg = { width: '100%', maskImage: 'linear-gradient(to bottom, rgba(0,0,0,1) 70%, rgba(0,0,0,0) 100%)' };
const cardGrid = { 
  display: 'grid', 
  gridTemplateColumns: 'repeat(auto-fill, minmax(300px, 1fr))', 
  gap: '20px', 
  padding: '20px',
  marginTop: '-300px', // Pulls the cards up over the image
  position: 'relative',
  zIndex: 10
};
const amazonCard = { backgroundColor: 'white', padding: '20px', display: 'flex', flexDirection: 'column' };
const imgBox = { height: '200px', display: 'flex', alignItems: 'center', justifyContent: 'center', marginBottom: '10px' };
const amazonBtn = { backgroundColor: '#ffd814', border: '1px solid #fcd200', padding: '8px', borderRadius: '20px', cursor: 'pointer', fontWeight: 'bold' };

export default HomePage;
