import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { supabase } from '../supabaseClient';
import { 
  ShieldCheck, CheckCircle, Globe, CreditCard, 
  Smartphone, Tag, ChevronRight, Lock, Mail, User, MapPin
} from 'lucide-react';

const CheckoutPage = ({ cart, setCart, user }) => {
  const navigate = useNavigate();
  const [step, setStep] = useState(1);
  const [loading, setLoading] = useState(false);
  const [selectedCountry, setSelectedCountry] = useState('Rwanda');
  const [currency, setCurrency] = useState({ code: 'RWF', symbol: 'RWF', rate: 1 });
  const [paymentMethod, setPaymentMethod] = useState('card');

  // Coupon States
  const [couponCode, setCouponCode] = useState('');
  const [appliedCoupon, setAppliedCoupon] = useState(null);
  const [couponError, setCouponError] = useState('');
  const [isApplying, setIsApplying] = useState(false);

  const [formData, setFormData] = useState({
    email: user?.email || '',
    firstName: '',
    lastName: '',
    address: '',
    city: 'Kigali'
  });

  const currencies = [
    { code: 'RWF', symbol: 'RWF', rate: 1 },
    { code: 'USD', symbol: '$', rate: 0.00078 },
    { code: 'EUR', symbol: 'â‚¬', rate: 0.00072 }
  ];

  // Calculations
  const subtotal = cart.reduce((acc, item) => acc + (item.price || 0), 0);
  const discountAmount = appliedCoupon ? (subtotal * appliedCoupon.discount_percent) / 100 : 0;
  const delivery = selectedCountry === 'Rwanda' ? 2000 : 15000;
  const totalInRWF = (subtotal - discountAmount) + delivery;

  const formatPrice = (amount) => {
    const converted = (amount * currency.rate).toLocaleString(undefined, {
      minimumFractionDigits: currency.code === 'RWF' ? 0 : 2,
      maximumFractionDigits: currency.code === 'RWF' ? 0 : 2
    });
    return `${currency.symbol} ${converted}`;
  };

  const handleApplyCoupon = async () => {
    if (!couponCode) return;
    setIsApplying(true);
    setCouponError('');
    try {
      const { data, error } = await supabase
        .from('promotions')
        .select('*')
        .eq('code', couponCode.toUpperCase())
        .eq('is_active', true)
        .single();

      if (error || !data) {
        setCouponError('Invalid or expired code');
        setAppliedCoupon(null);
      } else {
        setAppliedCoupon(data);
        setCouponError('');
      }
    } catch (err) {
      setCouponError('Error verifying coupon');
    } finally {
      setIsApplying(false);
    }
  };

  const handlePayment = async () => {
    if (!formData.firstName || !formData.address) {
      alert("Please fill in your details.");
      return;
    }
    setLoading(true);
    try {
      const { error } = await supabase.from('orders').insert([{
        user_email: formData.email || 'Guest',
        items: cart,
        total_amount: totalInRWF,
        currency: currency.code,
        shipping_address: `${formData.firstName} ${formData.lastName}, ${formData.address}, ${formData.city}, ${selectedCountry}`,
        status: 'paid',
        payment_method: paymentMethod,
        coupon_used: appliedCoupon ? appliedCoupon.code : null
      }]);
      if (error) throw error;
      setCart([]);
      setStep(3);
    } catch (e) { alert(e.message); }
    finally { setLoading(false); }
  };

  if (step === 3) {
    return (
      <div style={pageBackground}>
        <div style={successCard}>
          <CheckCircle size={60} color="#28a745" />
          <h1 style={{margin: '20px 0 10px'}}>Order Confirmed!</h1>
          <p style={{color: '#666'}}>Thank you, {formData.firstName}. Your package is on its way.</p>
          <button onClick={() => navigate('/')} style={continueBtn}>Back to Shop</button>
        </div>
      </div>
    );
  }

  return (
    <div style={pageBackground}>
      <div style={checkoutContainer}>
        {/* LEFT: SHIPPING & PAYMENT */}
        <div style={mainPanel}>
          <div style={brandHeader}>
            <h2 style={brandName}>MegaMart</h2>
            <div style={breadcrumb}>
              <span style={activeStep}>Shipping</span>
              <ChevronRight size={14} />
              <span>Payment</span>
            </div>
          </div>

          <div style={formWrapper}>
            <div style={sectionTitle}><Mail size={16}/> Contact Information</div>
            <input style={fullInput} placeholder="Email" value={formData.email} onChange={e => setFormData({...formData, email: e.target.value})} />

            <div style={sectionTitle}><MapPin size={16}/> Shipping Address</div>
            <div style={inputRow}>
              <input style={halfInput} placeholder="First name" onChange={e => setFormData({...formData, firstName: e.target.value})} />
              <input style={halfInput} placeholder="Last name" onChange={e => setFormData({...formData, lastName: e.target.value})} />
            </div>
            <input style={fullInput} placeholder="Address" onChange={e => setFormData({...formData, address: e.target.value})} />
            <div style={inputRow}>
              <input style={halfInput} placeholder="City" value={formData.city} onChange={e => setFormData({...formData, city: e.target.value})} />
              <select style={halfInput} value={selectedCountry} onChange={e => setSelectedCountry(e.target.value)}>
                <option value="Rwanda">Rwanda ðŸ‡·ðŸ‡¼</option>
                <option value="United States">USA ðŸ‡ºðŸ‡¸</option>
              </select>
            </div>

            <div style={sectionTitle}><CreditCard size={16}/> Payment Method</div>
            <div style={paymentOptions}>
              <div style={paymentMethod === 'card' ? activePayment : paymentOption} onClick={() => setPaymentMethod('card')}>
                <CreditCard size={18} /> Card
              </div>
              {selectedCountry === 'Rwanda' && (
                <div style={paymentMethod === 'momo' ? activePayment : paymentOption} onClick={() => setPaymentMethod('momo')}>
                  <Smartphone size={18} /> MoMo
                </div>
              )}
            </div>

            <button style={confirmBtn} onClick={handlePayment} disabled={loading}>
              {loading ? "Processing..." : `Complete Purchase â€¢ ${formatPrice(totalInRWF)}`}
            </button>
            <p style={secureBadge}><Lock size={12} /> Secure 256-bit SSL Encrypted Checkout</p>
          </div>
        </div>

        {/* RIGHT: ORDER SUMMARY */}
        <div style={summaryPanel}>
          <h3 style={{margin: '0 0 20px'}}>Order Summary</h3>
          <div style={cartItemsList}>
            {cart.map((item, i) => (
              <div key={i} style={itemLine}>
                <div style={itemIcon}>{item.name[0]}</div>
                <div style={{flex: 1}}>
                  <div style={itemName}>{item.name}</div>
                  <div style={itemQty}>Quantity: 1</div>
                </div>
                <div style={itemPrice}>RWF {item.price?.toLocaleString()}</div>
              </div>
            ))}
          </div>

          <div style={divider} />

          {/* Coupon logic inside summary */}
          <div style={couponBox}>
            <div style={{display: 'flex', gap: '8px'}}>
              <input 
                style={miniInput} 
                placeholder="Discount code" 
                onChange={e => setCouponCode(e.target.value.toUpperCase())} 
              />
              <button style={miniBtn} onClick={handleApplyCoupon}>Apply</button>
            </div>
            {appliedCoupon && <div style={appliedLabel}><Tag size={12}/> {appliedCoupon.code} (-{appliedCoupon.discount_percent}%)</div>}
          </div>

          <div style={summaryCalculation}>
            <div style={calcLine}><span>Subtotal</span><span>{formatPrice(subtotal)}</span></div>
            <div style={calcLine}><span>Shipping</span><span>{formatPrice(delivery)}</span></div>
            {appliedCoupon && <div style={{...calcLine, color: '#28a745'}}><span>Discount</span><span>-{formatPrice(discountAmount)}</span></div>}
            <div style={divider} />
            <div style={totalLine}><span>Total</span><span>{formatPrice(totalInRWF)}</span></div>
          </div>
        </div>
      </div>
    </div>
  );
};

// --- STYLES (Modern & Dark Centered Card) ---
const pageBackground = { backgroundColor: '#0f1111', minHeight: '100vh', display: 'flex', alignItems: 'center', justifyContent: 'center', padding: '20px', fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Helvetica, Arial, sans-serif' };
const checkoutContainer = { backgroundColor: 'white', width: '100%', maxWidth: '1000px', borderRadius: '16px', display: 'flex', flexWrap: 'wrap', overflow: 'hidden', boxShadow: '0 25px 50px -12px rgba(0, 0, 0, 0.5)' };
const mainPanel = { flex: '1.2', padding: '40px', minWidth: '350px' };
const summaryPanel = { flex: '0.8', backgroundColor: '#f9fafb', padding: '40px', borderLeft: '1px solid #eee', minWidth: '320px' };
const brandHeader = { marginBottom: '30px' };
const brandName = { margin: 0, color: '#008ECC', fontSize: '24px', fontWeight: '800', fontStyle: 'italic' };
const breadcrumb = { display: 'flex', alignItems: 'center', gap: '8px', fontSize: '12px', color: '#999', marginTop: '5px' };
const activeStep = { color: '#000', fontWeight: 'bold' };
const formWrapper = { display: 'flex', flexDirection: 'column', gap: '15px' };
const sectionTitle = { fontSize: '13px', fontWeight: 'bold', color: '#444', marginTop: '10px', display: 'flex', alignItems: 'center', gap: '8px' };
const fullInput = { padding: '12px', borderRadius: '8px', border: '1px solid #ddd', fontSize: '14px', outline: 'none' };
const inputRow = { display: 'flex', gap: '10px' };
const halfInput = { ...fullInput, flex: 1 };
const paymentOptions = { display: 'flex', gap: '10px' };
const paymentOption = { flex: 1, padding: '12px', borderRadius: '8px', border: '1px solid #ddd', textAlign: 'center', cursor: 'pointer', fontSize: '14px', display: 'flex', alignItems: 'center', justifyContent: 'center', gap: '8px' };
const activePayment = { ...paymentOption, borderColor: '#008ECC', backgroundColor: '#f0f9ff', color: '#008ECC', borderWidth: '2px' };
const confirmBtn = { padding: '16px', backgroundColor: '#111', color: 'white', border: 'none', borderRadius: '10px', fontWeight: 'bold', fontSize: '16px', cursor: 'pointer', marginTop: '20px' };
const secureBadge = { fontSize: '11px', color: '#999', textAlign: 'center', marginTop: '15px', display: 'flex', alignItems: 'center', justifyContent: 'center', gap: '5px' };
const cartItemsList = { display: 'flex', flexDirection: 'column', gap: '15px' };
const itemLine = { display: 'flex', gap: '12px', alignItems: 'center' };
const itemIcon = { width: '40px', height: '40px', backgroundColor: '#eee', borderRadius: '8px', display: 'flex', alignItems: 'center', justifyContent: 'center', fontWeight: 'bold', color: '#555' };
const itemName = { fontSize: '13px', fontWeight: '600', margin: 0 };
const itemQty = { fontSize: '11px', color: '#888' };
const itemPrice = { fontSize: '13px', fontWeight: 'bold' };
const divider = { height: '1px', backgroundColor: '#eee', margin: '15px 0' };
const couponBox = { marginBottom: '20px' };
const miniInput = { flex: 1, padding: '8px', borderRadius: '6px', border: '1px solid #ddd', fontSize: '12px', outline: 'none', width: '70%' };
const miniBtn = { padding: '8px 12px', backgroundColor: '#eee', border: 'none', borderRadius: '6px', cursor: 'pointer', fontSize: '12px' };
const appliedLabel = { fontSize: '11px', color: '#28a745', marginTop: '8px', fontWeight: 'bold', display: 'flex', alignItems: 'center', gap: '4px' };
const summaryCalculation = { display: 'flex', flexDirection: 'column', gap: '8px' };
const calcLine = { display: 'flex', justifyContent: 'space-between', fontSize: '13px', color: '#666' };
const totalLine = { display: 'flex', justifyContent: 'space-between', fontSize: '18px', fontWeight: 'bold', color: '#000' };
const successCard = { backgroundColor: 'white', padding: '50px', borderRadius: '20px', textAlign: 'center', maxWidth: '400px', width: '90%' };
const continueBtn = { marginTop: '20px', padding: '12px 25px', backgroundColor: '#111', color: 'white', border: 'none', borderRadius: '30px', cursor: 'pointer' };

export default CheckoutPage;
