import React, { useEffect, useState } from 'react';
import { supabase } from '../supabaseClient';
import { ShoppingBag, ArrowRight, Zap, Star, ShieldCheck, Menu } from 'lucide-react';
import { Link, useNavigate } from 'react-router-dom';

const HomePage = ({ addToCart }) => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  // Tech products for the sliding ticker
  const techHighlights = [
    "iPhone 16 Pro", "MacBook M3 Max", "Samsung S24 Ultra", 
    "Sony WH-1000XM5", "PlayStation 5 Pro", "Apple Watch Ultra 2", 
    "NVIDIA RTX 4090", "DJI Mini 4 Pro", "iPad Pro OLED", "Google Pixel 9"
  ];

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    const { data, error } = await supabase.from('products').select('*');
    if (!error) setProducts(data);
    setLoading(false);
  };

  return (
    <div style={homeContainer}>
      
      {/* 1. AUTO-SCROLLING TICKER BAR */}
      <div style={tickerWrapper}>
        <div style={allMenu}>
          <Menu size={18} color="white" />
          <span style={{ fontWeight: 'bold', fontSize: '14px', marginLeft: '8px' }}>All</span>
        </div>

        <div style={slidingContainer}>
          <div style={slidingTrack}>
            {/* Doubling the array for seamless infinite loop */}
            {[...techHighlights, ...techHighlights].map((item, index) => (
              <span key={index} style={tickerItem}>
                {item} <span style={dotSeparator}>â€¢</span>
              </span>
            ))}
          </div>
        </div>
      </div>

      {/* 2. HERO SECTION */}
      <section style={heroSection}>
        <div style={heroOverlay}>
          <span style={heroBadge}>NEW ARRIVALS 2025</span>
          <h1 style={heroTitle}>The Future of <br/> <span style={highlightText}>Gadgets</span> is Here.</h1>
          <p style={heroSubtitle}>Upgrade your lifestyle with the latest tech from MegaMart Rwanda.</p>
          <div style={heroButtons}>
            {/* Updated Shop Now button to scroll to Featured Products */}
            <button 
              style={primaryBtn} 
              onClick={() => document.getElementById('featured-products').scrollIntoView({ behavior: 'smooth' })}
            >
              Shop Now <ArrowRight size={18} />
            </button>
            
            {/* Updated View Deals button to navigate to dealType route */}
            <button 
              style={secondaryBtn} 
              onClick={() => navigate('/deals/hot-deals')}
            >
              View Deals
            </button>
          </div>
        </div>
      </section>

      {/* 3. TRUST BAR */}
      <div style={trustBar}>
        <div style={trustItem}><Zap size={20} color="#008ECC" /> <span>Same Day Delivery</span></div>
        <div style={trustItem}><ShieldCheck size={20} color="#008ECC" /> <span>Official Warranty</span></div>
        <div style={trustItem}><Star size={20} color="#008ECC" /> <span>Top Rated Service</span></div>
      </div>

      {/* 4. CATEGORY BUBBLES */}
      <div style={categoryWrapper}>
        {['Smartphones', 'Laptops', 'Watch', 'Audio', 'Gaming'].map(cat => (
          <Link key={cat} to={`/category/${cat}`} style={catCircleLink}>
            <div style={catCircle}>{cat[0]}</div>
            <span style={catName}>{cat}</span>
          </Link>
        ))}
      </div>

      {/* 5. PRODUCT GRID */}
      <div style={sectionWrapper} id="featured-products">
        <div style={sectionHeader}>
          <h2>Featured Products</h2>
          <Link to="/category/all" style={viewAll}>View All</Link>
        </div>

        {loading ? (
          <div style={loaderStyle}>Loading Greatness...</div>
        ) : (
          <div style={productGrid}>
            {products.map((product) => (
              <div key={product.id} style={productCard} className="glass-card">
                <div style={imagePlaceholder}>
                   <ShoppingBag size={40} color="#ccc" />
                </div>
                <div style={cardContent}>
                  <span style={cardCategory}>{product.category}</span>
                  <h3 style={cardTitle}>{product.name}</h3>
                  <div style={priceRow}>
                    <span style={cardPrice}>RWF {product.price?.toLocaleString()}</span>
                    <button 
                      onClick={() => addToCart(product)} 
                      style={addBtn}
                    >
                      +
                    </button>
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>

      {/* INLINE ANIMATION STYLE */}
      <style>
        {`
          @keyframes scrollTicker {
            0% { transform: translateX(0); }
            100% { transform: translateX(-50%); }
          }
        `}
      </style>
    </div>
  );
};

// --- STYLES ---
const homeContainer = { backgroundColor: '#fff', minHeight: '100vh' };

// Ticker Styles
const tickerWrapper = { display: 'flex', alignItems: 'center', backgroundColor: '#232f3e', color: 'white', height: '40px', overflow: 'hidden' };
const allMenu = { display: 'flex', alignItems: 'center', padding: '0 20px', backgroundColor: '#232f3e', zIndex: 10, height: '100%', borderRight: '1px solid rgba(255,255,255,0.2)', cursor: 'pointer' };
const slidingContainer = { flex: 1, overflow: 'hidden', display: 'flex', alignItems: 'center' };
const slidingTrack = { display: 'flex', whiteSpace: 'nowrap', animation: 'scrollTicker 30s linear infinite' };
const tickerItem = { padding: '0 30px', fontSize: '13px', color: '#cbd5e0', display: 'flex', alignItems: 'center' };
const dotSeparator = { color: '#008ECC', marginLeft: '20px', fontSize: '18px' };

// Hero Styles
