import React, { useEffect, useState } from 'react';
import { supabase } from '../supabaseClient';

const HomePage = ({ addToCart }) => {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    const fetchProducts = async () => {
      const { data } = await supabase.from('products').select('*');
      setProducts(data || []);
    };
    fetchProducts();
  }, []);

  // Professional Gadget Categories
  const categories = [
    { name: "Smartphones", icon: "ðŸ“±" },
    { name: "Laptops", icon: "ðŸ’»" },
    { name: "Smart Watches", icon: "âŒš" },
    { name: "Audio", icon: "ðŸŽ§" },
    { name: "Gaming", icon: "ðŸŽ®" },
    { name: "Accessories", icon: "ðŸ”Œ" }
  ];

  return (
    <div style={{ backgroundColor: '#f4f7f8', minHeight: '100vh', paddingBottom: '50px' }}>
      
      {/* 1. Category Quick Links */}
      <div style={categoryBar}>
        {categories.map(cat => (
          <div key={cat.name} style={categoryItem}>
            <div style={categoryIcon}>{cat.icon}</div>
            <span style={{ fontSize: '12px', fontWeight: '600' }}>{cat.name}</span>
          </div>
        ))}
      </div>

      {/* 2. Hero Deals Slider Area */}
      <div style={dealHero}>
        <div style={dealContent}>
          <span style={badge}>Limited Time Offer</span>
          <h2 style={{ fontSize: '32px', margin: '10px 0' }}>Latest Galaxy S24 Ultra</h2>
          <p>Get up to 15% discount on all pre-orders this week at MegaMart Rwanda.</p>
          <button style={dealBtn}>Shop Now</button>
        </div>
        <div style={dealImage}>
          <img 
            src="https://images.unsplash.com/photo-1610641818989-c2051b5e2cfd?auto=format&fit=crop&q=80&w=400" 
            alt="Special Deal" 
            style={{ borderRadius: '15px', maxWidth: '100%' }} 
          />
        </div>
      </div>

      {/* 3. Grouped Product Listing */}
      {categories.map(cat => {
        const filteredProducts = products.filter(p => p.category === cat.name);
        if (filteredProducts.length === 0) return null;

        return (
          <div key={cat.name} style={{ padding: '0 50px', marginBottom: '40px' }}>
            <div style={sectionHeader}>
              <h3 style={{ margin: 0 }}>Top {cat.name} Brands</h3>
              <span style={{ color: '#008ECC', fontWeight: 'bold', cursor: 'pointer' }}>View all â€º</span>
            </div>
            
            <div style={horizontalGrid}>
              {filteredProducts.map(product => (
                <div key={product.id} style={amazonStyleCard}>
                  <div style={imgContainer}>
                    <img src={product.image_url} alt={product.name} style={prodImg} />
                    <div style={saveBadge}>Save 10%</div>
                  </div>
                  <div style={{ padding: '15px' }}>
                    <h4 style={prodTitle}>{product.name}</h4>
                    <div style={{ color: '#555', fontSize: '12px', marginBottom: '10px' }}>Official Warranty Included</div>
                    <div style={priceRow}>
                      <span style={priceLarge}>RWF {product.price.toLocaleString()}</span>
                      <button onClick={() => addToCart(product)} style={cartBtn}>+</button>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </div>
        );
      })}
    </div>
  );
};

// --- ALL DEFINITIONS (Fixes your Errors) ---
const categoryBar = { display: 'flex', justifyContent: 'center', gap: '40px', padding: '30px', backgroundColor: 'white', marginBottom: '30px' };
const categoryItem = { display: 'flex', flexDirection: 'column', alignItems: 'center', cursor: 'pointer' };
const categoryIcon = { width: '60px', height: '60px', borderRadius: '50%', backgroundColor: '#f0f9ff', display: 'flex', alignItems: 'center', justifyContent: 'center', fontSize: '24px', marginBottom: '8px' };

const dealHero = { margin: '0 50px 40px 50px', backgroundColor: '#1a2a3a', borderRadius: '20px', color: 'white', padding: '40px', display: 'flex', justifyContent: 'space-between', alignItems: 'center' };
const dealContent = { maxWidth: '50%' };
const dealImage = { maxWidth: '40%', display: 'flex', justifyContent: 'center' }; // FIXED
const badge = { backgroundColor: '#ff4d4d', padding: '4px 12px', borderRadius: '20px', fontSize: '12px', fontWeight: 'bold' };
const dealBtn = { backgroundColor: 'white', color: '#1a2a3a', border: 'none', padding: '12px 25px', borderRadius: '10px', fontWeight: 'bold', cursor: 'pointer', marginTop: '20px' };

const sectionHeader = { display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '20px' };
const horizontalGrid = { display: 'grid', gridTemplateColumns: 'repeat(auto-fill, minmax(240px, 1fr))', gap: '20px' };

const amazonStyleCard = { backgroundColor: 'white', borderRadius: '12px', overflow: 'hidden', border: '1px solid #eee' };
const imgContainer = { height: '180px', backgroundColor: '#f8f8f8', display: 'flex', alignItems: 'center', justifyContent: 'center', position: 'relative' };
const prodImg = { maxWidth: '70%', maxHeight: '70%', objectFit: 'contain' };
const saveBadge = { position: 'absolute', top: '10px', right: '10px', backgroundColor: '#e8f8f0', color: '#27ae60', padding: '4px 8px', borderRadius: '6px', fontSize: '10px', fontWeight: 'bold' };

const prodTitle = { fontSize: '15px', fontWeight: '600', margin: '0 0 5px 0', color: '#1a1a1a' };
const priceRow = { display: 'flex', justifyContent: 'space-between', alignItems: 'center' }; // FIXED
const priceLarge = { fontSize: '18px', fontWeight: '800', color: '#1a1a1a' };
const cartBtn = { backgroundColor: '#008ECC', color: 'white', border: 'none', width: '35px', height: '35px', borderRadius: '10px', cursor: 'pointer', fontSize: '20px' };

export default HomePage;
