import React from 'react';
import { useNavigate } from 'react-router-dom';
import { CheckCircle, Package, ArrowRight } from 'lucide-react';

const SuccessPage = () => {
  const navigate = useNavigate();

  return (
    <div style={container}>
      <CheckCircle size={80} color="#2e7d32" style={{ marginBottom: '20px' }} />
      <h1 style={{ color: '#232f3e' }}>Thank You!</h1>
      <p style={{ fontSize: '18px', color: '#565959' }}>Your order has been placed successfully.</p>
      
      <div style={orderBox}>
        <div style={row}><Package size={20} /> <span>Estimated Delivery: 24 - 48 Hours</span></div>
        <p style={{ fontSize: '14px', marginTop: '10px' }}>You will receive an SMS via MoMo once your package is dispatched in Kigali.</p>
      </div>

      <button onClick={() => navigate('/')} style={homeBtn}>
        Continue Shopping <ArrowRight size={18} />
      </button>
    </div>
  );
};

const container = { display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center', height: '80vh', textAlign: 'center', padding: '20px' };
const orderBox = { backgroundColor: '#f0f7ef', padding: '30px', borderRadius: '12px', border: '1px solid #c3e6cb', maxWidth: '450px', margin: '30px 0' };
const row = { display: 'flex', alignItems: 'center', justifyContent: 'center', gap: '10px', fontWeight: 'bold', color: '#155724' };
const homeBtn = { display: 'flex', alignItems: 'center', gap: '10px', padding: '15px 30px', backgroundColor: '#232f3e', color: 'white', border: 'none', borderRadius: '8px', cursor: 'pointer', fontSize: '16px', fontWeight: 'bold' };

export default SuccessPage;
