import React from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import HomePage from './pages/HomePage';
import AdminDashboard from './pages/AdminDashboard';
import SuccessPage from './pages/SuccessPage'; 
import Navbar from './components/Navbar'; // Ensure this exists in src/components/

function App() {
  return (
    <Router>
      <div className="App" style={{ minHeight: '100vh', display: 'flex', flexDirection: 'column' }}>
        
        {/* 1. Admin/Store Switcher (Top Bar) */}
        <div style={{ backgroundColor: '#212121', color: 'white', padding: '8px 50px', fontSize: '11px', display: 'flex', gap: '20px', letterSpacing: '0.5px' }}>
          <Link to="/" style={{ color: 'white', textDecoration: 'none', opacity: 0.8 }}>CUSTOMER VIEW</Link>
          <span style={{ opacity: 0.3 }}>|</span>
          <Link to="/admin" style={{ color: 'white', textDecoration: 'none', opacity: 0.8 }}>ADMIN PANEL</Link>
        </div>

        {/* 2. Professional Navbar (Visible on all pages) */}
        <Navbar />
        
        {/* 3. Main Content Area */}
        <main style={{ flex: 1 }}>
          <Routes>
            <Route path="/" element={<HomePage />} />
            <Route path="/admin" element={<AdminDashboard />} />
            <Route path="/success" element={<SuccessPage />} />
          </Routes>
        </main>

        {/* 4. Simple Professional Footer */}
        <footer style={{ backgroundColor: '#fff', padding: '30px', textAlign: 'center', borderTop: '1px solid #eee', marginTop: '50px' }}>
          <p style={{ color: '#008ECC', fontWeight: 'bold', marginBottom: '5px' }}>MegaMart Rwanda</p>
          <p style={{ fontSize: '12px', color: '#888' }}>Â© 2025 Electronics Store Demo. Built for Sunday Presentation.</p>
        </footer>

      </div>
    </Router>
  );
}

export default App;
