import React, { useState, useEffect } from 'react';
import { supabase } from '../supabaseClient';
import { Package, Truck, CheckCircle, List, Tag, LogOut, RefreshCcw, Trash2, Search } from 'lucide-react';

const AdminDashboard = ({ setIsAdmin }) => {
  const [activeTab, setActiveTab] = useState('orders'); 
  const [products, setProducts] = useState([]);
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState(''); // NEW: Search state
  const [formData, setFormData] = useState({ name: '', price: '', category: 'Smartphones', image_url: '' });

  useEffect(() => {
    fetchInventory();
    fetchOrders();
  }, []);

  const fetchInventory = async () => {
    const { data } = await supabase.from('products').select('*');
    if (data) setProducts(data);
  };

  const fetchOrders = async () => {
    setLoading(true);
    const { data } = await supabase.from('orders').select('*').order('created_at', { ascending: false });
    if (data) setOrders(data);
    setLoading(false);
  };

  const updateOrderStatus = async (orderId, newStatus) => {
    const { error } = await supabase.from('orders').update({ status: newStatus }).eq('id', orderId);
    if (!error) {
      alert(`Order updated to ${newStatus}`);
      fetchOrders();
    }
  };

  const handleAddProduct = async (e) => {
    e.preventDefault();
    const { error } = await supabase.from('products').insert([formData]);
    if (!error) {
      alert("Product Registered!");
      setFormData({ name: '', price: '', category: 'Smartphones', image_url: '' });
      fetchInventory();
    }
  };

  // NEW: Delete Function
  const deleteProduct = async (id, name) => {
    if (window.confirm(`Are you sure you want to delete "${name}"?`)) {
      const { error } = await supabase.from('products').delete().eq('id', id);
      if (!error) {
        alert("Product deleted");
        fetchInventory();
      } else {
        alert("Error deleting product");
      }
    }
  };

  const handleLogout = () => setIsAdmin(false);

  // NEW: Filtered Products Logic
  const filteredProducts = products.filter(p => 
    p.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
    p.category.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div style={{ padding: '30px', backgroundColor: '#f4f7f6', minHeight: '100vh', fontFamily: 'sans-serif' }}>
      
      {/* HEADER SECTION */}
      <div style={headerStyle}>
        <div>
          <h1 style={{ margin: 0, fontSize: '24px' }}>MegaMart Command Center</h1>
          <p style={{ color: '#666', fontSize: '14px' }}>Managing Rwanda's Official Gadget Store</p>
        </div>
        <button onClick={handleLogout} style={logoutBtn}><LogOut size={18} /> Logout</button>
      </div>

      {/* TAB NAVIGATION */}
      <div style={tabBar}>
        <button onClick={() => setActiveTab('orders')} style={activeTab === 'orders' ? activeTabBtn : tabBtn}>
          <Package size={18} /> Customer Orders
        </button>
        <button onClick={() => setActiveTab('inventory')} style={activeTab === 'inventory' ? activeTabBtn : tabBtn}>
          <Tag size={18} /> Inventory & Pricing
        </button>
      </div>

      {/* VIEW 1: CUSTOMER ORDERS */}
      {activeTab === 'orders' && (
        <div style={panelStyle}>
          <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '20px' }}>
            <h3>Recent Orders</h3>
            <button onClick={fetchOrders} style={refreshBtn}><RefreshCcw size={16} /></button>
          </div>
          {loading ? <p>Loading orders...</p> : (
            <table style={tableStyle}>
              <thead>
                <tr style={thRow}>
                  <th>DATE</th>
                  <th>CUSTOMER</th>
                  <th>TOTAL</th>
                  <th>STATUS</th>
                  <th>ACTION</th>
                </tr>
              </thead>
              <tbody>
                {orders.map(o => (
                  <tr key={o.id} style={trStyle}>
                    <td>{new Date(o.created_at).toLocaleDateString()}</td>
                    <td><span style={{ fontWeight: 'bold' }}>{o.email}</span></td>
                    <td>RWF {o.total_amount?.toLocaleString()}</td>
                    <td><span style={getStatusBadge(o.status)}>{o.status}</span></td>
                    <td>
                      <div style={{ display: 'flex', gap: '5px' }}>
                        <button onClick={() => updateOrderStatus(o.id, 'shipped')} style={actionBtn} title="Ship"><Truck size={14} /></button>
                        <button onClick={() => updateOrderStatus(o.id, 'delivered')} style={{...actionBtn, backgroundColor: '#28a745'}} title="Deliver"><CheckCircle size={14} /></button>
                      </div>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          )}
        </div>
      )}

      {/* VIEW 2: INVENTORY & PRICING */}
      {activeTab === 'inventory' && (
        <div style={{ display: 'grid', gridTemplateColumns: '1fr 2fr', gap: '30px' }}>
          <div style={panelStyle}>
            <h3>Add New Product</h3>
            <form onSubmit={handleAddProduct} style={{ display: 'flex', flexDirection: 'column', gap: '12px' }}>
              <input type="text" placeholder="Product Name" style={inputStyle} onChange={e => setFormData({...formData, name: e.target.value})} value={formData.name} required />
              <input type="number" placeholder="Price (RWF)" style={inputStyle} onChange={e => setFormData({...formData, price: e.target.value})} value={formData.price} required />
              <input type="text" placeholder="Image URL" style={inputStyle} onChange={e => setFormData({...formData, image_url: e.target.value})} value={formData.image_url} />
              <select style={inputStyle} value={formData.category} onChange={e => setFormData({...formData, category: e.target.value})}>
                <option>Smartphones</option>
                <option>Laptops</option>
                <option>Accessories</option>
              </select>
              <button type="submit" style={mainBtn}>Register Item</button>
            </form>
          </div>

          <div style={panelStyle}>
            <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '15px' }}>
              <h3 style={{ margin: 0 }}>Stock Levels ({filteredProducts.length})</h3>
              
              {/* NEW: Search Bar UI */}
              <div style={searchContainer}>
                <Search size={16} color="#888" />
                <input 
                  type="text" 
                  placeholder="Search products..." 
                  style={searchInput}
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                />
              </div>
            </div>

            <table style={tableStyle}>
              <thead>
                <tr style={thRow}>
                  <th>ITEM NAME</th>
                  <th>CATEGORY</th>
                  <th>PRICE</th>
                  <th style={{ textAlign: 'right' }}>ACTION</th>
                </tr>
              </thead>
              <tbody>
                {filteredProducts.map(p => (
                  <tr key={p.id} style={trStyle}>
                    <td style={{ padding: '12px 0' }}>{p.name}</td>
                    <td>{p.category}</td>
                    <td>RWF {p.price?.toLocaleString()}</td>
                    <td style={{ textAlign: 'right' }}>
                      <button 
                        onClick={() => deleteProduct(p.id, p.name)} 
                        style={deleteBtn}
                      >
                        <Trash2 size={14} />
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      )}
    </div>
  );
};

// --- Helpers & Styles ---
const getStatusBadge = (s) => ({
  padding: '4px 8px', borderRadius: '12px', fontSize: '10px', fontWeight: 'bold', textTransform: 'uppercase',
  backgroundColor: s === 'pending' ? '#fff3cd' : s === 'shipped' ? '#d1ecf1' : '#d4edda',
  color: s === 'pending' ? '#856404' : s === 'shipped' ? '#0c5460' : '#155724'
});

const headerStyle = { display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '30px' };
const tabBar = { display: 'flex', gap: '10px', marginBottom: '20px', borderBottom: '1px solid #ddd', paddingBottom: '10px' };
const tabBtn = { background: 'none', border: 'none', padding: '10px 20px', cursor: 'pointer', display: 'flex', alignItems: 'center', gap: '8px', color: '#666' };
const activeTabBtn = { ...tabBtn, borderBottom: '3px solid #008ECC', color: '#008ECC', fontWeight: 'bold' };
const panelStyle = { backgroundColor: 'white', padding: '25px', borderRadius: '12px', boxShadow: '0 4px 12px rgba(0,0,0,0.05)' };
const inputStyle = { padding: '10px', borderRadius: '6px', border: '1px solid #ddd' };
const mainBtn = { padding: '12px', backgroundColor: '#008ECC', color: 'white', border: 'none', borderRadius: '6px', cursor: 'pointer', fontWeight: 'bold' };
const logoutBtn = { display: 'flex', alignItems: 'center', gap: '8px', padding: '8px 15px', backgroundColor: '#212121', color: 'white', border: 'none', borderRadius: '6px', cursor: 'pointer' };
const tableStyle = { width: '100%', borderCollapse: 'collapse', marginTop: '10px' };
const thRow = { textAlign: 'left', borderBottom: '2px solid #eee', fontSize: '12px', color: '#888' };
const trStyle = { borderBottom: '1px solid #f9f9f9', fontSize: '13px' };
const actionBtn = { border: 'none', backgroundColor: '#17a2b8', color: 'white', padding: '5px', borderRadius: '4px', cursor: 'pointer' };
const refreshBtn = { background: 'none', border: 'none', cursor: 'pointer', color: '#888' };

// NEW: Added styles for Search and Delete
const searchContainer = { display: 'flex', alignItems: 'center', gap: '8px', backgroundColor: '#f0f2f5', padding: '6px 12px', borderRadius: '20px', border: '1px solid #ddd' };
const searchInput = { border: 'none', background: 'none', outline: 'none', fontSize: '13px', width: '150px' };
const deleteBtn = { backgroundColor: '#ff4d4d', color: 'white', border: 'none', padding: '6px', borderRadius: '6px', cursor: 'pointer', display: 'inline-flex', alignItems: 'center', transition: '0.2s' };

export default AdminDashboard;
