import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { Search, ShoppingCart, Menu, User, Headphones, ChevronDown } from 'lucide-react';

const Navbar = ({ cartCount, openSidebar }) => {
  const [searchInput, setSearchInput] = useState('');
  const navigate = useNavigate();
  const BRAND_BLUE = '#008ECC';

  const handleSearch = (e) => {
    e.preventDefault();
    if (searchInput.trim()) {
      navigate(`/category/search?q=${searchInput}`);
    }
  };

  return (
    <nav style={navContainer}>
      {/* Top Row: Search & Logo */}
      <div style={topRow}>
        <div style={logoSection}>
          <Menu 
            size={28} 
            onClick={openSidebar} 
            style={{ cursor: 'pointer', color: '#333' }} 
          />
          <Link to="/" style={{ textDecoration: 'none' }}>
            <div style={logoWrapper}>
               <Headphones size={24} color={BRAND_BLUE} />
               <h1 style={{...logoText, color: BRAND_BLUE}}>MegaMart</h1>
            </div>
          </Link>
        </div>

        <form onSubmit={handleSearch} style={searchBarContainer}>
          <input 
            type="text" 
            placeholder="Search for electronics..." 
            style={searchInputStyle}
            value={searchInput}
            onChange={(e) => setSearchInput(e.target.value)}
          />
          <button type="submit" style={{...searchBtn, backgroundColor: BRAND_BLUE}}>
            <Search size={20} color="white" />
          </button>
        </form>

        <div style={navLinks}>
          <Link to="/profile" style={navItem}>
            <User size={22} color="#444" />
            <div style={textGroup}>
              <span style={navTinyText}>Sign in</span>
              <span style={navBoldText}>Account</span>
            </div>
          </Link>
          
          <div style={navItem} onClick={() => navigate('/checkout')}>
            <div style={cartIconContainer}>
              <ShoppingCart size={26} color="#444" />
              <span style={{...cartBadge, backgroundColor: BRAND_BLUE}}>{cartCount}</span>
            </div>
            <span style={navBoldText}>Cart</span>
          </div>
        </div>
      </div>

      {/* NEW: Secondary Blue Sub-Navbar (Matches your design) */}
      <div style={subNavbar}>
        <div style={subNavbarContent}>
          <div style={categoryTrigger} onClick={openSidebar}>
            <Menu size={18} />
            <span style={{fontWeight: '700'}}>Shop by Category</span>
            <ChevronDown size={16} />
          </div>
          
          <div style={navLinksGroup}>
            <Link to="/about" style={subNavLink}>About us</Link>
            <Link to="/partnership" style={subNavLink}>Business partnership</Link>
            <Link to="/contact-us" style={subNavLink}>Contact us</Link>
          </div>
        </div>
      </div>
    </nav>
  );
};

// --- STYLES ---
const navContainer = { display: 'flex', flexDirection: 'column', width: '100%', boxShadow: '0 2px 10px rgba(0,0,0,0.05)', position: 'sticky', top: 0, zIndex: 1000, backgroundColor: '#fff' };
const topRow = { display: 'flex', alignItems: 'center', padding: '12px 8%', gap: '20px', justifyContent: 'space-between' };
const logoSection = { display: 'flex', alignItems: 'center', gap: '15px' };
const logoWrapper = { display: 'flex', alignItems: 'center', gap: '5px' };
const logoText = { fontSize: '24px', fontWeight: '900', margin: 0 };
const searchBarContainer = { flex: 1, display: 'flex', maxWidth: '700px', height: '42px', border: '2px solid #008ECC', borderRadius: '25px', overflow: 'hidden' };
const searchInputStyle = { flex: 1, padding: '0 20px', border: 'none', outline: 'none' };
const searchBtn = { border: 'none', width: '60px', cursor: 'pointer', display: 'flex', justifyContent: 'center', alignItems: 'center' };
const navLinks = { display: 'flex', gap: '25px', alignItems: 'center' };
const navItem = { display: 'flex', alignItems: 'center', gap: '10px', cursor: 'pointer', textDecoration: 'none', color: '#333' };
const textGroup = { display: 'flex', flexDirection: 'column' };
const navTinyText = { fontSize: '11px', color: '#777' };
const navBoldText = { fontSize: '14px', fontWeight: '700' };
const cartIconContainer = { position: 'relative' };
const cartBadge = { position: 'absolute', top: '-8px', right: '-10px', color: 'white', borderRadius: '12px', padding: '2px 6px', fontSize: '10px', fontWeight: 'bold' };

// Sub-Navbar Styles
const subNavbar = { borderTop: '1px solid #eee', borderBottom: '2px solid #008ECC' };
const subNavbarContent = { display: 'flex', alignItems: 'center', padding: '0 8%', gap: '40px' };
const categoryTrigger = { backgroundColor: '#008ECC', color: 'white', padding: '12px 25px', display: 'flex', alignItems: 'center', gap: '10px', cursor: 'pointer' };
const navLinksGroup = { display: 'flex', gap: '30px' };
const subNavLink = { textDecoration: 'none', color: '#444', fontSize: '14px', fontWeight: '500' };

export default Navbar;
