import React, { useEffect, useState } from 'react';
import { supabase } from '../supabaseClient';
import { ShoppingBag, ArrowRight, Zap, Star, ShieldCheck, Menu, Timer } from 'lucide-react';
import { Link, useNavigate } from 'react-router-dom';

const HomePage = ({ addToCart }) => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  // --- UPDATED: REAL CDN 3D ASSETS ---
  const categories = [
    { name: 'Smartphones', img: 'https://images.unsplash.com/photo-1511707171634-5f897ff02aa9?auto=format&fit=crop&w=500&q=80' },
    { name: 'Laptops', img: '' },
    { name: 'Watch', img: '' },
    { name: 'Audio', img: '' },
    // { name: 'Gaming', img: 'https://img.icons8.com/isometric/512/nintendo-switch.png' }
  ];

  const [timeLeft, setTimeLeft] = useState({ hours: 24, minutes: 0, seconds: 0 });

  useEffect(() => {
    const timer = setInterval(() => {
      const now = new Date();
      const endOfDay = new Date();
      endOfDay.setHours(23, 59, 59);
      const diff = endOfDay - now;
      if (diff > 0) {
        setTimeLeft({
          hours: Math.floor((diff / (1000 * 60 * 60)) % 24),
          minutes: Math.floor((diff / 1000 / 60) % 60),
          seconds: Math.floor((diff / 1000) % 60),
        });
      }
    }, 1000);
    return () => clearInterval(timer);
  }, []);

  const techHighlights = [
    "iPhone 16 Pro", "MacBook M3 Max", "Samsung S24 Ultra", 
    "Sony WH-1000XM5", "PlayStation 5 Pro", "Apple Watch Ultra 2", 
    "NVIDIA RTX 4090", "DJI Mini 4 Pro", "iPad Pro OLED"
  ];

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    const { data, error } = await supabase.from('products').select('*');
    if (!error) setProducts(data);
    setLoading(false);
  };

  return (
    <div style={homeContainer}>
      {/* 1. AUTO-SCROLLING TICKER */}
      <div style={tickerWrapper}>
        <div style={slidingContainer}>
          <div style={slidingTrack}>
            {[...techHighlights, ...techHighlights].map((item, index) => (
              <span key={index} style={tickerItem}>{item} <span style={dotSeparator}>â€¢</span></span>
            ))}
          </div>
        </div>
      </div>

      {/* 2. HERO SECTION */}
      <section style={heroSection}>
        <div style={heroOverlay}>
          <div style={timerBadge}>
            <Timer size={14} /> 
            <span>FLASH SALE ENDS IN: </span>
            <strong style={{marginLeft: '5px'}}>
              {timeLeft.hours}h {timeLeft.minutes}m {timeLeft.seconds}s
            </strong>
          </div>
          <h1 style={heroTitle}>The Future of <br/> <span style={highlightText}>Gadgets</span> is Here.</h1>
          <p style={heroSubtitle}>Official Megamart Rwanda: Your destination for verified global tech.</p>
          <div style={heroButtons}>
            <button style={primaryBtn} onClick={() => document.getElementById('featured-products').scrollIntoView({ behavior: 'smooth' })}>
              Shop Now <ArrowRight size={18} />
            </button>
            <button style={secondaryBtn} onClick={() => navigate('/deals/hot-deals')}>
              View Deals
            </button>
          </div>
        </div>
      </section>

      {/* 3. TRUST BAR */}
      <div style={trustBar}>
        <div style={trustItem}><Zap size={20} color="#008ECC" /> <span>Same Day Delivery</span></div>
        <div style={trustItem}><ShieldCheck size={20} color="#008ECC" /> <span>Official Warranty</span></div>
        <div style={trustItem}><Star size={20} color="#008ECC" /> <span>Top Rated Service</span></div>
      </div>

      {/* 4. CATEGORY BUBBLES WITH REAL 3D ICONS */}
      <div style={categoryWrapper}>
        {categories.map(cat => (
          <Link key={cat.name} to={`/category/${cat.name}`} style={catCircleLink}>
            <div style={catCircle} className="category-bubble">
              <img src={cat.img} alt={cat.name} style={catImage} />
            </div>
            <span style={catName}>{cat.name}</span>
          </Link>
        ))}
      </div>

      {/* 5. PRODUCT GRID */}
      <div style={sectionWrapper} id="featured-products">
        <div style={sectionHeader}>
          <h2>Featured Products</h2>
          <Link to="/category/all" style={viewAll}>View All</Link>
        </div>

        {loading ? <div style={loaderStyle}>Loading Greatness...</div> : (
          <div style={productGrid}>
            {products.map((product) => (
              <div key={product.id} style={productCard}>
                <div style={imagePlaceholder}>
                  {product.image_url ? (
                    <img src={product.image_url} alt={product.name} style={productImg} />
                  ) : (
                    <ShoppingBag size={40} color="#ccc" />
                  )}
                </div>
                <div style={cardContent}>
                  <span style={cardCategory}>{product.category}</span>
                  <h3 style={cardTitle}>{product.name}</h3>
                  <div style={priceRow}>
                    <span style={cardPrice}>RWF {product.price?.toLocaleString()}</span>
                    <button onClick={() => addToCart(product)} style={addBtn}>+</button>
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>

      <style>
        {`
          @keyframes scrollTicker { 0% { transform: translateX(0); } 100% { transform: translateX(-50%); } }
          .category-bubble { transition: all 0.3s cubic-bezier(0.175, 0.885, 0.32, 1.275); }
          .category-bubble:hover { 
            transform: scale(1.15) translateY(-5px);
            background-color: #ffffff !important;
            box-shadow: 0 15px 30px rgba(0,0,0,0.1);
          }
        `}
      </style>
    </div>
  );
};

