import React, { useEffect, useState } from 'react';
import { supabase } from '../supabaseClient';

const HomePage = ({ addToCart }) => {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    const fetchProducts = async () => {
      const { data } = await supabase.from('products').select('*');
      setProducts(data || []);
    };
    fetchProducts();
  }, []);

  return (
    <div style={{ backgroundColor: '#f4f7f6', minHeight: '100vh' }}>
      {/* Welcome Banner */}
      <div style={welcomeBanner}>
        <div style={{ maxWidth: '600px' }}>
          <h2 style={{ fontSize: '36px', marginBottom: '10px' }}>Upgrade Your Tech Today</h2>
          <p>Fast delivery across Kigali and secure MoMo payments.</p>
        </div>
      </div>

      <div style={{ padding: '0 50px', marginTop: '-50px' }}>
        <div style={gridStyle}>
          {products.map((product) => (
            <div key={product.id} style={productCard}>
              <div style={categoryLabel}>{product.category}</div>
              <div style={imgWrapper}>
                <img src={product.image_url} alt={product.name} style={imgStyle} />
              </div>
              <h4 style={titleStyle}>{product.name}</h4>
              <div style={priceContainer}>
                <span style={priceStyle}>RWF {product.price.toLocaleString()}</span>
                <button onClick={() => addToCart(product)} style={addBtnStyle}>Add +</button>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

const welcomeBanner = { background: 'linear-gradient(135deg, #008ECC 0%, #005f87 100%)', color: 'white', padding: '80px 50px', textAlign: 'left' };
const gridStyle = { display: 'grid', gridTemplateColumns: 'repeat(auto-fill, minmax(260px, 1fr))', gap: '25px' };
const productCard = { backgroundColor: 'white', borderRadius: '15px', padding: '20px', boxShadow: '0 4px 15px rgba(0,0,0,0.05)', position: 'relative' };
const categoryLabel = { fontSize: '10px', color: '#008ECC', fontWeight: '800', textTransform: 'uppercase', marginBottom: '10px' };
const imgWrapper = { height: '180px', display: 'flex', alignItems: 'center', justifyContent: 'center', borderBottom: '1px solid #f9f9f9', marginBottom: '15px' };
const imgStyle = { maxHeight: '100%', maxWidth: '100%', objectFit: 'contain' };
const titleStyle = { fontSize: '16px', fontWeight: '600', margin: '0 0 15px 0', height: '40px', overflow: 'hidden' };
const priceContainer = { display: 'flex', justifyContent: 'space-between', alignItems: 'center' };
const priceStyle = { fontSize: '18px', fontWeight: 'bold', color: '#1a1a1a' };
const addBtnStyle = { backgroundColor: '#008ECC', color: 'white', border: 'none', padding: '8px 15px', borderRadius: '8px', cursor: 'pointer', fontWeight: 'bold' };

export default HomePage;
