import React, { useEffect, useState } from 'react';
import { supabase } from '../supabaseClient';
import { 
  ShoppingBag, 
  ChevronRight, 
  User, 
  CreditCard, 
  Package, 
  MessageCircle 
} from 'lucide-react';
import { Link } from 'react-router-dom';

// BRAND COLORS
const BRAND_BLUE = '#008ECC'; 
const LIGHT_BG = '#F5F7F9';

const HomePage = ({ addToCart }) => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);

  // Categories matching your sidebar/KiKUU layout
  const categories = [
    "Electronics", "Smartphones", "Laptops", "Watches", 
    "Audio & Headphones", "Gaming", "Cameras", "Accessories"
  ];

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    // Fetching first 10 products from your Supabase database
    const { data, error } = await supabase.from('products').select('*').limit(10);
    if (!error) setProducts(data);
    setLoading(false);
  };

  return (
    <div style={pageContainer}>
      {/* 1. SUB-NAV MENU (KiKUU Style) */}
      <nav style={subNavBar}>
        <div style={navItemActive}>
           <ShoppingBag size={18} /> Shop by Category
        </div>
        
        <Link to="/about" style={navLinkTag}>
          <div style={navItem}>About us</div>
        </Link>
        
        <div style={navItem}>Business partnership</div>
        <div style={navItem}>Contact us</div>
      </nav>

      {/* 2. HERO SECTION (Sidebar + Banner + Profile) */}
      <div style={heroGrid}>
        
        {/* LEFT SIDEBAR (Category List) */}
        <aside style={sidebar}>
          {categories.map((cat, i) => (
            <div key={i} style={sidebarItem}>
              {cat} <ChevronRight size={14} color="#ccc" />
            </div>
          ))}
        </aside>

        {/* MIDDLE BANNER (Main Advertisement) */}
        <div style={mainBanner}>
          <div style={bannerContent}>
            <h2 style={bannerTitle}>Wholesale Market</h2>
            <button style={bannerBtn}>GO SHOPPING <ChevronRight size={16} /></button>
          </div>
        </div>

        {/* RIGHT PROFILE BOX */}
        <div style={profileSection}>
          <div style={userHeader}>
            <div style={avatarCircle}><User size={24} color="#666" /></div>
            <p style={userGreeting}>Hi, Guest</p>
          </div>
          
          <div style={userActions}>
            <div style={actionIcon}>
              <CreditCard size={18} color={BRAND_BLUE} />
              <span style={iconLabel}>My Coupons</span>
            </div>
            <div style={actionIcon}>
              <Package size={18} color={BRAND_BLUE} />
              <span style={iconLabel}>My Orders</span>
            </div>
            <div style={actionIcon}>
              <MessageCircle size={18} color={BRAND_BLUE} />
              <span style={iconLabel}>Support</span>
            </div>
          </div>

          <div style={promoBox}>
            <p style={promoText}><strong>Great Value</strong><br/>Continuous promotions</p>
            <p style={promoText}><strong>Worldwide Delivery</strong><br/>Global shipping available</p>
          </div>
        </div>
      </div>

      {/* 3. FEATURED PRODUCTS SECTION */}
      <div style={sectionWrapper}>
        <div style={sectionHeader}>
           <h2 style={sectionTitle}>Featured Products</h2>
           <Link to="/category/all" style={viewAllLink}>View All</Link>
        </div>

        {loading ? (
          <div style={loadingState}>Loading latest gadgets...</div>
        ) : (
          <div style={productGrid}>
            {products.map((product) => (
              <div key={product.id} style={productCard}>
                <div style={imgBox}>
                  <img src={product.image_url} alt={product.name} style={prodImg} />
                </div>
                <div style={cardInfo}>
                  <p style={prodName}>{product.name}</p>
                  <p style={prodPrice}>RWF {product.price?.toLocaleString()}</p>
                  <button 
                    onClick={() => addToCart(product)} 
                    style={addBtn}
                  >
                    Add to Cart
                  </button>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

// --- STYLES (Blue & White Theme) ---

const pageContainer = { 
  backgroundColor: '#f5f5f5', 
  minHeight: '100vh', 
  width: '100%' 
};

const subNavBar = { 
  display: 'flex', 
  gap: '30px', 
  padding: '0 8%', 
  backgroundColor: '#fff', 
  borderBottom: '1px solid #eee', 
  height: '50px', 
  alignItems: 'center' 
};

const navItemActive = { 
  backgroundColor: BRAND_BLUE, 
  color: '#fff', 
  height: '100%', 
  padding: '0 25px', 
  display: 'flex', 
  alignItems: 'center', 
  gap: '10px', 
  fontWeight: '700',
  borderTopLeftRadius: '4px', 
  borderTopRightRadius: '4px',
  cursor: 'pointer'
};

const navItem = { 
  color: '#333', 
  fontSize: '14px', 
  fontWeight: '500', 
  cursor: 'pointer' 
};

const navLinkTag = {
  textDecoration: 'none',
  color: 'inherit'
};

const heroGrid = { 
  display: 'grid', 
  gridTemplateColumns: '220px 1fr 240px', 
  gap: '15px', 
  padding: '15px 8%', 
  height: '420px', 
  boxSizing: 'border-box' 
};

const sidebar = { 
  backgroundColor: '#fff', 
  padding: '10px 0', 
  border: '1px solid #eee', 
  borderRadius: '4px' 
};

const sidebarItem = { 
  padding: '10px 20px', 
  fontSize: '13px', 
  display: 'flex', 
  justifyContent: 'space-between', 
  alignItems: 'center', 
  cursor: 'pointer', 
  borderBottom: '1px solid #f9f9f9'
