import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { supabase } from '../supabaseClient';
import { CreditCard, Truck, ShieldCheck, MapPin, Smartphone, AlertCircle } from 'lucide-react';

const CheckoutPage = ({ cart, setCart, user }) => {
  const navigate = useNavigate();
  const [loading, setLoading] = useState(false);
  const [paymentMethod, setPaymentMethod] = useState('momo');
  
  // Shipping State
  const [shippingDetails, setShippingDetails] = useState({
    phone: '',
    address: '',
    district: 'Gasabo'
  });

  const subtotal = cart.reduce((sum, item) => sum + (item.price || 0), 0);
  const deliveryFee = 2000;
  const total = subtotal + deliveryFee;

  const handlePlaceOrder = async (e) => {
    e.preventDefault();
    if (!user) {
      alert("Please login to place an order");
      return navigate('/login');
    }

    setLoading(true);

    try {
      const orderData = {
        user_id: user.id,
        email: user.email,
        items: cart, // Stores array of products as JSONB
        total_amount: total,
        shipping_address: `${shippingDetails.district}, ${shippingDetails.address}`,
        phone_number: shippingDetails.phone,
        payment_method: paymentMethod,
        status: 'pending'
      };

      // 1. Insert into Supabase 'orders' table
      const { error } = await supabase
        .from('orders')
        .insert([orderData]);

      if (error) throw error;

      // 2. SUCCESS: Clear Cart & Redirect
      localStorage.removeItem('megaMartCart');
      setCart([]);
      navigate('/success');

    } catch (error) {
      console.error("Checkout Error:", error.message);
      alert("Order failed: " + error.message);
    } finally {
      setLoading(false);
    }
  };

  if (cart.length === 0) {
    return (
      <div style={emptyState}>
        <AlertCircle size={48} color="#888" />
        <h2>Your cart is empty</h2>
        <button onClick={() => navigate('/')} style={continueBtn}>Continue Shopping</button>
      </div>
    );
  }

  return (
    <div style={pageLayout}>
      <div style={leftColumn}>
        <h2 style={sectionHeader}>Checkout Details</h2>
        
        {/* SHIPPING FORM */}
        <section style={cardStyle}>
          <h3 style={cardTitle}><MapPin size={20} /> Shipping Information</h3>
          <div style={formGrid}>
            <div style={inputGroup}>
              <label style={labelStyle}>Phone Number (Momo)</label>
              <input 
                type="text" 
                placeholder="078... / 079..." 
                style={inputStyle}
                required
                onChange={(e) => setShippingDetails({...shippingDetails, phone: e.target.value})}
              />
            </div>
            <div style={inputGroup}>
              <label style={labelStyle}>District</label>
              <select 
                style={inputStyle}
                onChange={(e) => setShippingDetails({...shippingDetails, district: e.target.value})}
              >
                <option>Gasabo</option>
                <option>Kicukiro</option>
                <option>Nyarugenge</option>
                <option>Outside Kigali</option>
              </select>
            </div>
            <div style={{...inputGroup, gridColumn: 'span 2'}}>
              <label style={labelStyle}>Specific Address / Street KG...</label>
              <input 
                type="text" 
                placeholder="House number or Landmark" 
                style={inputStyle}
                onChange={(e) => setShippingDetails({...shippingDetails, address: e.target.value})}
              />
            </div>
          </div>
        </section>

        {/* PAYMENT SELECTION */}
        <section style={cardStyle}>
          <h3 style={cardTitle}><CreditCard size={20} /> Payment Method</h3>
          <div style={paymentOptions}>
            <div 
              style={paymentMethod === 'momo' ? activeMethod : methodStyle} 
              onClick={() => setPaymentMethod('momo')}
            >
              <Smartphone size={24} />
              <span>Mobile Money (MTN/Airtel)</span>
            </div>
            <div 
              style={paymentMethod === 'card' ? activeMethod : methodStyle} 
              onClick={() => setPaymentMethod('card')}
            >
              <CreditCard size={24} />
              <span>Debit / Credit Card</span>
            </div>
          </div>
        </section>
      </div>

      {/* ORDER SUMMARY SIDEBAR */}
      <div style={rightColumn}>
        <div style={summaryCard}>
          <h3 style={{ marginTop: 0 }}>Order Summary</h3>
          <div style={summaryRow}>
            <span>Items ({cart.length})</span>
            <span>RWF {subtotal.toLocaleString()}</span>
          </div>
          <div style={summaryRow}>
            <span>Delivery Fee</span>
            <span>RWF {deliveryFee.toLocaleString()}</span>
          </div>
          <hr style={divider} />
          <div style={totalRow}>
            <span>Total</span>
            <span>RWF {total.toLocaleString()}</span>
          </div>
          
          <button 
            onClick={handlePlaceOrder} 
            style={orderBtn}
            disabled={loading}
          >
            {loading ? "Processing..." : "Confirm & Place Order"}
          </button>
          
          <p style={securityNote}>
            <ShieldCheck size={14} /> Encrypted Secure Checkout
          </p>
        </div>
      </div>
    </div>
  );
};

// --- STYLES ---
const pageLayout = { display: 'flex', gap: '30px', padding: '40px 10%', backgroundColor: '#f8f9fa', minHeight: '100vh' };
const leftColumn = { flex: 2, display: 'flex', flexDirection: 'column', gap: '20px' };
const rightColumn = { flex: 1 };
const sectionHeader = { margin: '0 0 10px 0', color: '#232f3e' };
const cardStyle = { backgroundColor: 'white', padding: '25px', borderRadius: '12px', border: '1px solid #eee' };
const cardTitle = { display: 'flex', alignItems: 'center', gap: '10px', marginTop: 0, fontSize: '18px' };
const formGrid = { display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '15px', marginTop: '15px' };
const inputGroup = { display: 'flex', flexDirection: 'column', gap: '5px' };
const labelStyle = { fontSize: '13px', fontWeight: 'bold', color: '#555' };
const inputStyle = { padding: '12px', borderRadius: '8px', border: '1px solid #ddd', outline: 'none' };

const paymentOptions = { display: 'flex', flexDirection: 'column', gap: '10px', marginTop: '15px' };
const methodStyle = { display: 'flex', alignItems: 'center', gap: '15px', padding: '15px', border: '1px solid #ddd', borderRadius: '8px', cursor: 'pointer', transition: '0.2s' };
const activeMethod = { ...methodStyle, borderColor: '#008ECC', backgroundColor: '#f0faff', borderWeight: '2px' };

const summaryCard = { backgroundColor: 'white', padding: '25px', borderRadius: '12px', position: 'sticky', top: '20px', border: '1px solid #ddd' };
const summaryRow = { display: 'flex', justifyContent: 'space-between', marginBottom: '10px', color: '#666' };
const totalRow = { display: 'flex', justifyContent: 'space-between', fontWeight: 'bold', fontSize: '20px', color: '#B12704', marginBottom: '20px' };
const divider = { border: 'none', borderTop: '1px solid #eee', margin: '15px 0' };
const orderBtn = { width: '100%', padding: '15px', backgroundColor: '#FFD814', border: 'none', borderRadius: '8px', fontWeight: 'bold', cursor: 'pointer', fontSize: '16px' };
const securityNote = { textAlign: 'center', fontSize: '12px', color: '#888', marginTop: '15px', display: 'flex', alignItems: 'center', justifyContent: 'center', gap: '5px' };
const emptyState = { textAlign: 'center', padding: '100px 20px' };
const continueBtn = { marginTop: '20px', padding: '10px 20px', backgroundColor: '#232f3e', color: 'white', border: 'none', borderRadius: '6px', cursor: 'pointer' };

export default CheckoutPage;
