import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { supabase } from '../supabaseClient';

const Navbar = ({ cartCount, openModal, user }) => {
  const navigate = useNavigate();

  const handleLogout = async () => {
    await supabase.auth.signOut();
    navigate('/');
  };

  return (
    <nav style={{ 
      backgroundColor: 'rgba(255, 255, 255, 0.95)', 
      backdropFilter: 'blur(10px)',
      padding: '12px 60px', 
      display: 'flex', 
      alignItems: 'center', 
      justifyContent: 'space-between', 
      boxShadow: '0 2px 15px rgba(0,0,0,0.05)', 
      position: 'sticky', 
      top: 0, 
      zIndex: 1000 
    }}>
      {/* Brand */}
      <Link to="/" style={{ textDecoration: 'none', display: 'flex', alignItems: 'center', gap: '8px' }}>
        <div style={{ backgroundColor: '#008ECC', width: '35px', height: '35px', borderRadius: '8px', display: 'flex', alignItems: 'center', justifyContent: 'center', color: 'white', fontWeight: 'bold' }}>M</div>
        <h1 style={{ color: '#1a1a1a', fontSize: '22px', fontWeight: '800', margin: 0, letterSpacing: '-0.5px' }}>MegaMart</h1>
      </Link>

      {/* Modern Search Bar */}
      <div style={{ flex: 1, margin: '0 60px', position: 'relative' }}>
        <input 
          type="text" 
          placeholder="Search for premium electronics..." 
          style={{ 
            width: '100%', 
            padding: '12px 20px', 
            borderRadius: '12px', 
            border: '1px solid #e1e1e1', 
            backgroundColor: '#f8f9fa',
            fontSize: '14px',
            transition: 'all 0.3s'
          }} 
        />
      </div>

      {/* User Actions */}
      <div style={{ display: 'flex', gap: '30px', alignItems: 'center' }}>
        {user ? (
          <div style={{ display: 'flex', gap: '20px', alignItems: 'center' }}>
            <Link to="/profile" style={{ textDecoration: 'none', color: '#555', fontSize: '14px', fontWeight: '600' }}>Orders</Link>
            <button onClick={handleLogout} style={{ background: 'none', border: 'none', color: '#ff4d4d', cursor: 'pointer', fontWeight: '600', fontSize: '14px' }}>Logout</button>
          </div>
        ) : (
          <div style={{ display: 'flex', gap: '20px', alignItems: 'center' }}>
            <Link to="/login" style={{ textDecoration: 'none', color: '#555', fontSize: '14px', fontWeight: '600' }}>Sign In</Link>
            <Link to="/login" style={{ textDecoration: 'none', backgroundColor: '#008ECC', color: 'white', padding: '10px 20px', borderRadius: '10px', fontSize: '14px', fontWeight: 'bold' }}>Sign Up</Link>
          </div>
        )}

        <div 
          onClick={openModal} 
          style={{ cursor: 'pointer', position: 'relative', display: 'flex', alignItems: 'center' }}
        >
          <span style={{ fontSize: '24px' }}>ðŸ›’</span>
          {cartCount > 0 && (
            <span style={{ position: 'absolute', top: '-8px', right: '-10px', backgroundColor: '#ff4d4d', color: 'white', fontSize: '10px', width: '18px', height: '18px', borderRadius: '50%', display: 'flex', alignItems: 'center', justifyContent: 'center', fontWeight: 'bold' }}>
              {cartCount}
            </span>
          )}
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
