import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { supabase } from '../supabaseClient';
import { 
  CreditCard, Truck, ShieldCheck, MapPin, 
  Smartphone, AlertCircle, Trash2, ChevronRight 
} from 'lucide-react';

const CheckoutPage = ({ cart, setCart, user }) => {
  const navigate = useNavigate();
  const [loading, setLoading] = useState(false);
  const [paymentMethod, setPaymentMethod] = useState('momo');
  
  // Shipping State
  const [shippingDetails, setShippingDetails] = useState({
    phone: '',
    address: '',
    district: 'Gasabo'
  });

  // Calculations
  const subtotal = cart.reduce((sum, item) => sum + (item.price || 0), 0);
  const deliveryFee = 2000;
  const total = subtotal + deliveryFee;

  // --- Functions ---
  
  const removeItem = (index) => {
    const updatedCart = cart.filter((_, i) => i !== index);
    setCart(updatedCart);
  };

  const handlePlaceOrder = async (e) => {
    e.preventDefault();
    if (!user) {
      alert("Please login to place an order");
      return navigate('/login');
    }

    if (!shippingDetails.phone || !shippingDetails.address) {
      alert("Please fill in your delivery details");
      return;
    }

    setLoading(true);

    try {
      const orderData = {
        user_id: user.id,
        email: user.email,
        items: cart, 
        total_amount: total,
        shipping_address: `${shippingDetails.district}, ${shippingDetails.address}`,
        phone_number: shippingDetails.phone,
        payment_method: paymentMethod,
        status: 'pending'
      };

      const { error } = await supabase
        .from('orders')
        .insert([orderData]);

      if (error) throw error;

      // SUCCESS
      localStorage.removeItem('megaMartCart');
      setCart([]);
      navigate('/success');

    } catch (error) {
      console.error("Checkout Error:", error.message);
      alert("Order failed: " + error.message);
    } finally {
      setLoading(false);
    }
  };

  if (cart.length === 0) {
    return (
      <div style={emptyState}>
        <AlertCircle size={48} color="#888" />
        <h2>Your cart is empty</h2>
        <p>Add some gadgets to get started!</p>
        <button onClick={() => navigate('/')} style={continueBtn}>Continue Shopping</button>
      </div>
    );
  }

  return (
    <div style={pageLayout}>
      {/* LEFT: SHIPPING & PAYMENT */}
      <div style={leftColumn}>
        <h2 style={sectionHeader}>Secure Checkout</h2>
        
        {/* SHIPPING FORM */}
        <section style={cardStyle}>
          <h3 style={cardTitle}><MapPin size={20} color="#008ECC" /> Delivery Destination</h3>
          <div style={formGrid}>
            <div style={inputGroup}>
              <label style={labelStyle}>Phone Number (Momo)</label>
              <input 
                type="text" 
                placeholder="078... / 079..." 
                style={inputStyle}
                required
                value={shippingDetails.phone}
                onChange={(e) => setShippingDetails({...shippingDetails, phone: e.target.value})}
              />
            </div>
            <div style={inputGroup}>
              <label style={labelStyle}>District</label>
              <select 
                style={inputStyle}
                value={shippingDetails.district}
                onChange={(e) => setShippingDetails({...shippingDetails, district: e.target.value})}
              >
                <option>Gasabo</option>
                <option>Kicukiro</option>
                <option>Nyarugenge</option>
                <option>Musanze</option>
                <option>Rubavu</option>
              </select>
            </div>
            <div style={{...inputGroup, gridColumn: 'span 2'}}>
              <label style={labelStyle}>Specific Street / Landmark</label>
              <input 
                type="text" 
                placeholder="Example: KG 123 St, near the stadium" 
                style={inputStyle}
                value={shippingDetails.address}
                onChange={(e) => setShippingDetails({...shippingDetails, address: e.target.value})}
              />
            </div>
          </div>
        </section>

        {/* PAYMENT SELECTION */}
        <section style={cardStyle}>
          <h3 style={cardTitle}><CreditCard size={20} color="#008ECC" /> Payment Method</h3>
          <div style={paymentOptions}>
            <div 
              style={paymentMethod === 'momo' ? activeMethod : methodStyle} 
              onClick={() => setPaymentMethod('momo')}
            >
              <Smartphone size={22} />
              <div style={{ flex: 1 }}>
                <p style={methodName}>Mobile Money</p>
                <p style={methodDesc}>MTN MoMo or Airtel Money</p>
              </div>
              {paymentMethod === 'momo' && <CheckCircleIcon />}
            </div>
            <div 
              style={paymentMethod === 'card' ? activeMethod : methodStyle} 
              onClick={() => setPaymentMethod('card')}
            >
              <CreditCard size={22} />
              <div style={{ flex: 1 }}>
                <p style={methodName}>Bank Card</p>
                <p style={methodDesc}>Visa or Mastercard</p>
              </div>
              {paymentMethod === 'card' && <CheckCircleIcon />}
            </div>
          </div>
        </section>
      </div>

      {/* RIGHT: ORDER SUMMARY */}
      <div style={rightColumn}>
        <div style={summaryCard}>
          <h3 style={{ marginTop: 0 }}>Order Summary</h3>
          
          <div style={itemList}>
            {cart.map((item, index) => (
              <div key={index} style={itemSummaryRow}>
                <div style={{ display: 'flex', gap: '10px', alignItems: 'center' }}>
                  <img src={item.image_url} alt="" style={miniImg} />
                  <div>
                    <p style={itemName}>{item.name}</p>
                    <p style={itemPrice}>RWF {item.price?.toLocaleString()}</p>
                  </div>
                </div>
                <button onClick={() => removeItem(index)} style={removeIconBtn}>Ã—</button>
              </div>
            ))}
          </div>

          <hr style={divider} />
          
          <div style={summaryRow}><span>Subtotal</span><span>RWF {subtotal.toLocaleString()}</span></div>
          <div style={summaryRow}><span>Delivery</span><span>RWF {deliveryFee.toLocaleString()}</span></div>
          <div style={totalRow}><span>Total</span><span>RWF {total.toLocaleString()}</span></div>
          
          <button onClick={handlePlaceOrder} style={orderBtn} disabled={loading}>
            {loading ? "Processing..." : "Place Order Now"}
          </button>
          
          <div style={securityNote}>
            <ShieldCheck size={14} /> <span>100% Secure Transaction</span>
          </div>
        </div>
      </div>
    </div>
  );
};

// --- Sub-Component for UI ---
const CheckCircleIcon = () => (
  <div style={{ width: '18px', height: '18px', backgroundColor: '#008ECC', borderRadius: '50%', display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
    <div style={{ width: '8px', height: '8px', backgroundColor: 'white', borderRadius: '50%' }} />
  </div>
);

// --- STYLES ---
const pageLayout = { display: 'flex', gap: '30px', padding: '40px 8%', backgroundColor: '#f8f9fa', minHeight: '100vh' };
const leftColumn = { flex: 2, display: 'flex', flexDirection: 'column', gap: '20px' };
const rightColumn = { flex: 1.2 };
const sectionHeader = { margin: '0 0 10px 0', color: '#1a1a1a', fontSize: '28px' };
const cardStyle = { backgroundColor: 'white', padding: '25px', borderRadius: '16px', border: '1px solid #eee', boxShadow: '0 2px 10px rgba(0,0,0,0.02)' };
const cardTitle = { display: 'flex', alignItems: 'center', gap: '10px', marginTop: 0, fontSize: '18px', fontWeight: 'bold' };
const formGrid = { display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '15px', marginTop: '20px' };
const inputGroup = { display: 'flex', flexDirection: 'column', gap: '6px' };
const labelStyle = { fontSize: '13px', fontWeight: 'bold', color: '#555' };
const inputStyle = { padding: '12px', borderRadius: '10px', border: '1px solid #ddd', outline: 'none', transition: '0.2s' };

const paymentOptions = { display: 'flex', flexDirection: 'column', gap: '12px', marginTop: '20px' };
const methodStyle = { display: 'flex', alignItems: 'center', gap: '15px', padding: '15px', border: '1px solid #eee', borderRadius: '12px', cursor: 'pointer', transition: '0.2s', backgroundColor: '#fafafa' };
const activeMethod = { ...methodStyle, borderColor: '#008ECC', backgroundColor: '#f0faff' };
const methodName = { margin: 0, fontWeight: 'bold', fontSize: '14px' };
const methodDesc = { margin: 0, fontSize: '12px', color: '#777' };

const summaryCard = { backgroundColor: 'white', padding: '30px', borderRadius: '16px', position: 'sticky', top: '20px', border: '1px solid #eee', boxShadow: '0 4px 20px rgba(0,0,0,0.05)' };
const itemList = { maxHeight: '220px', overflowY: 'auto', marginBottom: '15px' };
const itemSummaryRow = { display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '10px', padding: '10px', backgroundColor: '#f9f9f9', borderRadius: '10px' };
const miniImg = { width: '45px', height: '45px', objectFit: 'cover', borderRadius: '6px' };
const itemName = { margin: 0, fontSize: '13px', fontWeight: '600' };
const itemPrice = { margin: 0, fontSize: '12px', color: '#B12704', fontWeight: 'bold' };
const removeIconBtn = { background: '#eee', border: 'none', borderRadius: '50%', width: '22px', height: '22px', cursor: 'pointer', color: '#999', fontSize: '16px' };

const summaryRow = { display: 'flex', justifyContent: 'space-between', marginBottom: '12px', color: '#555', fontSize: '14px' };
const totalRow = { display: 'flex', justifyContent: 'space-between', fontWeight: '800', fontSize: '22px', color: '#1a1a1a', margin: '15px 0' };
const divider = { border: 'none', borderTop: '1px solid #eee', margin: '20px 0' };
const orderBtn = { width: '100%', padding: '16px', backgroundColor: '#008ECC', color: 'white', border: 'none', borderRadius: '12px', fontWeight: 'bold', cursor: 'pointer', fontSize: '16px', transition: '0.3s' };
const securityNote = { textAlign: 'center', fontSize: '12px', color: '#888', marginTop: '15px', display: 'flex', alignItems: 'center', justifyContent: 'center', gap: '5px' };

const emptyState = { textAlign: 'center', padding: '120px 20px' };
const continueBtn = { marginTop: '20px', padding: '12px 25px', backgroundColor: '#1a1a1a', color: 'white', border: 'none', borderRadius: '10px', cursor: 'pointer', fontWeight: 'bold' };

export default CheckoutPage;
