import React, { useEffect, useState } from 'react';
import { supabase } from '../supabaseClient';

const ProfilePage = ({ user }) => {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchOrders = async () => {
      if (user) {
        const { data } = await supabase.from('orders').select('*').eq('user_id', user.id).order('created_at', { ascending: false });
        setOrders(data || []);
      }
      setLoading(false);
    };
    fetchOrders();
  }, [user]);

  if (!user) return <div style={{ textAlign: 'center', padding: '100px' }}>Please login to view history.</div>;

  return (
    <div style={{ maxWidth: '900px', margin: '50px auto', padding: '0 20px' }}>
      <header style={{ marginBottom: '40px' }}>
        <h2 style={{ fontSize: '30px', margin: 0 }}>Account Dashboard</h2>
        <p style={{ color: '#888' }}>Tracking your MoMo payments and order status.</p>
      </header>

      <section>
        <h3 style={{ fontSize: '18px', borderBottom: '1px solid #eee', paddingBottom: '10px' }}>Recent Orders</h3>
        {loading ? <p>Loading history...</p> : (
          orders.length === 0 ? <p>No orders found.</p> : (
            orders.map(order => (
              <div key={order.id} style={orderRow}>
                <div style={{ display: 'flex', gap: '20px', alignItems: 'center' }}>
                  <div style={orderStatusIcon}>ðŸ“¦</div>
                  <div>
                    <div style={{ fontWeight: 'bold' }}>Order #{order.id.slice(0, 8)}</div>
                    <div style={{ fontSize: '13px', color: '#999' }}>{new Date(order.created_at).toLocaleDateString()}</div>
                  </div>
                </div>
                <div style={{ textAlign: 'right' }}>
                  <div style={{ fontSize: '18px', fontWeight: '800', color: '#27ae60' }}>RWF {order.amount.toLocaleString()}</div>
                  <div style={statusBadge}>SUCCESSFUL</div>
                </div>
              </div>
            ))
          )
        )}
      </section>
    </div>
  );
};

const orderRow = { display: 'flex', justifyContent: 'space-between', alignItems: 'center', padding: '20px', backgroundColor: 'white', borderRadius: '18px', marginBottom: '15px', border: '1px solid #f0f0f0' };
const orderStatusIcon = { width: '45px', height: '45px', borderRadius: '12px', backgroundColor: '#f0f9ff', display: 'flex', alignItems: 'center', justifyContent: 'center', fontSize: '20px' };
const statusBadge = { display: 'inline-block', padding: '3px 8px', borderRadius: '6px', backgroundColor: '#e8f8f0', color: '#27ae60', fontSize: '10px', fontWeight: 'bold', marginTop: '5px' };

export default ProfilePage;
