import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { supabase } from '../supabaseClient';
import { 
  ChevronRight, 
  ShieldCheck, 
  Truck, 
  RotateCcw, 
  Star, 
  Heart,
  Share2,
  Info,
  History // Icon for recently viewed
} from 'lucide-react';

const BRAND_BLUE = '#008ECC';

const ProductDetails = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [product, setProduct] = useState(null);
  const [relatedProducts, setRelatedProducts] = useState([]);
  const [recentlyViewed, setRecentlyViewed] = useState([]); // New State
  const [loading, setLoading] = useState(true);
  const [quantity, setQuantity] = useState(1);

  // 1. Fetch Main Product
  useEffect(() => {
    const fetchProduct = async () => {
      const { data, error } = await supabase
        .from('products')
        .select('*')
        .eq('id', id)
        .single();
      
      if (data) setProduct(data);
      setLoading(false);
    };
    fetchProduct();
  }, [id]);

  // 2. Manage Recently Viewed (LocalStorage)
  useEffect(() => {
    if (product) {
      // Get existing list from LocalStorage
      const existing = JSON.parse(localStorage.getItem('recent_viewed') || '[]');
      
      // Add current product ID to start, remove duplicates, limit to 5
      const updated = [product.id, ...existing.filter(item => item !== product.id)].slice(0, 5);
      
      localStorage.setItem('recent_viewed', JSON.stringify(updated));

      // Fetch the full details for the "Recent" items (excluding current)
      const fetchRecentDetails = async () => {
        const otherIds = updated.filter(recentId => recentId !== product.id);
        if (otherIds.length > 0) {
          const { data } = await supabase
            .from('products')
            .select('*')
            .in('id', otherIds);
          if (data) setRecentlyViewed(data);
        }
      };
      fetchRecentDetails();
    }
  }, [product]);

  // 3. Fetch Related Products
  useEffect(() => {
    const fetchRelated = async () => {
      if (product?.category) {
        const { data } = await supabase
          .from('products')
          .select('*')
          .eq('category', product.category)
          .not('id', 'eq', product.id)
          .limit(4);
        
        if (data) setRelatedProducts(data);
      }
    };
    fetchRelated();
  }, [product]);

  if (loading) return <div style={loader}>Loading product details...</div>;
  if (!product) return <div style={loader}>Product not found.</div>;

  return (
    <div style={pageWrapper}>
      {/* Breadcrumb */}
      <div style={breadcrumb}>
        <span onClick={() => navigate('/')} style={linkText}>Home</span> 
        <ChevronRight size={14} /> 
        <span style={linkText}>{product.category}</span> 
        <ChevronRight size={14} /> 
        <span style={{ color: '#333', fontWeight: '600' }}>{product.name}</span>
      </div>

      <div style={mainContent}>
        {/* Left: Image */}
        <div style={imageContainer}>
          <div style={mainImgWrapper}>
            <img 
              src={product.image_url || 'https://via.placeholder.com/400?text=No+Image'} 
              alt={product.name} 
              style={mainImg} 
            />
          </div>
          <div style={thumbnailRow}>
             <div style={activeThumb}><img src={product.image_url} style={thumbImg} alt="thumb" /></div>
          </div>
        </div>

        {/* Right: Info */}
        <div style={detailsContainer}>
          <div style={headerRow}>
            <h1 style={productTitle}>{product.name}</h1>
            <div style={iconActions}>
              <Heart size={20} style={{ cursor: 'pointer' }} />
              <Share2 size={20} style={{ cursor: 'pointer' }} />
            </div>
          </div>

          <div style={ratingRow}>
            <div style={stars}>
              {[...Array(5)].map((_, i) => <Star key={i} size={16} fill={BRAND_BLUE} color={BRAND_BLUE} />)}
            </div>
            <span style={reviewCount}>(48 Reviews)</span>
          </div>

          <div style={priceContainer}>
            <span style={currentPrice}>RWF {product.price?.toLocaleString()}</span>
            <span style={oldPrice}>RWF {(product.price * 1.2).toLocaleString()}</span>
            <span style={discountTag}>-20%</span>
          </div>

          <hr style={divider} />

          <div style={section}>
            <p style={label}><Info size={14} style={{verticalAlign:'middle', marginRight: '5px'}}/> AI Product Overview</p>
            <div style={aiDescriptionBox}>
              {product.description || "No description available."}
            </div>
          </div>

          <div style={actionBlock}>
            <div style={qtyPicker}>
              <button onClick={() => setQuantity(Math.max(1, quantity - 1))} style={qtyBtn}>-</button>
              <span style={{ width: '40px', textAlign: 'center' }}>{quantity}</span>
              <button onClick={() => setQuantity(quantity + 1)} style={qtyBtn}>+</button>
            </div>
            <button style={buyBtn}>Buy Now</button>
            <button style={cartBtn}>Add to Cart</button>
          </div>

          <div style={perksGrid}>
            <div style={perkItem}><Truck size={18} color={BRAND_BLUE} /> <span>Free Delivery</span></div>
            <div style={perkItem}><ShieldCheck size={18} color={BRAND_BLUE} /> <span>1 Year Warranty</span></div>
            <div style={perkItem}><RotateCcw size={18} color={BRAND_BLUE} /> <span>7 Days Return</span></div>
          </div>
        </div>
      </div>

      {/* Tech Specs */}
      <div style={specSection}>
        <h2 style={specHeading}>Technical Specifications</h2>
        <div style={fullSpecsWrapper}>
          <table style={specTable}>
            <tbody>
              <tr><td style={tdLabel}>Model Name</td><td style={tdValue}>{product.name}</td></tr>
              <tr><td style={tdLabel}>Category</td><td style={tdValue}>{product.category}</td></tr>
              <tr>
                <td style={tdLabel}>Full AI Specs</td>
                <td style={{...tdValue, whiteSpace: 'pre-line', lineHeight: '1.6'}}>
                  {product.description}
                </td>
              </tr>
            </tbody>
          </table>
        </div>
      </div>

      {/* 1. RELATED PRODUCTS SECTION */}
      {relatedProducts.length > 0 && (
        <div style={relatedSection}>
          <h2 style={specHeading}>You May Also Like</h2>
          <div style={relatedGrid}>
            {relatedProducts.map((item) => (
              <div 
                key={item.id} 
                style={relatedCard} 
                onClick={() => { navigate(`/product/${item.id}`); window.scrollTo(0, 0); }}
              >
                <div style={relatedImgWrapper}><img src={item.image_url} style={relatedImg} alt={item.name} /></div>
                <div style={{ padding: '12px' }}>
                  <h4 style={relatedTitle}>{item.name}</h4>
                  <p style={relatedPrice}>RWF {item.price?.toLocaleString()}</p>
                </div>
              </div>
            ))}
          </div>
        </div>
      )}

      {/* 2. RECENTLY VIEWED SECTION */}
      {recentlyViewed.length > 0 && (
        <div style={{...relatedSection, borderTop: '1px solid #eee', marginTop: '40px'}}>
          <h2 style={specHeading}><History size={20} style={{verticalAlign:'middle', marginRight:'10px'}}/> Recently Viewed</h2>
          <div style={relatedGrid}>
            {recentlyViewed.map((item) => (
              <div 
                key={item.id} 
                style={{...relatedCard, backgroundColor: '#fdfdfd'}} 
                onClick={() => { navigate(`/product/${item.id}`); window.scrollTo(0, 0); }}
              >
                <div style={relatedImgWrapper}><img src={item.image_url} style={relatedImg} alt={item.name} /></div>
                <div style={{ padding: '12px' }}>
                  <h4 style={{...relatedTitle, color: '#666'}}>{item.name}</h4>
                  <p style={{...relatedPrice, color: '#888'}}>RWF {item.price?.toLocaleString()}</p>
                </div>
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
};

// --- STYLES ---
const pageWrapper = { padding: '20px 8%', backgroundColor: '#fff', minHeight: '100vh', fontFamily: 'Inter, sans-serif' };
const loader = { padding: '100px', textAlign: 'center', fontSize: '18px', color: '#666' };
const breadcrumb = { display: 'flex', alignItems: 'center', gap: '8px', fontSize: '13px', color: '#888', marginBottom: '30px' };
const linkText = { cursor: 'pointer', transition: 'color 0.2s' };
const mainContent = { display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '60px', marginBottom: '60px' };
const imageContainer = { display: 'flex', flexDirection: 'column', gap: '15px' };
const mainImgWrapper = { border: '1px solid #f0f0f0', borderRadius: '8px', padding: '40px', display: 'flex', justifyContent: 'center', backgroundColor: '#fff' };
const mainImg = { maxWidth: '100%', maxHeight: '400px', objectFit: 'contain' };
const thumbnailRow = { display: 'flex', gap: '10px' };
const activeThumb = { width: '70px', height: '70px', border: `2px solid ${BRAND_BLUE}`, borderRadius: '4px', padding: '5px' };
const thumbImg = { width: '100%', height: '100%', objectFit: 'contain' };
const detailsContainer = { display: 'flex', flexDirection: 'column' };
const headerRow = { display: 'flex', justifyContent: 'space-between', alignItems: 'flex-start' };
const productTitle = { fontSize: '28px', fontWeight: '700', margin: '0 0 10px 0', color: '#222' };
const iconActions = { display: 'flex', gap: '15px', color: '#999' };
const ratingRow = { display: 'flex', alignItems: 'center', gap: '10px', marginBottom: '20px' };
const stars = { display: 'flex' };
const reviewCount = { fontSize: '13px', color: '#888' };
const priceContainer = { display: 'flex', alignItems: 'center', gap: '15px', marginBottom: '10px' };
const currentPrice = { fontSize: '32px', fontWeight: '800', color: BRAND_BLUE };
const oldPrice = { fontSize: '18px', textDecoration: 'line-through', color: '#bbb' };
const discountTag = { backgroundColor: '#FFEDED', color: '#FF4D4D', padding: '2px 8px', borderRadius: '4px', fontSize: '12px', fontWeight: '700' };
const divider = { border: 'none', borderTop: '1px solid #eee', margin: '25px 0' };
const section = { marginBottom: '25px' };
const label = { fontSize: '14px', fontWeight: '600', marginBottom: '12px', color: '#555' };
const aiDescriptionBox = { fontSize: '15px', color: '#555', lineHeight: '1.6', whiteSpace: 'pre-line', backgroundColor: '#fcfdff', padding: '15px', borderRadius: '8px', borderLeft: `4px solid ${BRAND_BLUE}` };
