const relatedCard = { border: '1px solid #eee', borderRadius: '8px', cursor: 'pointer', overflow: 'hidden', transition: 'transform 0.2s', backgroundColor: '#fff' };
const relatedImgWrapper = { height: '150px', display: 'flex', alignItems: 'center', justifyContent: 'center', padding: '15px', backgroundColor: '#fafafa' };
const relatedImg = { maxWidth: '100%', maxHeight: '100%', objectFit: 'contain' };
