import React, { useEffect, useState } from 'react';
import { supabase } from '../supabaseClient';
// 1. Import the professional icons
import { 
  Smartphone, 
  Laptop, 
  Watch, 
  Headphones, 
  Gamepad2, 
  Zap, 
  Plus, 
  ArrowRight,
  ShieldCheck,
  TrendingUp
} from 'lucide-react';

const HomePage = ({ addToCart }) => {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    const fetchProducts = async () => {
      const { data } = await supabase.from('products').select('*');
      setProducts(data || []);
    };
    fetchProducts();
  }, []);

  // 2. Map icons to your categories
  const categories = [
    { name: "Smartphones", icon: <Smartphone size={28} color="#008ECC" /> },
    { name: "Laptops", icon: <Laptop size={28} color="#008ECC" /> },
    { name: "Smart Watches", icon: <Watch size={28} color="#008ECC" /> },
    { name: "Audio", icon: <Headphones size={28} color="#008ECC" /> },
    { name: "Gaming", icon: <Gamepad2 size={28} color="#008ECC" /> },
    { name: "Accessories", icon: <Zap size={28} color="#008ECC" /> }
  ];

  return (
    <div style={{ backgroundColor: '#f4f7f8', minHeight: '100vh', paddingBottom: '50px' }}>
      
      {/* 1. Category Quick Links with Real Icons */}
      <div style={categoryBar}>
        {categories.map(cat => (
          <div key={cat.name} style={categoryItem}>
            <div style={categoryIconWrapper}>{cat.icon}</div>
            <span style={{ fontSize: '12px', fontWeight: '600', marginTop: '8px' }}>{cat.name}</span>
          </div>
        ))}
      </div>

      {/* 2. Hero Deals Slider */}
      <div style={dealHero}>
        <div style={dealContent}>
          <span style={badge}>Limited Time Offer</span>
          <h2 style={{ fontSize: '32px', margin: '10px 0' }}>Latest Galaxy S24 Ultra</h2>
          <p>Get up to 15% discount on all pre-orders this week at MegaMart Rwanda.</p>
          <button style={dealBtn}>Shop Now</button>
        </div>
        <div style={dealImage}>
          <img 
            src="https://images.unsplash.com/photo-1610641818989-c2051b5e2cfd?auto=format&fit=crop&q=80&w=400" 
            alt="Special Deal" 
            style={{ borderRadius: '15px', maxWidth: '100%' }} 
          />
        </div>
      </div>

      {/* 3. Grouped Product Listing */}
      {categories.map(cat => {
        const filteredProducts = products.filter(p => p.category === cat.name);
        if (filteredProducts.length === 0) return null;

        return (
          <div key={cat.name} style={{ padding: '0 50px', marginBottom: '40px' }}>
            <div style={sectionHeader}>
              <h3 style={{ margin: 0, display: 'flex', alignItems: 'center', gap: '10px' }}>
                Top {cat.name} Brands
              </h3>
              <span style={viewAllStyle}>View all <ArrowRight size={16} /></span>
            </div>
            
            <div style={horizontalGrid}>
              {filteredProducts.map(product => (
                <div key={product.id} style={amazonStyleCard}>
                  <div style={imgContainer}>
                    <img src={product.image_url} alt={product.name} style={prodImg} />
                    <div style={saveBadge}><TrendingUp size={12} /> Save 10%</div>
                  </div>
                  <div style={{ padding: '15px' }}>
                    <h4 style={prodTitle}>{product.name}</h4>
                    <div style={{ color: '#666', fontSize: '12px', marginBottom: '8px', display: 'flex', alignItems: 'center', gap: '4px' }}>
                      <ShieldCheck size={14} color="#27ae60" /> Official Warranty
                    </div>
                    <div style={priceRow}>
                      <span style={priceLarge}>RWF {product.price.toLocaleString()}</span>
                      <button onClick={() => addToCart(product)} style={cartBtn}>
                        <Plus size={20} />
                      </button>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </div>
        );
      })}

      {/* 4. Recommended Section */}
      <div style={{ padding: '40px 50px', backgroundColor: 'white', marginTop: '40px' }}>
        <h3 style={{ marginBottom: '20px' }}>Recommended for You</h3>
        <div style={{ display: 'flex', gap: '20px', overflowX: 'auto', paddingBottom: '20px' }}>
          {products.slice(0, 6).map(product => (
            <div key={product.id} style={{ minWidth: '150px', textAlign: 'center' }}>
               <img src={product.image_url} alt={product.name} style={{ width: '100px', height: '100px', objectFit: 'contain' }} />
               <p style={{ fontSize: '12px', fontWeight: 'bold', marginTop: '10px' }}>{product.name}</p>
               <span style={{ color: '#008ECC', fontSize: '14px' }}>RWF {product.price.toLocaleString()}</span>
            </div>
          ))}
        </div>
      </div>

      {/* 5. Official Footer */}
      <footer style={footerStyle}>
        <div style={footerGrid}>
          <div>
            <h4 style={{ color: 'white' }}>Get to Know Us</h4>
            <p style={footerLink}>About MegaMart</p>
            <p style={footerLink}>Careers</p>
            <p style={footerLink}>Sustainability</p>
          </div>
          <div>
            <h4 style={{ color: 'white' }}>Payment & Shipping</h4>
            <p style={footerLink}>MoMo Payment Guide</p>
            <p style={footerLink}>Shipping Rates</p>
            <p style={footerLink}>Returns & Replacements</p>
          </div>
          <div>
            <h4 style={{ color: 'white' }}>Help</h4>
            <p style={footerLink}>Your Account</p>
            <p style={footerLink}>Customer Service</p>
            <p style={footerLink}>Contact Us</p>
          </div>
        </div>
        <div style={{ textAlign: 'center', marginTop: '40px', borderTop: '1px solid #3a4553', paddingTop: '20px', fontSize: '12px' }}>
          Â© 2025 MegaMart Rwanda. Built for Excellence.
        </div>
      </footer>
    </div>
  );
};

