import React, { useEffect, useState } from 'react';
import { supabase } from '../supabaseClient';

const HomePage = () => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchProducts = async () => {
      // This pulls your data from the Supabase table you showed in your screenshot
      const { data, error } = await supabase.from('products').select('*');
      if (error) {
        console.error("Error fetching products:", error);
      } else {
        setProducts(data);
      }
      setLoading(false);
    };
    fetchProducts();
  }, []);

  if (loading) return <div className="text-center p-20">Loading MegaMart Inventory...</div>;

  return (
    <div style={{ backgroundColor: '#f9f9f9', minHeight: '100vh', padding: '20px' }}>
      <div style={{ maxWidth: '1200px', margin: '0 auto' }}>
        <h2 style={{ fontSize: '24px', fontWeight: 'bold', borderBottom: '3px solid #008ECC', width: 'fit-content', paddingBottom: '5px', marginBottom: '30px' }}>
          Grab the best deal on <span style={{ color: '#008ECC' }}>Smartphones</span>
        </h2>

        {/* Product Grid */}
        <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fill, minmax(200px, 1fr))', gap: '20px' }}>
          {products.map((product) => (
            <div key={product.id} style={{ backgroundColor: 'white', borderRadius: '15px', padding: '15px', position: 'relative', boxShadow: '0 2px 10px rgba(0,0,0,0.05)', border: '1px solid #eee' }}>
              
              {/* Blue Ribbon (from your design) */}
              <div style={{ position: 'absolute', top: '0', right: '0', backgroundColor: '#008ECC', color: 'white', fontSize: '10px', fontWeight: 'bold', padding: '10px 15px', borderBottomLeftRadius: '20px' }}>
                10% OFF
              </div>

              <img 
                src={product.image_url || 'https://via.placeholder.com/150'} 
                alt={product.name} 
                style={{ width: '100%', height: '150px', objectFit: 'contain', marginBottom: '10px' }} 
              />
              
              <h3 style={{ fontSize: '14px', margin: '10px 0', height: '40px', overflow: 'hidden' }}>{product.name}</h3>
              <p style={{ fontWeight: 'bold', fontSize: '18px', color: '#333' }}>RWF {product.price?.toLocaleString()}</p>
              
              <button style={{ width: '100%', marginTop: '15px', backgroundColor: '#008ECC', color: 'white', border: 'none', padding: '10px', borderRadius: '8px', cursor: 'pointer', fontWeight: 'bold' }}>
                Add to Cart
              </button>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default HomePage;
