import React from 'react';
import { useNavigate } from 'react-router-dom';

const SuccessPage = () => {
  const navigate = useNavigate();

  return (
    <div style={{ 
      height: '80vh', 
      display: 'flex', 
      flexDirection: 'column', 
      alignItems: 'center', 
      justifyContent: 'center', 
      fontFamily: 'sans-serif' 
    }}>
      {/* Big Green Checkmark */}
      <div style={{ 
        width: '80px', 
        height: '80px', 
        backgroundColor: '#4BB543', 
        borderRadius: '50%', 
        display: 'flex', 
        alignItems: 'center', 
        justifyContent: 'center', 
        color: 'white', 
        fontSize: '40px',
        marginBottom: '20px',
        boxShadow: '0 4px 15px rgba(75, 181, 67, 0.3)'
      }}>
        âœ“
      </div>

      <h1 style={{ color: '#333', marginBottom: '10px' }}>Payment Successful!</h1>
      <p style={{ color: '#666', textAlign: 'center', maxWidth: '300px', lineHeight: '1.5' }}>
        Thank you for shopping with <strong>MegaMart Rwanda</strong>. Your order is being processed and will be delivered shortly.
      </p>

      <button 
        onClick={() => navigate('/')}
        style={{ 
          marginTop: '30px', 
          backgroundColor: '#008ECC', 
          color: 'white', 
          border: 'none', 
          padding: '12px 30px', 
          borderRadius: '8px', 
          fontWeight: 'bold', 
          cursor: 'pointer' 
        }}
      >
        Back to Shopping
      </button>
    </div>
  );
};

export default SuccessPage;
