import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { supabase } from '../supabaseClient';
import { 
  ChevronRight, 
  ShieldCheck, 
  Truck, 
  RotateCcw, 
  Star, 
  Heart,
  Share2
} from 'lucide-react';

const BRAND_BLUE = '#008ECC';

const ProductDetails = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [product, setProduct] = useState(null);
  const [loading, setLoading] = useState(true);
  const [selectedColor, setSelectedColor] = useState('Black');
  const [selectedSize, setSelectedSize] = useState('Standard');
  const [quantity, setQuantity] = useState(1);

  useEffect(() => {
    const fetchProduct = async () => {
      const { data, error } = await supabase
        .from('products')
        .select('*')
        .eq('id', id)
        .single();
      
      if (data) setProduct(data);
      setLoading(false);
    };
    fetchProduct();
  }, [id]);

  if (loading) return <div style={loader}>Loading product details...</div>;
  if (!product) return <div style={loader}>Product not found.</div>;

  return (
    <div style={pageWrapper}>
      {/* Breadcrumb Navigation */}
      <div style={breadcrumb}>
        <span onClick={() => navigate('/')} style={linkText}>Home</span> 
        <ChevronRight size={14} /> 
        <span style={linkText}>{product.category}</span> 
        <ChevronRight size={14} /> 
        <span style={{ color: '#333', fontWeight: '600' }}>{product.name}</span>
      </div>

      <div style={mainContent}>
        {/* Left: Image Gallery Container */}
        <div style={imageContainer}>
          <div style={mainImgWrapper}>
            <img src={product.image_url} alt={product.name} style={mainImg} />
          </div>
          {/* Placeholder for secondary thumbnails */}
          <div style={thumbnailRow}>
             <div style={activeThumb}><img src={product.image_url} style={thumbImg} /></div>
             <div style={inactiveThumb}></div>
             <div style={inactiveThumb}></div>
          </div>
        </div>

        {/* Right: Product Info */}
        <div style={detailsContainer}>
          <div style={headerRow}>
            <h1 style={productTitle}>{product.name}</h1>
            <div style={iconActions}>
              <Heart size={20} style={{ cursor: 'pointer' }} />
              <Share2 size={20} style={{ cursor: 'pointer' }} />
            </div>
          </div>

          <div style={ratingRow}>
            <div style={stars}>
              {[...Array(5)].map((_, i) => <Star key={i} size={16} fill={BRAND_BLUE} color={BRAND_BLUE} />)}
            </div>
            <span style={reviewCount}>(48 Reviews)</span>
          </div>

          <div style={priceContainer}>
            <span style={currentPrice}>RWF {product.price?.toLocaleString()}</span>
            <span style={oldPrice}>RWF {(product.price * 1.2).toLocaleString()}</span>
            <span style={discountTag}>-20%</span>
          </div>

          <hr style={divider} />

          {/* Color Selection */}
          <div style={section}>
            <p style={label}>Select Color: <b>{selectedColor}</b></p>
            <div style={flexGap}>
              {['Black', 'White', 'Blue'].map((color) => (
                <div 
                  key={color}
                  onClick={() => setSelectedColor(color)}
                  style={{
                    ...colorBox, 
                    borderColor: selectedColor === color ? BRAND_BLUE : '#eee',
                    backgroundColor: color.toLowerCase() === 'white' ? '#fff' : color.toLowerCase()
                  }}
                />
              ))}
            </div>
          </div>

          {/* Size/Type Selection */}
          <div style={section}>
            <p style={label}>Size / Variation:</p>
            <div style={flexGap}>
              {['Standard', 'Pro', 'Max'].map((size) => (
                <button 
                  key={size}
                  onClick={() => setSelectedSize(size)}
                  style={{
                    ...variantBtn,
                    borderColor: selectedSize === size ? BRAND_BLUE : '#ddd',
                    color: selectedSize === size ? BRAND_BLUE : '#333',
                    backgroundColor: selectedSize === size ? '#f0faff' : '#fff'
                  }}
                >
                  {size}
                </button>
              ))}
            </div>
          </div>

          {/* Quantity and Actions */}
          <div style={actionBlock}>
            <div style={qtyPicker}>
              <button onClick={() => setQuantity(Math.max(1, quantity - 1))} style={qtyBtn}>-</button>
              <span style={{ width: '40px', textAlign: 'center' }}>{quantity}</span>
              <button onClick={() => setQuantity(quantity + 1)} style={qtyBtn}>+</button>
            </div>
            <button style={buyBtn}>Buy Now</button>
            <button style={cartBtn}>Add to Cart</button>
          </div>

          {/* Service Perks */}
          <div style={perksGrid}>
            <div style={perkItem}><Truck size={18} color={BRAND_BLUE} /> <span>Free Shipping</span></div>
            <div style={perkItem}><ShieldCheck size={18} color={BRAND_BLUE} /> <span>1 Year Warranty</span></div>
            <div style={perkItem}><RotateCcw size={18} color={BRAND_BLUE} /> <span>7 Days Return</span></div>
          </div>
        </div>
      </div>

      {/* Specifications Table */}
      <div style={specSection}>
        <h2 style={specHeading}>Product Specifications</h2>
        <table style={specTable}>
          <tbody>
            <tr><td style={tdLabel}>Condition</td><td style={tdValue}>Brand New, Original</td></tr>
            <tr><td style={tdLabel}>Brand</td><td style={tdValue}>MegaMart Certified</td></tr>
            <tr><td style={tdLabel}>Category</td><td style={tdValue}>{product.category}</td></tr>
            <tr><td style={tdLabel}>Stock Status</td><td style={tdValue}>In Stock (Rwanda Warehouse)</td></tr>
          </tbody>
        </table>
      </div>
    </div>
  );
};

// --- STYLES ---
const pageWrapper = { padding: '20px 8%', backgroundColor: '#fff', minHeight: '100vh' };
const loader = { padding: '100px', textAlign: 'center', fontSize: '18px', color: '#666' };
const breadcrumb = { display: 'flex', alignItems: 'center', gap: '8px', fontSize: '13px', color: '#888', marginBottom: '30px' };
const linkText = { cursor: 'pointer', transition: 'color 0.2s' };

const mainContent = { display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '60px', marginBottom: '60px' };
const imageContainer = { display: 'flex', flexDirection: 'column', gap: '15px' };
const mainImgWrapper = { border: '1px solid #f0f0f0', borderRadius: '8px', padding: '40px', display: 'flex', justifyContent: 'center', backgroundColor: '#fff' };
const mainImg = { maxWidth: '100%', maxHeight: '400px', objectFit: 'contain' };
const thumbnailRow = { display: 'flex', gap: '10px' };
const activeThumb = { width: '70px', height: '70px', border: `2px solid ${BRAND_BLUE}`, borderRadius: '4px', padding: '5px' };
const inactiveThumb = { width: '70px', height: '70px', border: '1px solid #eee', borderRadius: '4px', backgroundColor: '#fafafa' };
const thumbImg = { width: '100%', height: '100%', objectFit: 'contain' };

const detailsContainer = { display: 'flex', flexDirection: 'column' };
const headerRow = { display: 'flex', justifyContent: 'space-between', alignItems: 'flex-start' };
const productTitle = { fontSize: '28px', fontWeight: '700', margin: '0 0 10px 0', color: '#222' };
const iconActions = { display: 'flex', gap: '15px', color: '#999' };
const ratingRow = { display: 'flex', alignItems: 'center', gap: '10px', marginBottom: '20px' };
const stars = { display: 'flex' };
const reviewCount = { fontSize: '13px', color: '#888' };

const priceContainer = { display: 'flex', alignItems: 'center', gap: '15px', marginBottom: '10px' };
const currentPrice = { fontSize: '32px', fontWeight: '800', color: BRAND_BLUE };
const oldPrice = { fontSize: '18px', textDecoration: 'line-through', color: '#bbb' };
const discountTag = { backgroundColor: '#FFEDED', color: '#FF4D4D', padding: '2px 8px', borderRadius: '4px', fontSize: '12px', fontWeight: '700' };

const divider = { border: 'none', borderTop: '1px solid #eee', margin: '25px 0' };
const section = { marginBottom: '25px' };
const label = { fontSize: '14px', fontWeight: '600', marginBottom: '12px', color: '#555' };
const flexGap = { display: 'flex', gap: '12px' };
const colorBox = { width: '35px', height: '35px', borderRadius: '50%', cursor: 'pointer', border: '2px solid transparent', boxShadow: 'inset 0 0 0 2px #fff' };
const variantBtn = { padding: '10px 20px', border: '1px solid', borderRadius: '4px', cursor: 'pointer', fontWeight: '600', fontSize: '14px' };

const actionBlock = { display: 'flex', gap: '15px', marginTop: '10px' };
const qtyPicker = { display: 'flex', alignItems: 'center', border: '1px solid #ddd', borderRadius: '4px', overflow: 'hidden' };
const qtyBtn = { width: '35px', height: '45px', border: 'none', background: '#f9f9f9', cursor: 'pointer', fontSize: '18px' };
const buyBtn = { flex: 1.5, backgroundColor: BRAND_BLUE, color: '#fff', border: 'none', borderRadius: '4px', fontWeight: '700', fontSize: '16px', cursor: 'pointer' };
const cartBtn = { flex: 1, backgroundColor: '#fff', color: BRAND_BLUE, border: `1px solid ${BRAND_BLUE}`, borderRadius: '4px', fontWeight: '700', cursor: 'pointer' };

const perksGrid = { display: 'grid', gridTemplateColumns: 'repeat(3, 1fr)', gap: '10px', marginTop: '40px', paddingTop: '20px', borderTop: '1px solid #eee' };
const perkItem = { display: 'flex', alignItems: 'center', gap: '8px', fontSize: '12px', color: '#666' };

const specSection = { borderTop: '2px solid #f0f0f0', paddingTop: '40px' };
const specHeading = { fontSize: '20px', fontWeight: '700', marginBottom: '25px' };
const specTable = { width: '100%', borderCollapse: 'collapse' };
const tdLabel = { padding: '15px', backgroundColor: '#fcfcfc', border: '1px solid #eee', width: '250px', fontWeight: '600', color: '#555' };
const tdValue = { padding: '15px', border: '1px solid #eee', color: '#333' };

export default ProductDetails;
