import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { supabase } from '../supabaseClient'; // Ensure this path is correct
import { 
  ShieldCheck, CheckCircle, 
  Globe, CreditCard, Wallet, Smartphone 
} from 'lucide-react';

const CheckoutPage = ({ cart, setCart, user }) => {
  const navigate = useNavigate();
  const [step, setStep] = useState(1);
  const [loading, setLoading] = useState(false);
  const [selectedCountry, setSelectedCountry] = useState('Rwanda');
  const [currency, setCurrency] = useState({ code: 'RWF', symbol: 'RWF', rate: 1 });
  const [paymentMethod, setPaymentMethod] = useState('card');

  // Form States
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    address: ''
  });

  const currencies = [
    { code: 'RWF', symbol: 'RWF', rate: 1 },
    { code: 'USD', symbol: '$', rate: 0.00078 },
    { code: 'EUR', symbol: '€', rate: 0.00072 }
  ];

  const subtotal = cart.reduce((acc, item) => acc + (item.price || 0), 0);
  const delivery = selectedCountry === 'Rwanda' ? 2000 : 15000;
  const totalInRWF = subtotal + delivery;

  const formatPrice = (amount) => {
    const converted = (amount * currency.rate).toLocaleString(undefined, {
      minimumFractionDigits: currency.code === 'RWF' ? 0 : 2,
      maximumFractionDigits: currency.code === 'RWF' ? 0 : 2
    });
    return `${currency.symbol} ${converted}`;
  };

  // --- SAVE ORDER TO SUPABASE ---
  const handlePayment = async () => {
    if (!formData.firstName || !formData.address) {
      alert("Please fill in your name and address.");
      return;
    }

    setLoading(true);
    try {
      const { error } = await supabase
        .from('orders')
        .insert([
          {
            user_email: user?.email || 'Guest',
            items: cart,
            total_amount: totalInRWF * currency.rate,
            currency: currency.code,
            shipping_address: `${formData.firstName} ${formData.lastName}, ${formData.address}, ${selectedCountry}`,
            status: 'paid',
            payment_method: paymentMethod
          }
        ]);

      if (error) throw error;

      // Success logic
      setCart([]);
      setStep(3);
    } catch (error) {
      alert("Error saving order: " + error.message);
    } finally {
      setLoading(false);
    }
  };

  if (step === 3) {
    return (
      <div style={successContainer}>
        <CheckCircle size={80} color="#28a745" />
        <h1>Order Confirmed!</h1>
        <p>Thank you, {formData.firstName}! Your order is shipping to {selectedCountry}.</p>
        <button onClick={() => navigate('/')} style={continueBtn}>Continue Shopping</button>
      </div>
    );
  }

  return (
    <div style={pageWrapper} className="checkout-container">
      <div style={mainContent}>
        {/* 1. SHIPPING */}
        <div style={sectionCard}>
          <h2 style={sectionHeader}><Globe size={20} /> 1. Shipping Details</h2>
          <label style={labelStyle}>Country</label>
          <select style={selectInput} value={selectedCountry} onChange={(e) => setSelectedCountry(e.target.value)}>
            <option value="Rwanda">Rwanda</option>
            <option value="United States">United States</option>
            <option value="United Kingdom">United Kingdom</option>
          </select>
          
          <div className="mobile-stack" style={formRow}>
            <div style={{flex: 1}}>
              <label style={labelStyle}>First Name</label>
              <input 
                style={inputFull} 
                placeholder="John" 
                value={formData.firstName}
                onChange={(e) => setFormData({...formData, firstName: e.target.value})}
              />
            </div>
            <div style={{flex: 1}}>
              <label style={labelStyle}>Last Name</label>
              <input 
                style={inputFull} 
                placeholder="Doe" 
                value={formData.lastName}
                onChange={(e) => setFormData({...formData, lastName: e.target.value})}
              />
            </div>
          </div>
          
          <label style={labelStyle}>Street Address</label>
          <input 
            style={inputFull} 
            placeholder="House number and street name" 
            value={formData.address}
            onChange={(e) => setFormData({...formData, address: e.target.value})}
          />
        </div>

        {/* 2. PAYMENT */}
        <div style={sectionCard}>
          <h2 style={sectionHeader}><CreditCard size={20} /> 2. Payment & Currency</h2>
          
          <label style={labelStyle}>Select Currency</label>
          <div style={currencyGrid}>
            {currencies.map(c => (
              <button key={c.code} onClick={() => setCurrency(c)} style={currency.code === c.code ? activeCurrencyBtn : currencyBtn}>
                {c.code}
              </button>
            ))}
          </div>

          <label style={labelStyle}>Payment Method</label>
          <div style={paymentGrid}>
            <div style={paymentMethod === 'card' ? activePayment : paymentOption} onClick={() => setPaymentMethod('card')}>
              <CreditCard size={18} /> Visa / MasterCard
            </div>
            <div style={paymentMethod === 'paypal' ? activePayment : paymentOption} onClick={() => setPaymentMethod('paypal')}>
              <Wallet size={18} /> PayPal
            </div>
            {selectedCountry === 'Rwanda' && (
              <div style={paymentMethod === 'momo' ? activePayment : paymentOption} onClick={() => setPaymentMethod('momo')}>
                <Smartphone size={18} /> Mobile Money
              </div>
            )}
          </div>
        </div>
      </div>

      {/* SIDEBAR SUMMARY */}
      <div style={sidebar}>
        <div style={summaryCard} className="summary-sticky">
          <h3 style={{marginTop: 0}}>Order Summary</h3>
          <div style={summaryRow}><span>Items ({cart.length})</span><span>{formatPrice(subtotal)}</span></div>
          <div style={summaryRow}><span>Shipping</span><span>{formatPrice(delivery)}</span></div>
          <hr style={divider} />
          <div style={totalRow}><span>Total</span><span>{formatPrice(totalInRWF)}</span></div>
          
          <button 
            style={confirmBtn} 
            onClick={handlePayment} 
            disabled={loading || cart.length === 0}
          >
            {loading ? "Processing..." : `Confirm & Pay ${currency.code}`}
          </button>
          
          <div style={protectionBox}>
            <ShieldCheck size={16} color="#0053a0" />
            <span style={{fontSize: '11px'}}>Protected by MegaMart Secure Checkout</span>
          </div>
        </div>
      </div>
    </div>
  );
};

