import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { 
  ShieldCheck, ChevronDown, CheckCircle, 
  Star, Globe, CreditCard, Wallet, Smartphone 
} from 'lucide-react';

const CheckoutPage = ({ cart, setCart }) => {
  const navigate = useNavigate();
  const [step, setStep] = useState(1);
  const [selectedCountry, setSelectedCountry] = useState('Rwanda');
  const [currency, setCurrency] = useState({ code: 'RWF', symbol: 'RWF', rate: 1 });
  const [paymentMethod, setPaymentMethod] = useState('card');

  const currencies = [
    { code: 'RWF', symbol: 'RWF', rate: 1 },
    { code: 'USD', symbol: '$', rate: 0.00078 },
    { code: 'EUR', symbol: '€', rate: 0.00072 }
  ];

  const subtotal = cart.reduce((acc, item) => acc + (item.price || 0), 0);
  const delivery = selectedCountry === 'Rwanda' ? 2000 : 15000;

  const formatPrice = (amount) => {
    const converted = (amount * currency.rate).toLocaleString(undefined, {
      minimumFractionDigits: currency.code === 'RWF' ? 0 : 2,
      maximumFractionDigits: currency.code === 'RWF' ? 0 : 2
    });
    return `${currency.symbol} ${converted}`;
  };

  if (step === 3) {
    return (
      <div style={successContainer}>
        <CheckCircle size={80} color="#28a745" />
        <h1>Order Confirmed!</h1>
        <p>Shipping to {selectedCountry}. Thank you for shopping with MegaMart.</p>
        <button onClick={() => navigate('/')} style={continueBtn}>Continue Shopping</button>
      </div>
    );
  }

  return (
    <div style={pageWrapper}>
      <div style={mainContent}>
        <div style={sectionCard}>
          <h2 style={sectionHeader}><Globe size={20} /> 1. Shipping Details</h2>
          <select style={selectInput} value={selectedCountry} onChange={(e) => setSelectedCountry(e.target.value)}>
            <option value="Rwanda">Rwanda</option>
            <option value="United States">United States</option>
            <option value="United Kingdom">United Kingdom</option>
          </select>
          <div style={formRow}>
            <input style={inputHalf} placeholder="First name" />
            <input style={inputHalf} placeholder="Last name" />
          </div>
          <input style={inputFull} placeholder="Street address" />
        </div>

        <div style={sectionCard}>
          <h2 style={sectionHeader}><CreditCard size={20} /> 2. Payment & Currency</h2>
          <div style={currencyGrid}>
            {currencies.map(c => (
              <button key={c.code} onClick={() => setCurrency(c)} style={currency.code === c.code ? activeCurrencyBtn : currencyBtn}>
                {c.code}
              </button>
            ))}
          </div>

          <div style={paymentGrid}>
            <div style={paymentMethod === 'card' ? activePayment : paymentOption} onClick={() => setPaymentMethod('card')}>
              <CreditCard size={18} /> Visa / MasterCard
            </div>
            <div style={paymentMethod === 'paypal' ? activePayment : paymentOption} onClick={() => setPaymentMethod('paypal')}>
              <Wallet size={18} /> PayPal
            </div>
            {selectedCountry === 'Rwanda' && (
              <div style={paymentMethod === 'momo' ? activePayment : paymentOption} onClick={() => setPaymentMethod('momo')}>
                <Smartphone size={18} /> Mobile Money
              </div>
            )}
          </div>
        </div>
      </div>

      <div style={sidebar}>
        <div style={summaryCard}>
          <h3 style={{marginTop: 0}}>Order Summary</h3>
          <div style={summaryRow}><span>Items</span><span>{formatPrice(subtotal)}</span></div>
          <div style={summaryRow}><span>Shipping</span><span>{formatPrice(delivery)}</span></div>
          <hr style={divider} />
          <div style={totalRow}><span>Total</span><span>{formatPrice(subtotal + delivery)}</span></div>
          <button style={confirmBtn} onClick={() => setStep(3)}>Confirm & Pay</button>
          <div style={protectionBox}>
            <ShieldCheck size={16} color="#0053a0" />
            <span>MegaMart Money Back Guarantee</span>
          </div>
        </div>
      </div>
    </div>
  );
};

// --- STYLES (Fixed conflicts) ---
const pageWrapper = { display: 'flex', gap: '30px', padding: '40px 10%', backgroundColor: '#f8f9fa', minHeight: '100vh' };
const mainContent = { flex: 2 };
const sidebar = { flex: 1 };
const sectionCard = { backgroundColor: 'white', padding: '25px', borderRadius: '12px', border: '1px solid #eee', marginBottom: '20px' };
const sectionHeader = { fontSize: '20px', display: 'flex', alignItems: 'center', gap: '10px', marginBottom: '20px' };
const formRow = { display: 'flex', gap: '10px', marginBottom: '15px' };
const selectInput = { width: '100%', padding: '12px', borderRadius: '8px', border: '1px solid #ddd', marginBottom: '15px' };
const inputFull = { width: '100%', padding: '12px', borderRadius: '8px', border: '1px solid #ddd' };
const inputHalf = { width: '50%', padding: '12px', borderRadius: '8px', border: '1px solid #ddd' };
const currencyGrid = { display: 'flex', gap: '10px', marginBottom: '20px' };
const currencyBtn = { padding: '8px 15px', borderRadius: '8px', border: '1px solid #ddd', backgroundColor: 'white', cursor: 'pointer' };
const activeCurrencyBtn = { ...currencyBtn, borderColor: '#005efd', backgroundColor: '#f0faff' };
const paymentGrid = { display: 'flex', flexDirection: 'column', gap: '10px' };
const paymentOption = { display: 'flex', alignItems: 'center', gap: '12px', padding: '15px', borderRadius: '10px', cursor: 'pointer', borderWidth: '1px', borderStyle: 'solid', borderColor: '#ddd' };
const activePayment = { ...paymentOption, borderColor: '#005efd', backgroundColor: '#f0faff', borderWidth: '2px' };
const summaryCard = { backgroundColor: 'white', padding: '20px', borderRadius: '12px', border: '1px solid #eee', position: 'sticky', top: '20px' };
const summaryRow = { display: 'flex', justifyContent: 'space-between', marginBottom: '10px', fontSize: '14px' };
const divider = { border: 'none', borderTop: '1px solid #eee', margin: '15px 0' };
const totalRow = { display: 'flex', justifyContent: 'space-between', fontWeight: 'bold', fontSize: '18px' };
const confirmBtn = { width: '100%', padding: '15px', backgroundColor: '#005efd', color: 'white', border: 'none', borderRadius: '30px', fontWeight: 'bold', marginTop: '20px', cursor: 'pointer' };
const protectionBox = { display: 'flex', gap: '8px', marginTop: '15px', fontSize: '12px', color: '#666', alignItems: 'center' };
const successContainer = { textAlign: 'center', padding: '100px 20px' };
const continueBtn = { marginTop: '20px', padding: '10px 20px', backgroundColor: '#1a1a1a', color: 'white', border: 'none', borderRadius: '8px', cursor: 'pointer' };

export default CheckoutPage;
