import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { supabase } from '../supabaseClient';

const ProductDetailPage = ({ addToCart }) => {
  const { productId } = useParams();
  const [product, setProduct] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchProduct = async () => {
      setLoading(true);
      const { data, error } = await supabase
        .from('products')
        .select('*')
        .eq('id', productId)
        .single(); // Get exactly one item

      if (!error) setProduct(data);
      setLoading(false);
    };
    fetchProduct();
  }, [productId]);

  if (loading) return <div style={{ padding: '50px', textAlign: 'center' }}>Loading...</div>;
  if (!product) return <div style={{ padding: '50px', textAlign: 'center' }}>Product not found.</div>;

  return (
    <div style={{ padding: '20px', display: 'flex', flexWrap: 'wrap', gap: '30px', maxWidth: '1000px', margin: 'auto' }}>
      <img src={product.image_url} alt={product.name} style={{ width: '100%', maxWidth: '400px', borderRadius: '10px' }} />
      
      <div style={{ flex: 1, minWidth: '300px' }}>
        <h1 style={{ fontSize: '24px' }}>{product.name}</h1>
        <p style={{ color: '#ff4d4f', fontSize: '28px', fontWeight: 'bold' }}>RWF {product.price}</p>
        {product.old_price && <p style={{ textDecoration: 'line-through', color: '#888' }}>RWF {product.old_price}</p>}
        
        <hr style={{ margin: '20px 0', border: '0.5px solid #eee' }} />
        <p style={{ lineHeight: '1.6', color: '#444' }}>{product.description}</p>
        
        <button 
          onClick={() => addToCart(product)} 
          style={{ backgroundColor: '#ff4d4f', color: 'white', border: 'none', padding: '15px 30px', borderRadius: '5px', cursor: 'pointer', marginTop: '20px', width: '100%' }}
        >
          Add to Cart
        </button>
      </div>
    </div>
  );
};

export default ProductDetailPage;
