const selectInput = { padding: '15px', border: '1px solid #cbcbcb', borderRadius: '8px', width: '100%', backgroundColor: '#fff' };
const inputFull = { width: '100%', padding: '15px', borderRadius: '8px', border: '1px solid #cbcbcb' };
