import React, { useEffect, useState } from 'react';
import { supabase } from '../supabaseClient';
import { ShoppingBag, ArrowRight, Zap, Star, ShieldCheck } from 'lucide-react';
import { Link } from 'react-router-dom';

const HomePage = ({ addToCart }) => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    const { data, error } = await supabase.from('products').select('*');
    if (!error) setProducts(data);
    setLoading(false);
  };

  return (
    <div style={homeContainer}>
      
      {/* 1. HERO SECTION */}
      <section style={heroSection}>
        <div style={heroOverlay}>
          <span style={heroBadge}>NEW ARRIVALS 2025</span>
          <h1 style={heroTitle}>The Future of <br/> <span style={highlightText}>Gadgets</span> is Here.</h1>
          <p style={heroSubtitle}>Upgrade your lifestyle with the latest tech from MegaMart Rwanda.</p>
          <div style={heroButtons}>
            <button style={primaryBtn}>Shop Now <ArrowRight size={18} /></button>
            <button style={secondaryBtn}>View Deals</button>
          </div>
        </div>
      </section>

      {/* 2. TRUST BAR */}
      <div style={trustBar}>
        <div style={trustItem}><Zap size={20} color="#008ECC" /> <span>Same Day Delivery</span></div>
        <div style={trustItem}><ShieldCheck size={20} color="#008ECC" /> <span>Official Warranty</span></div>
        <div style={trustItem}><Star size={20} color="#008ECC" /> <span>Top Rated Service</span></div>
      </div>

      {/* 3. CATEGORY BUBBLES */}
      <div style={categoryWrapper}>
        {['Smartphones', 'Laptops', 'Watch', 'Audio', 'Gaming'].map(cat => (
          <Link key={cat} to={`/category/${cat}`} style={catCircleLink}>
            <div style={catCircle}>{cat[0]}</div>
            <span style={catName}>{cat}</span>
          </Link>
        ))}
      </div>

      {/* 4. PRODUCT GRID */}
      <div style={sectionWrapper}>
        <div style={sectionHeader}>
          <h2>Featured Products</h2>
          <Link to="/deals/all" style={viewAll}>View All</Link>
        </div>

        {loading ? (
          <div style={loaderStyle}>Loading Greatness...</div>
        ) : (
          <div style={productGrid}>
            {products.map((product) => (
              <div key={product.id} style={productCard} className="glass-card">
                <div style={imagePlaceholder}>
                   {/* Replace with <img src={product.image_url} /> when ready */}
                   <ShoppingBag size={40} color="#ccc" />
                </div>
                <div style={cardContent}>
                  <span style={cardCategory}>{product.category}</span>
                  <h3 style={cardTitle}>{product.name}</h3>
                  <div style={priceRow}>
                    <span style={cardPrice}>RWF {product.price?.toLocaleString()}</span>
                    <button 
                      onClick={() => addToCart(product)} 
                      style={addBtn}
                    >
                      +
                    </button>
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

// --- STYLES FOR THE "COOL DESIGN OF ALL TIME" ---
const homeContainer = { backgroundColor: '#fff', minHeight: '100vh' };

const heroSection = {
  height: '70vh',
  background: 'linear-gradient(rgba(0,0,0,0.6), rgba(0,0,0,0.6)), url("https://images.unsplash.com/photo-1519389950473-47ba0277781c?ixlib=rb-1.2.1&auto=format&fit=crop&w=1350&q=80")',
  backgroundSize: 'cover',
  backgroundPosition: 'center',
  display: 'flex',
  alignItems: 'center',
  padding: '0 8%',
  color: 'white'
};

const heroOverlay = { maxWidth: '700px' };
const heroBadge = { backgroundColor: '#008ECC', padding: '5px 12px', borderRadius: '20px', fontSize: '12px', fontWeight: 'bold', letterSpacing: '1px' };
const heroTitle = { fontSize: 'clamp(40px, 8vw, 64px)', lineHeight: '1.1', margin: '20px 0' };
const highlightText = { color: '#008ECC' };
const heroSubtitle = { fontSize: '18px', opacity: '0.9', marginBottom: '30px' };
const heroButtons = { display: 'flex', gap: '15px' };

const primaryBtn = { backgroundColor: '#008ECC', color: 'white', border: 'none', padding: '15px 30px', borderRadius: '30px', fontWeight: 'bold', display: 'flex', alignItems: 'center', gap: '10px', cursor: 'pointer' };
const secondaryBtn = { backgroundColor: 'transparent', color: 'white', border: '2px solid white', padding: '15px 30px', borderRadius: '30px', fontWeight: 'bold', cursor: 'pointer' };

const trustBar = { display: 'flex', justifyContent: 'space-around', padding: '30px 5%', borderBottom: '1px solid #eee', flexWrap: 'wrap', gap: '20px' };
const trustItem = { display: 'flex', alignItems: 'center', gap: '10px', fontWeight: '500', fontSize: '14px' };

const categoryWrapper = { display: 'flex', gap: '25px', padding: '40px 8%', overflowX: 'auto', whiteSpace: 'nowrap' };
const catCircleLink = { textDecoration: 'none', color: '#333', textAlign: 'center' };
const catCircle = { width: '70px', height: '70px', borderRadius: '50%', backgroundColor: '#f0f2f5', display: 'flex', alignItems: 'center', justifyContent: 'center', fontSize: '24px', fontWeight: 'bold', marginBottom: '10px', transition: '0.3s' };
const catName = { fontSize: '12px', fontWeight: 'bold' };

const sectionWrapper = { padding: '40px 8%' };
const sectionHeader = { display: 'flex', justifyContent: 'space-between', alignItems: 'flex-end', marginBottom: '30px' };
const viewAll = { color: '#008ECC', textDecoration: 'none', fontWeight: 'bold', fontSize: '14px' };

const productGrid = { display: 'grid', gridTemplateColumns: 'repeat(auto-fill, minmax(260px, 1fr))', gap: '30px' };
const productCard = { backgroundColor: 'white', borderRadius: '20px', overflow: 'hidden', boxShadow: '0 10px 30px rgba(0,0,0,0.05)', transition: 'transform 0.3s' };
const imagePlaceholder = { height: '250px', backgroundColor: '#f8f9fa', display: 'flex', alignItems: 'center', justifyContent: 'center' };
const cardContent = { padding: '20px' };
const cardCategory = { fontSize: '11px', color: '#888', textTransform: 'uppercase', fontWeight: 'bold' };
const cardTitle = { fontSize: '18px', margin: '5px 0 15px', fontWeight: '600' };
const priceRow = { display: 'flex', justifyContent: 'space-between', alignItems: 'center' };
const cardPrice = { fontSize: '18px', fontWeight: 'bold', color: '#008ECC' };
const addBtn = { width: '40px', height: '40px', borderRadius: '50%', border: 'none', backgroundColor: '#131921', color: 'white', fontSize: '20px', cursor: 'pointer' };
const loaderStyle = { textAlign: 'center', padding: '100px', fontSize: '20px', color: '#888' };

export default HomePage;
